    float r = 2.*length(uv*1.);
    float n = noise(uv*rot(t*.1)*200.);

    float ts = SIN(4.5*length(uv)+t*4.);

    col = mix(gradient1(uvo), gradient2(uvo), smoothstep(ts+.2, ts-.2, n));

    fragColor = vec4(col, 1);
}

