float rchar(in vec2 outer,in vec2 inner){
    float grid = 5.;
    vec2 margin = vec2(.2,.05);
    float seed = 23.;
    vec2 borders = step(margin,inner)*step(margin,1.-inner);
    return step(.5,random(outer*seed+floor(inner*grid))) * borders.x * borders.y;
}

vec3 matrix(in vec2 st){
    float rows = 80.0;
    vec2 ipos = floor(st*rows);

    ipos += vec2(.0,floor(iTime*20.*random(ipos.x)));


    vec2 fpos = fract(st*rows);
    vec2 center = (.5-fpos);

    float pct = random(ipos);
    float glow = (1.-dot(center,center)*3.)*2.0;

    return vec3(rchar(ipos,fpos) * pct * glow);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec3 color = vec3(.0);

    uv += 5.;

    vec2 i_st = floor(uv);
    vec2 f_st = fract(uv);

    float m_dist = 1.0;
    for(int j = -1; j <= 1; j++) {
        for (int i = -1; i <= 1; i++) {
            vec2 neighbor = vec2(float(i), float(j));
            vec2 offset = random2(i_st + neighbor);

            offset = 0.5 + 0.5*sin(iTime + 6.28323231*random2(offset));

            vec2 pos = neighbor + offset - f_st;

            float dist = length(pos);

            m_dist = min(m_dist, m_dist*dist);
        }
    }

    color += step(0.1, m_dist);

    if (color == vec3(0)) {
        color = matrix(uv);
        color = vec3(color.x + random(color.x)*sin(iTime+uv.x), color.y*cos(iTime+uv.y), color.z*cos(uv.y)*sin(uv.x));
        fragColor = vec4(1.-color, 1.0);
    } else {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }

}

