vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;

    o = length(p) - 1.7;

    vec2 d = floor(p.xz);
    p.x += d.y/2.;
    d = floor(p.xz);
    p.xz = mod(p.xz,1.)-.5;
    float t = iTime*13.;
    t = floor(t) + pow(fract(t),3.);
    float h = sin(t * pi * 2.)*.5+.5;
    //t = iTime*15.;

    //p.x += d.y/2.;
    pp = p;
    d.x += floor((rand(vec3(d,1.)+floor(t) )-.5)*12.);
    d.y += floor((rand(vec3(d.yx,-1.)+floor(t) )-.5)*12.);
    float th = 1.-step(.9,length(d));
    h *= th/2.;
    tt =t;
    p.y -= h;
    p = abs(p)-clamp((p.y+.2)/5. ,0.,.1)-vec3(.4,.2,.4);
    //p.y += h;
    o = max(p.x,max(p.y,p.z));
    o = max(o,-(length(pp + vec3(0.,1.9+h ,0.))-1.8) );
    return vec2(o,id);
}

vec2 march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i< 99 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.01)
        {
            return vec2(depth,d.y);
        }
        depth += d.x;
    }
    return vec2(-depth , 0.);

}


float noise(vec3 p)
{
    vec3 fp = floor(p);
	vec3 fs = fract(p);
    vec3 sf = vec3(1.,0.,0.);

    float a = rand(fp);
    float b = rand(fp + sf);
    float c = rand(fp + sf.yxz);
    float d = rand(fp + sf.xxz);
    float e = rand(fp + sf.yyx);
    float f = rand(fp + sf.xyx);
    float g = rand(fp + sf.yxx);
    float h = rand(fp + sf.xxx);

    vec3 u = smoothstep(0.,1.,fs);
  //  u = pow(fs,vec3(3.));

    return mix(mix(
        		mix(a,b,u.x),
        		mix(c,d,u.x),u.y),
               mix(
                mix(e,f,u.x),
                mix(g,h,u.x),u.y),u.z
            );
}
