void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 cp = vec3(0.,-15.,-6.);
    vec3 target = vec3(0.,2.,0.);

    vec3 col = vec3(0.);

    vec3 cd = normalize(vec3(target - cp));
    vec3 cs = normalize(cross(cd , vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cd,cs));

    float fov = 2.5;

    vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);

    vec2 d = march(cp,rd);
    if( d.x > 0.)
    {
        vec2 e = vec2(0.,0.01);
        vec3 pos = cp + rd * d.x;
        vec3 N = normalize(map(pos).x - vec3(map(pos - e.xyy).x , map(pos - e.yxy).x,map(pos - e.yyx).x));
        col = N;
        vec3 sun = normalize(vec3(2.,4.,8.));
        //sun.xz *= rot(iTime);
        float diff = max(0.,dot(-sun,N));
        diff = mix(diff , 1.,.1);
        float sp = max(0.,dot(rd,reflect(N,sun)));
        sp = pow(sp,56.6) * 1.;
        vec3 mat = str(pos,pp) * vec3(1.);
        col = sp * mat + diff * mat;
    }

    fragColor = vec4(col, 1.0);
}

// Inspired by this nice video: https://youtu.be/zJSBL1XWFDc
// Every integer in the complex plane can be turned into a natural number n
// Consecutive values of n follow an interesting fractal pattern that tiles the plane

#define AA 3
#define MAXBITS 64 // anything > 31 will overflow an int - but we don't actually use the value of n here

vec2 rand(uvec2 v)
{
    // https://www.shadertoy.com/view/XlGcRh
    v = v * 1664525u + 1013904223u;
    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;
    v = v ^ (v>>16u);
    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;
    v = v ^ (v>>16u);
    // return float in [0,1)
    return vec2(v >> 8) / float(0x01000000);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // zoom in/out
    float S = 12500.0;
    float scale = 2.0 * (S * cos(0.3 * iTime) + S + 50.0);
    vec3 result = vec3(0.0);
    for (int y = 0; y < AA; y++)
    for (int x = 0; x < AA; x++) {
        // stratified tent filter sampling
        vec2 j = (vec2(x,y)+rand(uvec2(fragCoord) * uvec2(AA) + uvec2(x,y)))/float(AA);
        j.x *= 2.; j.x = j.x < 1. ? sqrt(j.x) - 1. : 1. - sqrt(2. - j.x);
        j.y *= 2.; j.y = j.y < 1. ? sqrt(j.y) - 1. : 1. - sqrt(2. - j.y);
        // jitter around pixel center
        vec2 uv = (fragCoord+j+0.5)/iResolution.x;
        // get nearest gaussian integer
        uv *= scale;
        uv -= vec2(1.0, iResolution.y / iResolution.x)*scale*0.5;

        ivec2 g = ivec2(round(uv));
        // decompose the gaussian integer g one digit at a time
        // to extract the binary value n in base -1+i
        // we don't actually need the value n, we just care about the highest bit index
        // left the code in as an example
        int n = 0, b = 1, bit = 0;
        for (; bit < MAXBITS; bit++)
        {
            int r = (g.x ^ g.y) & 1; // remainder (1 only when x or y is odd)
            g = ivec2( g.y - g.x + r, -g.x - g.y + r) / 2;
            if (all(equal(g, ivec2(0))))
            {
                // done decomposing g, return index of current bit
                break;
            }
            n += b * r;
            b *= 2;
        }
        // color based on highest bit index (each colored region has the same number of digits)
        float rx = 6.0*rand(uvec2(bit, 0)).x;
        // hash to get a random color
        result += 0.5 + 0.5*cos(rx+vec3(0,2,4));
    }
    fragColor=vec4(result / float(AA * AA), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec2 topLeft = vec2(-.5, .5);
    vec2 topRight = vec2(.5, .5);
    vec2 bottomRight = vec2(.5, -.5);
    vec2 bottomLeft = vec2(-.5, -.5);

    float d1 = length(uv + topLeft);
    float d2 = length(uv + topRight);
    float d3 = length(uv + bottomRight);
    float d4 = length(uv + bottomLeft);
    // float d3 = length(uv);

    float m1 = cos(d1 * 20. - iTime * 3.) * .5 + .5;
    float m2 = cos(d2 * 20. - iTime * 4.) * .5 + .5;
    float m3 = cos(d3 * 20. - iTime * 5.) * .5 + .5;
    float m4 = cos(d4 * 20. - iTime * 2.) * .5 + .5;

    float m = m1 / 4. + m2 / 4. + m3 / 4. + m4 / 4.;
    m = smoothstep(.2, .6, m);

    float r = mix(.123, .7, m + sin(uv.x * m * 30.) * cos(uv.y * m * 20.));
    float g = mix(.23, .9, m );
    float b = mix(.3, .8, m );

    vec3 col = vec3(r, g, b);



    fragColor = vec4(col,1.0);
}

// Made by Darko Supe (omegasbk)
// 27.10.2021.
// Super hacky, I was just having fun :D

#define MAX_STEPS 1000
#define MAX_DIST 100.
#define SURF_DIST .005

#define PI 3.14159265359

#define NONE 0.0
#define SEA 1.0
#define ISLAND 2.0
#define SUN 3.0

#define FLYING_SPEED 2.0
#define SUN_SIZE 2.1

#define SUN_DISTANCE 200.

#define FOG_AMOUNT .97

float SHORE_LINE = -4.5;

vec3 lightPosition = vec3(0, 1, SUN_DISTANCE);
vec3 lightColor = vec3(0.34, 0.3, 0.3) * 2.1;
const float lightIntensity = 1.5;

float noise(vec2 uv)
{
    return fract(sin(uv.x * 113. + uv.y * 412.) * 6339.);
}

float add(float d1, float d2) 
{ 
    return min(d1,d2); 
}

float noiseSmooth(vec2 uv)
{
    vec2 index = floor(uv);

    vec2 pq = fract(uv);
    pq = smoothstep(0., 1., pq);

    float topLeft = noise(index);
    float topRight = noise(index + vec2(1, 0.));
    float top = mix(topLeft, topRight, pq.x);

    float bottomLeft = noise(index + vec2(0, 1));
    float bottomRight = noise(index + vec2(1, 1));
    float bottom = mix(bottomLeft, bottomRight, pq.x);

    return mix(top, bottom, pq.y);
}

float plane(vec3 p, vec3 n, float h)
{
    return dot(p,n) + h;
}

float sea(vec3 p)
{
    return dot(p, vec3(0., 1., 0)) - SHORE_LINE;
}

float islands(vec3 p, float h)
{
    h += noiseSmooth(p.xz / 8.) * 4.;
    h += noiseSmooth(p.xz / 16.) * 2.;
    h += noiseSmooth(p.xz / 32.);

    h += textureLod(iChannel1, p.xz / 25., 0.).r;
    return dot(p, vec3(0., 1., 0)) - h;
}

float sun(vec3 p)
{
    return distance(p, lightPosition - vec3(0., 0., SUN_DISTANCE)) - SUN_SIZE;
}

vec2 getDist(in vec3 p, in float specific)
{    
    float dIslands = islands(p, -8.);
    float dSea = sea(p);
    float dSun = sun(p);

    if (int(specific) == int(ISLAND))
        return vec2(dIslands, ISLAND);

    if (dIslands < dSea && dIslands < dSun)
    {
        return vec2(dIslands, ISLAND);
    }
    else if (dSea < dIslands && dSea < dSun)
    {
        return vec2(dSea, SEA);
    }

    return vec2(dSun, SUN);
}

vec3 getNormal(in vec3 p) 
{
    vec2 d = getDist(p, NONE);

    vec2 e = vec2(.01, 0); 

    vec3 n = d.x - vec3(
        getDist(p - e.xyy, NONE).x, 
        getDist(p - e.yxy, NONE).x, 
        getDist(p - e.yyx, NONE).x);

    return normalize(n);
}

vec2 rayMarch(in vec3 ro, in vec3 rd, float specific)
{
    float dO = 0.;
    float body = -1.;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * dO;
        vec2 distVec = getDist(p, specific);
        float dS = distVec.x;
        body = distVec.y;

        dO += dS;

        if (dO > MAX_DIST)
            return vec2(MAX_DIST, -1.);

        if (dS < SURF_DIST)
            return vec2(dO, body);
    }

    return vec2(dO, body);
}

vec3 getLight(in vec3 p, in vec3 rd, float ambiant, float diff, float shininess)
{    
	vec3 normal = getNormal(p);

    vec3 lightDir = normalize(p - lightPosition);   

    float cosa = pow(0.5+0.5*dot(normal, -lightDir), 3.0);
    float cosr = max(dot(-rd, reflect(lightDir, normal)), 0.0);

    vec3 diffuse = vec3(diff * cosa);
    vec3 phong = vec3(0.5 * pow(cosr, shininess));

    return lightColor * (vec3(ambiant) + diffuse + phong) * lightIntensity;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 skyColor = vec3(0.1, 0.5, 1.) * (1.4 - uv.y);

    vec3 ro = vec3(sin(iTime) / 4., 0.5 + sin(sin(iTime)) / 2., iTime * FLYING_SPEED) + vec3(0., 1.2, 0.);
    vec3 rd = normalize(vec3(uv.x, uv.y-0.2, 0.8));

    float lightMovement = fract(iTime / 25.) - 0.5;
    //float lightMovement = 0.;

    lightPosition.x = 200. * lightMovement;
    lightPosition.y += sin((lightPosition.x - 189.) / 40.) * 18.; 

    lightPosition += ro + vec3(0., 0., 60.);

    lightColor *= 1.0 - pow(abs(lightMovement * 2.), 2.0); 

    lightColor.z += pow(abs(lightMovement * 2.), 2.0) / 3.; 

    skyColor *= lightColor + 0.5;
    skyColor.x *= (18. - lightPosition.y) * smoothstep(0.5, 0.1, abs(lightMovement));

    SHORE_LINE =((iMouse.y / iResolution.y) * 5.) - 4.;

    vec2 distVec = rayMarch(ro, rd, NONE);
    if (distVec.x < MAX_DIST)
    {
        vec3 p = ro + rd * distVec.x;   
        vec3 n = getNormal(p);

        if (distVec.y == SEA)
        {
            col = vec3(0.1, 0.65, 0.9);

            vec3 refrRay = refract(rd, n, 1./1.4);
            distVec = rayMarch(p + refrRay * 0.01, rd, ISLAND);

            if (distVec.x < MAX_DIST)
            {
                col = mix(col, vec3(max(0., 1. - distVec.x)), 0.1);
            }

            vec3 reflRay = reflect(rd, n);
            distVec = rayMarch(p + reflRay * 0.01, reflRay, NONE);

            if (distVec.x < MAX_DIST)
            {
                vec3 pRefl = p + reflRay * distVec.x;  

                if (distVec.y != SUN)
                    col = mix(getLight(pRefl, reflRay, 0.4, 0.6, 0.2), col, 0.8);
                else
                    col = mix(lightColor * 4., col, 0.8);
            }

            col *= getLight(p, n, 1.3, 0.4, 50.0) / 1.5;
        }
        else if (distVec.y == SUN)
        {
            col = lightColor * 4.;
        }  
        else
        {
            col = vec3(0.1, 0.8, 0.3);
            float sand = smoothstep(SHORE_LINE + 0.5, SHORE_LINE, p.y);
            col = mix(col, vec3(0.8, 0.7, 0.5) * (2.5 - sand), sand);
            col *= getLight(p, rd, 0.4, 0.2, 1.); 
        }  

        col = mix(col, skyColor, clamp(mix((FOG_AMOUNT * distance(ro, p)) / MAX_DIST, 1., -.5), 0., 1.)); // Distance fog
        col = mix(col, skyColor, clamp(mix(FOG_AMOUNT * 0.38, 1., -.5), 0., 1.)); // General fog
    }
    else 
    {    
        col = skyColor;
    }   


    fragColor = vec4(col,1.0) / 1.1;
}

#define PI 3.1415326

mat2 rotate2D(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float levelDist(vec3 p, float level) {
    float scale = 1./pow(4., level);
	p = mod(p + scale, scale*2.) - scale;
	p = abs(p);
	return .3*scale - min(max(p.x, p.y), min(max(p.y, p.z), max(p.z, p.x)));
}

float cylinder(vec3 p) {
    return .5-length(p.xy);
}

// Max gives us the intersection of all the surfaces
float maxDist(vec3 p) {
	float dist = 0.;
	for (float i = 0.; i < 4.; i++) {
		dist = max(dist, levelDist(p, i));
	}
	return max(dist, cylinder(p));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float i;
    float eyeDist, minDist;

    vec3 eyePos = vec3(0, 0, iTime); // Keep moving forward
    vec3 ray = vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.x, 1.);
    ray = normalize(ray);

    // Camera rotation
// 	ray.xz *= rotate2D(PI*iTime*.2);	

    for(minDist=1.; i<100. && minDist>.001; i++) {  
        // Point to check
        vec3 p = eyePos+eyeDist*ray;

        minDist = maxDist(p);
        // Move point forward
        eyeDist += minDist*.5;
    }
    // Fix for banding
    i += 2.*(minDist*1000.-1.);
    fragColor = vec4(0);
    fragColor += 400./(i*i);
}

mat2 R(float a){
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}


void mainImage( out vec4 C, vec2 FC )
{
    float o,i,e,f,s, g=4., t=iTime, k=.1;
    vec3 q, r=iResolution, l=vec3(2.);

    for(;i++<1e2; g+=min(f,min(e,3.*e*f))*k){
        s=2.;
        vec3 p=vec3((FC.xy-r.xy/s)/r.y*g,g-5.);
        k*=1.015;
        p.yz*=R(-.7);
        p.z+=t;
        for(e=f=p.y;s<2e2;s/=.6)
            p.xz*=R(s),
            q=p,
            q.x+=t,
            e+=abs(dot(sin(q.xz*s*.1)/s,l.xz)),
            f+=abs(dot(sin(p*s*.15)/s,l));
         o=1.+(f>4e-3?(e>.002?exp(-f*e):-exp(-e*e)):-exp(-f*f));

         C *= .96;
         C.rgb += .05*max(o,.1)*(exp(-f)*vec3(.5,.3,.2)+exp(-e)*vec3(.1,.4,.7));
         }

}

