// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
#define igt iTime

vec4 ot=vec4(1.);
float g=1.25;

const int MaxIter = 12;

float zoom=1.3;


float a1 = 1.15;
float a2 = -.53;
float a3 = 2.15;
vec2 vr1; 
vec2 vr2; 
vec2 vr3;

vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }


float capsule( vec3 p)
{
   	p.y -= clamp( p.y, 0.0, 6.0 );
    return length( p-.05 ) - .6;
}

float sphere( vec3 p, float r)
{
   	p.y *= .4;
    return length( p ) - r;
}


float branches(in vec3 p,in float l, in float dr, in int maxi , in int type)
{ 
    l= min(l,capsule(p));
    float b = sphere(p,9./dr);if(b>.5)return min(b,l);
    for(int i=0;i<maxi;i++) {
        p.y-=2.5;   		
        p.xz = abs(p.xz);
        p.xz = cmul(vr1,p.xz);
        p.xy = cmul(vr2,p.xy);	
        if (type==0)
            if (i<maxi-2)p =  p * g; else p *=.8;
        else if (type==1)
            p *= g;
        else
            if (i>0 && i<maxi-1)p =  p * g; else p *=.9;
        dr *= g;
        l= min (l ,capsule(p) / dr);
    }
	return l;
}

float map(vec3 p)
{ 

    int a = int(floor(mod(p.x/20.+.5,3.)));
    int b = int(floor(mod(p.z/20.+.5,4.)));
    p.xz=mod(10.+p.xz,20.)-10.; 
    p.xz=cmul(p.xz, vec2(cos(float(b)),sin(float(b))));
    float dr = 1.0;  	
    float l=p.y;

    if(p.y>.15){   
		for(int i=0;i<5+b;i++){
            l=branches(p,l,dr,MaxIter-i-b,a);
            p.xz = cmul(vr3,p.xz);  
            p.y-=5.5-float(b);
            if(p.y<0.)break;
            p*=g,dr*=g;   
        }
    }			
    return l;   
}

vec4 branchesOt(in vec3 p,in vec4 ot, in int maxi, in int type)
{ 


    for(int i=0;i<maxi;i++) {
        p.y-=2.5;   		
        p.xz = abs(p.xz);
        p.xz = cmul(vr1,p.xz);
        p.xy = cmul(vr2,p.xy);	
        if (type==0)
            if (i<maxi-2)p =  p * g; else p *=.8;
        else if (type==1)
            p *= g;
        else
            if (i>0 && i<maxi-1)p =  p * g; else p *=.9;
        ot=min(ot,vec4(abs(p),dot(p,p)/float(i+1)));
    }
	return ot;
}

vec4 mapOt(vec3 p)
{ 

    int a = int(floor(mod(p.x/20.+.5,3.)));
    int b = int(floor(mod(p.z/20.+.5,4.)));
    p.xz=mod(10.+p.xz,20.)-10.; 
    p.xz=cmul(p.xz, vec2(cos(float(b)),sin(float(b))));
    float dr = 1.0;
    ot = vec4(1.);    	

    if(p.y>.15){   
		for(int i=0;i<5;i++){
            ot=branchesOt(p,ot,MaxIter-i-b,a);
            p.xz = cmul(vr3,p.xz);  
            p.y-=5.5-float(b);
            p*=g;dr*=g;   
        }
    }			
    return ot;   
}

float trace( in vec3 ro, in vec3 rd )
{
    float maxd = 120.;
    float precis = 0.001;
    float k = .5;

    float dt=1.;
    float t = 0.0;
    for( int i=0; i<128; i++ )
    {
		if( t>maxd ||  dt<precis*(.1+t)) continue;             
        t += dt*k;
        k *= 1.01;
		dt = map( ro+rd*t );
    }

    if( t>maxd ) t=-1.0;
    return t;
}

vec3 calcNormal( in vec3 pos )
{
	vec3  eps = vec3(.0001,0.0,0.0);
	vec3 nor;
	nor.x = map(pos+eps.xyy) - map(pos-eps.xyy);
	nor.y = map(pos+eps.yxy) - map(pos-eps.yxy);
	nor.z = map(pos+eps.yyx) - map(pos-eps.yyx);
	return normalize(nor);
}

float softShadow( in vec3 ro, in vec3 rd, in float tmin, in float tmax)
{
	float res = 1.0;
        float precis = 0.1;
	float t = tmin;
	for (int i = 0; i < 8; i++)
	{
		float dt = 2.*map( ro + rd*t );
		res = min(res, 16.0*dt/t);
		t += dt;
		if( dt<precis || t>tmax ) break;
	}
	return clamp(res*t/tmax, 0.0, 1.0);	
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = -1.0 + 2.0*fragCoord.xy / iResolution.xy;
	p.x *= iResolution.x/iResolution.y;

	vec2 m = vec2(-0.5)*6.28;
	m = (iMouse.xy/iResolution.xy-.5)*6.28;
	m += .5*vec2(cos(0.15*igt),cos(0.09*igt))+.3; 

	vr1 = vec2(cos(a1),sin(a1));
    vr2 = vec2(cos(a2),sin(a2));
    vr3 = vec2(cos(a3),sin(a3));
    // camera


	vec3 ta = vec3(.5*igt,8.,4.);
	vec3 ro =ta  + zoom*4.*(5.+sin(.2*igt))*vec3( cos(m.x)*cos(m.y), 1.+sin(m.y), sin(m.x)*cos(m.y));


	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(0.,1.,0.0);
	vec3 cu = normalize(cross(cw,cp));
	vec3 cv = normalize(cross(cu,cw));
	vec3 rd = normalize( p.x*cu + p.y*cv + 2.5*cw );


    // trace



	vec3 col = vec3(0.8,0.8,1.);
	float t = trace( ro, rd );
	if( t>0.0 )
	{
		vec3 pos = ro + t*rd;
		vec3 nor = calcNormal( pos );
        ot = mapOt(pos);

		// lighting

        vec3  light1 = normalize(vec3( 0.4, 0.9,0.4  ));
		float key = clamp( dot( light1, nor ), 0.0, 1.0 );
		if(pos.y<.15){
            key*=softShadow(pos,light1,1.,25.);
            ot=texture(iChannel0,(pos.xz / 16.0));
            ot.a=0.;
        }

		float amb = (0.4+0.6*nor.y);
		float ao = pow( clamp(1.-.5*ot.a,0.2,1.0), 1.2 );		
        vec3 brdf = vec3(ao)*(.4*amb+key);

        // material				
		vec3 rgb =ot.rgb*vec3(.9,.7,.2);

        if(pos.y>.15){
            rgb.rb = clamp(rgb.rb,0.,1.5*rgb.g);

        }


		// color
        col = rgb*brdf;
		col = mix(vec3(0.8,0.8,1.),col,exp(-0.015*t));


	}

	fragColor=vec4(col,1.0);
}

#define S(v) smoothstep( 3./R.y, 0., (v) -.07 )

float line0(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b);   // proj coord on line
    return length(p - b * h);          // dist to segment
}
float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b);   // proj coord on line
    h = clamp(h, 0.,1.);               // round end
    return length(p - b * h);          // dist to segment
}

vec2 P,_D,D, M;
#define L(B)  v = min( v, line(U,P,B) );                                        \
              _D = D; D = normalize(B-P); M = _D+D;                             \
              if (dot(U-P,D) < 0. && dot(U-P,_D) > 0. )                         \
                 v = min( v, dot(U-P,M) < 0. ? line0(U,P-_D,P) : line0(U,P,B)); \
              P = B; 
