// "Knot Curves 3" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
  Cycle through 11 simple parametric knot curves (as in "Knot Curves 2").
  Alternating tube and moving ball visualization.
  Moving helical colors for tube.
  Mouse near left/right edges overrides auto cycle.
  Mouse near right edge, or in bottom window half, shows tube instead of balls.
*/

#define AA  0   // (= 0/1) optional antialiasing

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
vec3 HsvToRgb (vec3 c);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir, knc[7], kns[7], qnHit;
float dstFar, tCur, nSeg, sclFac, qCol;
int knType;
bool showBalls;
const float nKnot = 11.;
const float pi = 3.1415927;

vec3 KtPoint (float a)
{
  vec3 r;
  vec2 cs;
  if (showBalls) a += mod (-0.2 * tCur, 2. * pi);
  r = vec3 (0.);
  for (int k = 0; k < 7; k ++) {
    cs = sin (float (k + 1) * a + vec2 (0.5 * pi, 0.));
    r += knc[k] * cs.x + kns[k] * cs.y;
  }
  return sclFac * r;
}

float SphHit (vec3 ro, vec3 rd, float rad)
{
  float b, d, w, dMin;
  dMin = dstFar;
  b = dot (rd, ro);
  w = b * b - dot (ro, ro) + rad * rad;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0.) {
      dMin = d;
      qnHit = (ro + d * rd) / rad;
    }
  }
  return dMin;
}

vec4 CapsHit (vec3 ro, vec3 rd, float rad, float len)
{
  vec3 s, sMin;
  float dMin, d, a, b, w, drz;
  dMin = dstFar;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - rad * rad);
  if (w > 0. && a > 0.) {
    d = - b - sqrt (w);
    d /= a;
    s = ro + d * rd;
    if (d > 0. && abs (s.z) < len) {
      dMin = d;
      sMin = s;
      qnHit = vec3 (s.xy, 0.);
    } else {
      drz = - len * sign (s.z);
      ro.z += drz;
      b = dot (rd, ro);
      w = b * b - (dot (ro, ro) - rad * rad);
      if (w > 0.) {
        d = - b - sqrt (w);
        if (d > 0.) {
          s = ro + d * rd;
          dMin = d;
          sMin = s;
          sMin.z -= drz;
          qnHit = s;
        }
      }        
    }
  }
  return vec4 (sMin, dMin);
}

mat3 EvalRMat (vec3 r, vec3 rp, vec3 rm)
{
  vec3 ddr, vt, vn;
  vt = normalize (rp - rm);
  ddr = rp + rm - 2. * r;
  vn = normalize (ddr - vt * dot (vt, ddr));
  return mat3 (vt, vn, cross (vt, vn));
}

#define NSEG_MAX  512.  // for tube: larger values, better but slower

float ObjRay (vec3 ro, vec3 rd)
{
  mat3 rMat, rMatS;
  vec4 ds4Min, ds4;
  vec3 r, rp, qnHitS, qTube;
  float dMin, d, dAng, eAng, len, jHit;
  dAng = 2. * pi / nSeg;
  dMin = dstFar;
  if (! showBalls) {
    ds4Min = vec4 (0., 0., 0., dMin);
    eAng = 0.5 * dAng;
  }
  r = KtPoint (2. * pi - dAng);
  for (float j = float (VAR_ZERO); j < NSEG_MAX; j ++) {
    if (j == nSeg) break;
    rp = r;
    r = KtPoint (j * dAng);
    if (showBalls) {
      d = SphHit (ro - r, rd, 0.1);
      if (d < dMin) {
        dMin = d;
        qnHitS = qnHit;
        jHit = j;
      }
    } else {
      rMat = EvalRMat (r, KtPoint (j * dAng + eAng), KtPoint (j * dAng - eAng));
      len = 0.5 * length (r - rp);
      ds4 = CapsHit (((ro - r) * rMat).yzx, (rd * rMat).yzx, 0.1, len);
      if (ds4.w < ds4Min.w) {
        ds4Min = ds4;
        ds4Min.z /= len;
        qnHitS = qnHit;
        rMatS = rMat;
        jHit = j;
      }
    }
  }
  if (showBalls) {
    if (dMin < dstFar) {
      qCol = 4. * jHit / nSeg;
      qnHit = qnHitS;
    }
  } else {
    if (ds4Min.w < dstFar) {
      dMin = ds4Min.w;
      qnHit = rMatS * normalize (qnHitS.zxy);
      qTube = ds4Min.xyz;
      qCol = 4. * (jHit + qTube.z + 0.5) / nSeg + atan (qTube.y, - qTube.x) / (2. * pi) +
         0.2 * tCur;
    }
  }
  return dMin;
}

// Knot parametrizations from A.K. Trautwein thesis (University of Iowa, 1995) (many more)

void KtSetup ()
{
  for (int k = 0; k < 7; k ++) {
    knc[k] = vec3 (0);  kns[k] = vec3 (0);
  }
  if (knType == 1) {  // 3.1 trefoil knot
     knc[0] = vec3 ( 41,   36,   0);   kns[0] = vec3 (-18,   27,   45);
     knc[1] = vec3 (-83, -113,  -30);  kns[1] = vec3 (-83,   30,  113);
     knc[2] = vec3 (-11,   11,  -11);  kns[2] = vec3 ( 27,  -27,   27);
     sclFac = 0.015;
  } else if (knType == 2) {  // 4.1 figure 8 knot
