#define pi acos(-1.)
#define eps 1./iResolution.y
#define MAX_LEVEL 8.
#define FAR 100.


//Rotation matrix for the ray direction 
mat2 rot(float a){
    float c = cos(a), s = sin(a); return mat2(c,-s,s,c);}


//  1 out, 3 in... from dave hoskins for the quad/octree
float hash13(vec3 p3)
{
	p3 = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}



// SHORTER, NEATER, BRANCHLESS, UNIQUE, SINGULAR PURPOSE
// This quad/octree map function returns the largest scaling factor 
//for a given posision
// the inverse of that is the cell size
float mapQ(vec3 p){
  float s = 0.5;
  for(float i = 1.; i < MAX_LEVEL; i++){
    s *= 2.;
    //if we don't pass the random check, add max to index so we break
    i += step( hash13(floor(p * s)), 0.5 ) * MAX_LEVEL;
  }
  return s;
}

//This DDA algo is based on the lodev Raycasting tutorial
// https://lodev.org/cgtutor/raycasting.html
//and the modification is from my quadtree traversal
//shader: https://www.shadertoy.com/view/7dVSRh
//I explain the idea in the comments.
//basically though we find the first axis the ray will 
//hit when traveling in the ray direction and return the 
//distance to that axis.
//Firsst we get the "scale factor" from the qaudtree map so
//we know what size box we are in. Then we find our distances
//for each axis and return the smallest. More detail is given in
//the link above.
float calcT(vec3 p, vec3 rd, vec3 delta){
    float s = mapQ(p);
    vec3 t;

    t.x = rd.x < 0. ? ((p.x*s - floor(p.x*s))/s)*delta.x
                    : ((ceil(p.x*s) - p.x*s)/s)*delta.x; 

    t.y = rd.y < 0. ? ((p.y*s - floor(p.y*s))/s)*delta.y
                    : ((ceil(p.y*s) - p.y*s)/s)*delta.y; 

    t.z = rd.z < 0. ? ((p.z*s - floor(p.z*s))/s)*delta.z
                    : ((ceil(p.z*s) - p.z*s)/s)*delta.z; 

                    //+0.01 to get rid of a bunch of artifacts
    return min(t.x,min(t.y,t.z))+0.001;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;


    //This is the canonical orthonormal basis for a camera 
    //ray in the uv plane. 
    vec3 fwd = vec3(0.,0.,1.);
    vec3 up = vec3(0.,1.,0.);
    vec3 right = normalize(cross(up,fwd));
    up = cross(fwd,right);
    vec3 ro = vec3(0., iTime/6.,0.);
    vec3 rd = right*uv.x + up*uv.y + fwd;
    rd = normalize(rd);

    //rotating the raydirection to look around.
    rd.xz *= rot(iTime/6.);

    //get the delta which is explained in the lodev tutorial
    //on raycasting: https://lodev.org/cgtutor/raycasting.html
	vec3 delta = 1./max(abs(rd), eps);
    float t = 0.;

    //It all comes together in this small iteration loop.
    //We are updating t just like in raymarching.
    //The only "sdf" we use though is the "hole" rect tunnel
    //We only render a block if our hash returns < 0.4 AND
    // the hit point of the block is outside of the sdf (hole > 0.001)
    for(float i = 0.; i < FAR; i++){
        vec3 pos = ro + rd * t;
        float ss = mapQ(pos);

        //create the hole / shield so blocks stop
        //hitting me in the face
        vec3 shi = abs(floor(pos-ro)) - 1.;
        //the hole. Basically a vertical rectangular/square tunnel
        float hole = max( shi.x, shi.z);

        //n = calcN(pos, rd, delta);
        //check the hole AND check the random missing squares
        //if we are out of the hole AND not hitting a missint square
        //then break!
        if(hole > 0.001 && hash13(floor((pos)*ss)) > .4) break;

        //updating t with the modified DDA algo.
        t += calcT(pos, rd, delta);
    }

    fragColor = vec4(pow(vec3(clamp(1.-t/7.,0.,1.))*1.3,vec3(4.)),1.);
}

// via https://www.redblobgames.com/grids/hexagons/#pixel-to-hex
mat2 hexel = mat2(2.,0.,-1.,sqrt(3.))/3.;
mat2 pixel = mat2(3.,0.,sqrt(3.),2.*sqrt(3.))/2.;

float zz = 0./0.;

vec3 to_cubic(vec2 p) { return vec3(p.x,-p.x-p.y,p.y); }
vec2 from_cubic(vec3 p) { return p.xz; }

// glsl-ized from https://www.redblobgames.com/grids/hexagons/#rounding
vec3 round_cubic(vec3 p) {
  vec3 r = round(p);

  vec3 diff = abs(r-p);
  vec3 alt = -r.yzx-r.zxy;

  float biggest = max(diff.x,max(diff.y,diff.z));

  return mix(r, alt, step(biggest, diff));
}

float cube_len(vec3 c) {
  vec3 d = abs(c);
  return (d.x+d.y+d.z)/2.;
}

#define nsin(X) (.5+.5*sin(6.283*(X)))

vec3 px_to_cube(float res, vec2 p) {
  return round_cubic(to_cubic(res * p * hexel))/res;
}

vec2 cube_to_px(vec3 c) {
  return c.xy * pixel;
}

mat2 rot(float T){float s=sin(T),c=cos(T);return mat2(c,s,-s,c);}

float warp(vec3 c) {
  float F = 10.+10.*nsin(iTime*.02);//+40.*nsin(iTime*.01);

  c *= F;

  vec2 p = cube_to_px(c);

  p*=rot(.3*iTime);
  vec3 c2 = px_to_cube(1., p);
  vec3 ta = tan(c2);
  float w = length(ta);//(ta.x+ta.y-ta.z);//(pow(tan(p.x),2.)-pow(tan(p.y),2.));
  return .05*w*w;//1./(1.-w);
#if 0
  float F = 3.+30.*nsin(.1*iTime);
  c *= F;
  vec3 t=tan(c);

  float w = (t.x*t.x+t.y*t.y+t.z*t.z)/F;
  return 1./(1.+w);

#endif
}

