#define MAX_STEPS 100
#define MAX_DIST 50.
#define SURF_DIST .001

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;

    return length(p-c)-r;
}

float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}

float sdTorus(vec3 p, vec2 r) {
	float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}

vec4 opUnionColor(vec4 a, vec4 b) {
    return (a.w<b.w) ? a : b;
}

vec4 GetDist(vec3 p) {
    float plane = p.y;
    //float box = sdBox(p-vec3(0,1,0), vec3(1));
    float d = MAX_DIST;

    p += vec3(0.,0.,-4) * fract(iTime * 0.1);

    vec3 c = vec3(4.,0,4.);
    p = mod(p+0.5*c,c)-0.5*c;

    //legs
    {
        float angleL = sin(3.14 + iTime * 3.) * 0.9 + 1.8f;
        float angleR = sin(       iTime * 3.) * 0.9 + 1.8f;
        float rightLeg = sdCapsule(p, vec3(-.2, .4 - sin(angleR) * 0.3, cos(angleR) * 0.3), vec3(-.2, .4, 0), .1);
        float leftLeg = sdCapsule(p,  vec3( .2, .4 - sin(angleL) * 0.3, cos(angleL) * 0.3), vec3( .2, .4, 0), .1);
        d=min(d, leftLeg);
        d=min(d, rightLeg);
    }

    //body
    {
        float body = sdRoundedCylinder(p - vec3(0, .9, 0), .2, .1, .3);
        d=min(d, body);
    }

    //arms
    {
        float angleL = sin(3.14 + iTime * 3.) * 0.9 + 1.8f;
        float angleR = sin(       iTime * 3.) * 0.9 + 1.8f;
        float rightArm = sdCapsule(p, vec3(-.5, 1.2-sin(angleR) * 0.4, cos(angleR) * 0.4), vec3(-.5, 1.2, 0), .1);
        float leftArm = sdCapsule(p,  vec3( .5, 1.2-sin(angleL) * 0.4, cos(angleL) * 0.4), vec3( .5, 1.2, 0), .1);
        d=min(d, leftArm);
        d=min(d, rightArm);
    }

    //head
    {
        // first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
        float fft  = texelFetch( iChannel0, ivec2(0.2,0), 0 ).x; 

        float c = cos(fft*.2 - .1);
        float s = sin(fft*.2 - .1);

        mat4 rot = mat4(
            vec4(1, 0, 0, 0),
            vec4(0, c, -s, 0),
            vec4(0, s, c, 0),
            vec4(0, 0, 0, 1)
        );
        vec3 q = (rot * vec4(p, 1.0)).xyz ;


        float box = sdBox(q- vec3(0,1.75,0), vec3(1.,.4,1.));
        //d = min(d,box);
        float head = max(length(q-vec3(0,1.40,0)) - .4, box);

        //float bottomheadbox = sdBox(p-vec3(0,1.35,0), vec3(1.,.05,1.));
        //float bottomhead = max(bottomheadbox, sdRoundedCylinder(p - vec3(0,1.45,0), .2, .1, .05));

        d = min(d, head);// - (sin(iTime)*0.5 + 0.5) * .1;

        float angleL = sin(3.14 + iTime * 6.) * 0.1 + 1.6f;
        float angleR = sin(       iTime * 6.) * 0.1 + 1.6f;

        float ear = sdCapsule(q, vec3(-.1,1.70,0), vec3(-.2,1.4 + sin(angleL) * 0.6,cos(angleL) * 0.6), .03);
        d = opSmoothUnion(d, ear, .25);

        ear = sdCapsule(q, vec3(.1,1.70,0), vec3(.2,1.4 + sin(angleR) * 0.6,cos(angleR) * 0.6), .03);
        d = opSmoothUnion(d, ear, .25);

        d = max(d, -sdCappedCylinder(q, vec3(-.15, 1.55, .2), vec3(-.15, 1.55, 1), .03));
        d = max(d, -sdCappedCylinder(q, vec3(.15, 1.55, .2), vec3(.15, 1.55, 1), .03));
    }

    return opUnionColor(vec4(vec3(.7,.7,.7), plane) , vec4(vec3(0.,1.,0.), d));
}

float RayMarch(vec3 ro, vec3 rd, inout vec3 color) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
        if (dO>MAX_DIST) 
            break;

        vec3 p = ro + rd*dO;
        vec4 dS = GetDist(p);
        if (dS.w < SURF_DIST) {
            color = dS.rgb;
            break;
        }
        dO += dS.w *.8;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = GetDist(ro + rd*t).w;
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(8, 10, 8);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    vec3 color = vec3(0.);


    //float d = RayMarch(p+n*SURF_DIST*2., l, color);
    //if(p.y<.01 && d<length(lightPos-p)) dif *= .5;

    dif = clamp( dot( n, l ), 0.0, 1.0 ) *
                    softshadow( p+n*SURF_DIST*2., l, 0.01, 6.0, 16. );


    //float shadow = softshadow(p+n*SURF_DIST*2., l, 0.01, 6.0, 32.);
    //dif *= shadow;

    return dif;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -1);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);
    vec3 color = vec3(0.);
    float d = RayMarch(ro, rd,color);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;

    	float dif = GetLight(p);
        vec3 c = vec3(4,0,4);

        //vec3 q = p + vec3(0.,0.,-4) * fract(iTime * 0.1);
        //q = q - mod(q+0.5*c,c)-0.5*c;
        //vec3 baseColor = q*0.1;
        vec3 baseColor = color;

        vec3 m = mix(vec3(1,.5,0), baseColor, 1. - (dot(rd, GetNormal(p))*.5 + .5 ));
    	col = m * dif;
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

#define doLogEps
// logeps break condition of https://www.shadertoy.com/view/tdXXzl

//Daniel Piker 2021
/* 
Changed to get the Seifert surface using (1/4πi) log (Q/Q∗)
(added complex log line 85 and new field expression line 163)

Trying here to show different valued Seifert fibers like in 
https://twitter.com/KangarooPhysics/status/1381380025568616459

// Inigo Quilez 2021
/*

An (appoximated) SDF that has a Figure-8 knot shape. It's not very
symmetric or pretty, but it counts (topologically).

The idea is described here https://arxiv.org/pdf/1610.02463.pdf and
goes like this:

From R3, our regular 3D space, we do an inverse stereographic
projection to land in a 4D hyper sphere S3, a subset of R4.

In R4 we define a function F that returns a R2 field (or
two scalar fields, if you want). We find the zero isosurface
(the nodal set, or roots) of F by zeroing its two components.

Because we are only evaluating the subset of R4 that lays on S3,
we are really computing the intersection of the isosurfce of F
with the surface of the sphere S3. This creates a curve on the surface
of the sphere, which thanks to the stereographic projection we started
with becomes a curve in R3, our 3D space. That's the curve we are
raymarching.

The R4 parameter to F can be grouped as two complex numbers u and v.
And in the case of this shader I'm using

F(u,v) = 64v³ − 12v(3+2u²−2u*²) − (14u²+14u*²+u⁴-u*⁴)

where u* is the conjugate of u. This curve is a figure 8 shape, as
described in the paper. You can change the SHAPE define below to
see another shape (a torus knot).

Lastly, there's more than one way to convert F into a R1 scalar field
than taking it's length |F|. For example, you can pass F through any
2D SDF formula you want, such a square. Change the PROFILE define
bellow to explore that.


See: https://www.shadertoy.com/view/tsBGzt
See: https://www.shadertoy.com/view/7sGSRd

*/

#define AA 1

// 0: circular
// 1: square
#define PROFILE 0

// 0: figure 8
// 1: torus 5-2
// 2: something i made
#define SHAPE 0

//------------------------------------------------------------
// oldschool rand() from Visual Studio
//------------------------------------------------------------
int   seed = 1;
void  srand(int s ) { seed = s; }
int   rand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float frand(void) { return float(rand())/32767.0; }
//------------------------------------------------------------
// hash to initialize the random sequence (copied from Hugo Elias)
//------------------------------------------------------------
int hash( int n )
{
	n = (n << 13) ^ n;
    return n * (n * n * 15731 + 789221) + 1376312589;
}

//------------------------------------------------------------
// complex number operations
//------------------------------------------------------------
vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 cdiv( vec2 a, vec2 b )  { float d = dot(b,b); return vec2( dot(a,b), a.y*b.x - a.x*b.y ) / d; }
vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }
vec2 csqrt( vec2 z ) { float m = length(z); return sqrt( 0.5*vec2(m+z.x, m-z.x) ) * vec2( 1.0, sign(z.y) ); }
vec2 conj( vec2 z ) { return vec2(z.x,-z.y); }
vec2 cpow( vec2 z, float n ) { float r = length( z ); float a = atan( z.y, z.x ); return pow( r, n )*vec2( cos(a*n), sin(a*n) ); }
vec2 clog( vec2 z) {return vec2 (log(sqrt(z.x*z.x+z.y*z.y)),atan(z.y,z.x));}
//------------------------------------------------------------
// https://en.wikipedia.org/wiki/Stereographic_projection
vec4 inversestereographic(vec3 p)
{
    float r2 = 1.0+dot(p,p);
    return vec4(2.0*p,r2-2.0)/r2;
}
//------------------------------------------------------------
// https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdRoundSquare( in vec2 p, in float s, in float r ) 
{
    vec2 q = abs(p)-s+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}
//------------------------------------------------------------
// https://iquilezles.org/www/articles/biplanar/biplanar.htm
vec3 tex( sampler2D sam, in vec4 p, in vec4 n )
{
    n = n*n;
    n = n*n;
    vec3 col = vec3(0.0);
    col += n.x*texture(iChannel0,p.yz).xyz;
    col += n.y*texture(iChannel0,p.zw).xyz;
    col += n.z*texture(iChannel0,p.wx).xyz;
    col += n.w*texture(iChannel0,p.xy).xyz;
    return col / (n.x+n.y+n.z+n.w);
}
//===============================================================================================

vec4 r3_to_s3( in vec3 p )
{
    vec4 q = inversestereographic(p);

    // rotate in yw
    float a = iTime*6.283185/24.0;
    //float a = 0.0;
    q.yw *= mat2(cos(a),sin(a),-sin(a),cos(a));

    return q;
}

vec2 F( in vec2 u, vec2 v )
{
    #if SHAPE==0
    // figure-8
    vec2 a = vec2(3.0,0.0) - 2.0*csqr(v) + 2.0*csqr(conj(v));
    vec2 q = 64.0*cmul(u,csqr(u)) 
           - 12.0*cmul(u,a) 
           + 14.0*cmul(v,v) 
           + 14.0*csqr(conj(v)) 
           -      csqr(csqr(v)) 
           +      csqr(csqr(conj(v)));
    return q;
    #endif

    #if SHAPE==1
    // torus
    return 20.0*(cmul(u,csqr(csqr(u))) + csqr(v));
    #endif    

    #if SHAPE==2
    // something I made
    return    64.0*cmul(v,csqr(csqr(u)))
           + 32.0*cmul(u,csqr(v))
           + 32.0*csqr(v) 
           + 8.0;
    #endif    

}

float field( in vec4 q )
{
    vec2 phi = F(q.xy,q.zw);

    #if PROFILE==0
    //return length(phi) - 10.0;  

    //(1/4πi) log (Q/Q∗)  
  //  return (length(cmul(vec2(0,0.785398163),clog(cdiv(phi,conj(phi))))));    
    return abs(mod(((cmul(vec2(0,0.785398163),clog(cdiv(phi,conj(phi))))).x + iTime/3.14159265359),6.28318530718) - 0.5);


    #else
    return sdRoundSquare( phi, 8.0, 0.5 );
    #endif
}

// only used for texturing, not for distance-estimation
vec4 field_grad( in vec4 q )
{
    float f = field(q);
    const vec2 e = vec2(0.001,0.0);
    return normalize(vec4(
        field(q+e.xyyy),
        field(q+e.yxyy),
        field(q+e.yyxy),
        field(q+e.yyyx))-f );
}

float map( in vec3 p )
{

    float d = field(r3_to_s3(p));

    // could do some automatic differentiation here...
    float eps = 0.0001;
    vec3 g = (vec3(field(r3_to_s3(p+vec3(eps,0.0,0.0))),
                   field(r3_to_s3(p+vec3(0.0,eps,0.0))),
                   field(r3_to_s3(p+vec3(0.0,0.0,eps))))-d)/eps;

    // distance estimator
    return d/length(g);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos, in float eps )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*eps;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

// https://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow( in vec3 ro, in vec3 rd, float k )
{
    const float tmax = 5.0;
    float t = 0.001;
    float sh = 1.0;
    for( int i=0; i<256; i++ )
    {
        vec3 pos = ro + rd*t;
        float h = map(pos);
        sh = min( sh, clamp(k*h/t,0.0,1.0) );
        if( sh<0.001 ) break;
        t += clamp(h,0.001,0.1);
        if( t>tmax ) break;
    }
    return sh;
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;
	for( int i=0; i<32; i++ )
	{
		float h = frand();
        vec3 kv = normalize( vec3(frand(), frand(), frand()) );
        kv *= sign(dot(kv,nor));
		ao += clamp( map(pos+nor*0.001+kv*h*2.0)*1.0, 0.0, 1.0 );
	}
	ao /= 32.0;

	return clamp( ao*1.5-0.1, 0.0, 1.0 );
}


//logEps() put into subroutine
//, to toggle between various LOD-scales (distance field zoom levels)
//, to make it toggleAble (sometimes a simpler end condition is better)
bool logEps(float d,float s//t=distanceToCamera s=numberOfmarchingIterations
){if(1./exp(d)<.01)return s<.001;//better for short distance reflections
  //above line fails within a tensor-distorted gradient...
  return log(d*d/s/1e5)>0. //long distance logeps for spinor /tdXXzl
 ;}


float marchLog(inout vec3 pos, inout vec3 dir  ,float tmax     
){
