void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // init random seed
    ivec2 q = ivec2(fragCoord);
    srand( hash(q.x+hash(q.y+hash(iFrame))));

    // camera movement	
    float an = -0.0*iTime/20.0;
    vec3 ro = vec3( 5.0*sin(6.283185*an), 4.0, 5.0*cos(6.283185*an) );
    #if SHAPE>0
    ro.xz *= 1.5;
    ro.y = 0.0;
    #endif
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 3.0*ww );


        // raymarch
        const float tmax = 15.0;
        #ifdef doLogEps
        float t=marchLog(ro,rd,tmax);//child march loop function.
        #else
        float t = 0.0;
        for( int i=0; i<8192; i++ ){ //parent march loop;
            vec3 pos = ro + rd*t;
            float d = map(pos);
            if( d<0.001 ) break;
            d = min(d*0.8,0.1);
            t += d;
            if( t>tmax ) break;} 
        #endif

        vec3 col = vec3(0.0);
        col = vec3(0.05 + 0.05*rd.y);
        #ifdef doLogEps
        if(map(ro+rd*t)<0.001 && t<tmax)//because logeps may end up in empty space on this much distortion
       //t<tmax seems to NEVER be true for logeps, which is kinda sad.
        #else
        if( t<tmax)
        #endif
        {
            vec3  pos = ro + rd*t;
            vec3  nor = calcNormal( pos, 0.0001 );
            float occ = calcAO( pos+nor*0.001, nor );

            // we do texturing in R4
            vec4 pos4 = r3_to_s3(  pos );
            vec4 nor4 = field_grad( pos4 );
            vec3 mate = tex(iChannel0,pos4,nor4);
            mate = mate*mate;
            mate *= 0.3;

            // sun
            {
            vec3 lig = normalize(vec3(1.0,1.0,0.2));
            float dif = clamp(dot(nor,lig),0.0,1.0);
            if( dif>0.001 ) dif *= shadow(pos+nor*0.001, lig, 48.0);
            vec3 hal = normalize(lig-rd);
            float spe = pow(clamp(dot(nor,hal),00.,1.0),32.0);
            spe *= 0.04 + 0.96*pow( clamp(1.0-dot(lig,hal), 0.0, 1.0), 5.0 );
            spe *= 10.0;

            col  = 0.6*mate*vec3(9.0,8.0,7.0)*dif;
            col += spe*dif*mate.x*10.0;

            float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
            col += mate*fre*1.0*(0.25+0.75*dif)*vec3(1.0,0.7,0.4);
            }

            // sky
            {
            vec3 ref = reflect(rd,nor);
            float dif = 0.5+0.5*nor.y;
            float spe = smoothstep(0.3,0.5,ref.y);
            spe *= dif;
            spe *= 0.04 + 0.96*pow( clamp(1.0+dot(rd,nor), 0.0, 1.0), 5.0 );
            if( spe>0.001 ) spe *= shadow(pos+nor*0.001, ref, 8.0);
            col += 3.0*occ*mate*dif*vec3(0.5,0.6,1.0)*1.5;
            col += 1.0*spe;
            }

            // sun bounce
            col += mate*(1.0-occ)*vec3(1.2,0.5,0.2)*0.2;
        }

        // gain
        col = col*1.5/(1.0+col);

        // to gamma space
        col = pow( col, vec3(0.4545) );
        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    // remove color banding through dithering
    tot += (1.0/255.0)*frand();

    fragColor = vec4( tot, 1.0 );
}

/* Inigo Quilez 2021

An appoximated SDF for a Figure-8 knot. The idea is described here
https://arxiv.org/pdf/1610.02463.pdf and goes like this:

From R3, our regular 3D space, we do an inverse stereographic projection
to land in a 4D hyper sphere S3, a subset of R4. Here in R4 we define a
function F that returns a R2 field (or two scalar fields, if you want).
Then we find its zero isosurface (nodal set). Because we are only
evaluating the subset of R4 that lays on S3, we are really computing the
intersection of the isosurface of F with the sphere S3. This creates a
curve on the sphere, which thanks to the stereographic projection becomes
a curve in R3, our 3D space. That's the curve we are raymarching. Now,
the R4 domain of F can be grouped as two complex numbers u and v. In the
case of the figure 8 knot,

F(u,v) = 64v³ − 12v(3+2u²−2u*²) − (14u²+14u*²+u⁴-u*⁴)

where u* is the conjugate of u. Finally, texturing happens in spherical
coordinates on S3 itself, with becomes a regular 3D texturing problem.

You can change the SHAPE define below to see other shapes. Also, there
are more than one way to convert F into a R1 scalar field than taking
its length |F|. For example, you can pass F through any 2D SDF formula
you want, such a square. Change the PROFILE define bellow to explore that.

See also: https://www.shadertoy.com/view/tsBGzt
See also: https://www.shadertoy.com/view/7sGSRd                        */

// antialiasing. Can set to 2 if you have a fast machine
#define AA 1

// 0: figure 8
// 1: torus 5-2
// 2: clifford torus
// 3: something I made
// 4: some other shape I made
#define SHAPE 0

// 0: circular
// 1: square
#define PROFILE 0

// 0: distance estimation in R3 (pos)
// 1: distance estimation in R1 (t), equivalent to Newtwon-Raphson
#define METHOD 1

//------------------------------------------------------------
// oldschool rand() from Visual Studio
//------------------------------------------------------------
int   seed = 1;
void  srand(int s ) { seed = s; }
int   rand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float frand(void) { return float(rand())/32767.0; }
//------------------------------------------------------------
// hash to initialize the random sequence (copied from Hugo Elias)
//------------------------------------------------------------
int hash( int n )
{
	n = (n << 13) ^ n;
    return n * (n * n * 15731 + 789221) + 1376312589;
}
//------------------------------------------------------------
// complex number operations
//------------------------------------------------------------
vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 cdiv( vec2 a, vec2 b )  { float d = dot(b,b); return vec2( dot(a,b), a.y*b.x - a.x*b.y ) / d; }
vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }
vec2 csqrt( vec2 z ) { float m = length(z); return sqrt( 0.5*vec2(m+z.x, m-z.x) ) * vec2( 1.0, sign(z.y) ); }
vec2 conj( vec2 z ) { return vec2(z.x,-z.y); }
vec2 cpow( vec2 z, float n ) { float r = length( z ); float a = atan( z.y, z.x ); return pow( r, n )*vec2( cos(a*n), sin(a*n) ); }
//------------------------------------------------------------
// https://en.wikipedia.org/wiki/Stereographic_projection
vec4 inversestereographic(vec3 p)
{
    float r2 = 1.0+dot(p,p);
    return vec4(2.0*p,r2-2.0)/r2;
}
//------------------------------------------------------------
// https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdRoundSquare( in vec2 p, in float s, in float r ) 
{
    vec2 q = abs(p)-s+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}
//------------------------------------------------------------
// texture a 4D sphere, |p|=1
vec3 tex( sampler2D sam, in vec4 p )
{
    // https://en.wikipedia.org/wiki/N-sphere#Spherical_coordinates
    vec3 q = acos(p.xyz/vec3(length(p.xyzw),
                             length(p.yzw),
                             length(p.zw) ));

    // here we could do actual 3D texturing, but instead,
    // i hacked three 2D texture projections
    vec3 col = vec3(0.0);
    col  = texture(sam,q.xy).xyz;
    col += texture(sam,q.yz).xyz;
    col += texture(sam,q.zx).xyz;
    col = col/3.0;

    // adjust contrast a bit to match source texture
    col *= 0.5+col*(1.5-col);

    // srgb to linear
    return col*col;
}
//===============================================================================================

vec4 r3_to_s3( in vec3 p )
{
    vec4 q = inversestereographic(p);

    // rotate in yw
    float a = iTime*6.283185/6.0;
    q.yw *= mat2(cos(a),sin(a),-sin(a),cos(a));

    return q;
}

vec2 F( in vec2 u, vec2 v )
{
    #if SHAPE==0
    return  64.0*cmul(u,csqr(u)) 
          - 12.0*cmul(u,vec2(3.0,0.0) - 2.0*csqr(v) + 2.0*csqr(conj(v))) 
          + 14.0*cmul(v,v) 
          + 14.0*csqr(conj(v)) 
          -      csqr(csqr(v)) 
          +      csqr(csqr(conj(v)));
    #endif

    #if SHAPE==1
    return 20.0*(cmul(u,csqr(csqr(u))) + csqr(v));
    #endif    

    #if SHAPE==2
    return 32.0*(cmul(u,conj(u))-cmul(v,conj(v)));
    #endif    

    #if SHAPE==3
    return  64.0*cmul(v,csqr(csqr(u)))
          + 32.0*cmul(u,csqr(v))
          + 32.0*csqr(v) 
          + 8.0;
    #endif    

    #if SHAPE==4
    float h = u.x*u.x - u.y*u.y + v.x*v.x - v.y*v.y;
    return vec2(u.y*u.y*u.y + v.y*v.y*h,  u.x*u.x*v.x + u.y*v.x*h)*32.0;
    #endif

}

float field( in vec4 q )
{
    vec2 phi = F(q.xy,q.zw);

    #if SHAPE==4
    const float th = 1.0;
    #else
    const float th = 10.0;
    #endif

    #if PROFILE==0
    return length(phi) - th;
    #else
    return sdRoundSquare( phi, 0.8*th, 0.05*th );
    #endif
}

float map( in vec3 p )
{
    return field(r3_to_s3(p));
}

float distance_map( in vec3 p )
{
    float d = field(r3_to_s3(p));

    // could do some automatic differentiation here...
    float eps = 0.0001;
    vec3 g = (vec3(field(r3_to_s3(p+vec3(eps,0.0,0.0))),
                   field(r3_to_s3(p+vec3(0.0,eps,0.0))),
                   field(r3_to_s3(p+vec3(0.0,0.0,eps))))-d)/eps;

    // distance estimator
    return d/length(g);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos, in float eps )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*eps;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

// https://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow( in vec3 ro, in vec3 rd, float k )
{
    const float tmax = 5.0;
    float t = 0.001;
    float sh = 1.0;
    for( int i=0; i<256; i++ )
    {
        vec3 pos = ro + rd*t;
        #if METHOD==0
        float f = distance_map(pos);
        sh = min( sh, clamp(k*f/t,0.0,1.0) );
        if( sh<0.001 ) break;
        #else
        float f = map(pos);
        sh = min( sh, clamp(0.02*k*f/t,0.0,1.0) );
        if( sh<0.001 ) break;
        const float eps = 0.001;
        float df = (map(ro+rd*(t+eps))-f)/eps;
        f = f/abs(df);
        #endif


        t += clamp(f,0.001,0.1);
        if( t>tmax ) break;
    }
    return sh;
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;
	for( int i=0; i<32; i++ )
	{
		float h = frand();
        vec3 kv = normalize( vec3(frand(), frand(), frand()) );
        kv *= sign(dot(kv,nor));
		ao += clamp( distance_map(pos+nor*0.001+kv*h*2.0)*1.0, 0.0, 1.0 );
	}
	ao /= 32.0;

	return clamp( ao*1.5-0.1, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // init random seed
    ivec2 q = ivec2(fragCoord);
    srand( hash(q.x+hash(q.y+hash(iFrame))));

    // camera movement	
    float an = -0.0*iTime/20.0;
    vec3 ro = vec3( 5.0*sin(6.283185*an), 4.0, 5.0*cos(6.283185*an) );
    #if SHAPE==1
    ro.xz *= 1.5;
    ro.y = 0.0;
    #endif
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 3.0*ww );


        vec3 col = vec3(0.0);
        col = vec3(0.05 + 0.05*rd.y);

        // raymarch
        if( map(ro)>0.0 )
        {
        const float tmax = 15.0;
        float t = 0.0;
        for( int i=0; i<8192; i++ )
        {
            vec3 pos = ro + rd*t;

            #if METHOD==0
            float f = distance_map(pos);
            if( abs(f)<0.001 ) break;
            float dt = f;
            #else
            float f = map(pos);
            if( abs(f)<0.001 ) break;
            const float eps = 0.001;
            float df = (map(ro+rd*(t+eps))-f)/eps;
            float dt = f/abs(df);
            #endif

            t += clamp(dt,-0.1,0.1);
            if( t>tmax ) break;
        }


        if( t<tmax )
        {
            vec3  pos = ro + rd*t;
            vec3  nor = calcNormal( pos, 0.0001 );
            float occ = calcAO( pos+nor*0.001, nor );

            // we do texturing in R4
            vec4 pos4 = r3_to_s3(pos);
            vec3 mate = tex(iChannel0,pos4);
            mate *= 0.3;

            // sun
            {
            vec3 lig = normalize(vec3(1.0,1.0,0.2));
            float dif = clamp(dot(nor,lig),0.0,1.0);
            if( dif>0.001 ) dif *= shadow(pos+nor*0.001, lig, 48.0);
            vec3 hal = normalize(lig-rd);
            float spe = pow(clamp(dot(nor,hal),00.,1.0),32.0);
            spe *= 0.04 + 0.96*pow( clamp(1.0-dot(lig,hal), 0.0, 1.0), 5.0 );
            spe *= 10.0;

            col  = 0.6*mate*vec3(9.0,8.0,7.0)*dif;
            col += spe*dif*mate.x*10.0;

            float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
            col += mate*fre*1.0*(0.25+0.75*dif)*vec3(1.0,0.7,0.4);
            }

            // sky
            {
            vec3 ref = reflect(rd,nor);
            float dif = 0.5+0.5*nor.y;
            float spe = smoothstep(0.3,0.5,ref.y);
            spe *= dif;
            spe *= 0.04 + 0.96*pow( clamp(1.0+dot(rd,nor), 0.0, 1.0), 5.0 );
            if( spe>0.001 ) spe *= shadow(pos+nor*0.001, ref, 8.0);
            col += 3.0*occ*mate*dif*vec3(0.5,0.6,1.0)*1.5;
            #if METHOD==1
            spe *= 2.0;
            #endif
            col += 1.0*spe;
            }

            // sun bounce
            col += mate*(1.0-occ)*vec3(1.2,0.5,0.2)*0.2;
        }
        }

        // gain
        col = col*1.5/(1.0+col);

        // to gamma space
        col = pow( col, vec3(0.4545) );
        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    // remove color banding through dithering
    tot += (1.0/255.0)*frand();

    fragColor = vec4( tot, 1.0 );
}

// Constants
float PI = 3.14159265358979323846;
vec2 ii = vec2( 0., 1. ); // imaginary unit


// Operations on complex numbers (represented as vec2)

// complex logarithm
vec2 clog(vec2 x){
    return vec2(log(x.x), atan(x.y, x.x));
}

// complex multiplication
vec2 mul(vec2 a, vec2 b){
    return vec2(a.x * b.x - a.y * b.y,
                a.x * b.y + a.y * b.x);
}

// complex conjugation
vec2 conj(vec2 a){
    return vec2(a.x, -a.y);
}

// complex inverse
vec2 inv(vec2 a) {
   return conj(a)/dot(a,a);
}

// complex division
vec2 div(vec2 a, vec2 b){
    return mul(a,inv(b));
}

// harmonic segment
float segment(vec2 z, vec2 p, vec2 q){
    return clog(div((z - p), (z - q))).y;
}

// map scalar value to pseudocolor
vec3 colorMap(float x){
    x = .5 + .5*x/PI;
    vec3 colA = vec3(1, 0, 0);
    vec3 colB = vec3(0, 0, 1);
    return colA * x + colB * (1.0f - x);
}

vec2 mobiusTransform(float a, float b, float c, float d, vec2 x){
    return div(a * x + b, c * x + d);
}

vec2 invMobiusTransform(float a, float b, float c, float d, vec2 x){
    return div((-d * x) + b, (c * x) - a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime; // short name for time

    // transform coordinate system so origin is in the center,
    // and so that pixels have a square aspect ratio
    vec2 z = (fragCoord-iResolution.xy/2.)/iResolution.x;
    //float r = sin(t * 2.0f) * 0.25f;
    float r = 0.25f;
    // float r = 0.125;
    // vec2 p = r*vec2(cos(t),sin(t));
    float s2 = sqrt(2.0) / 2.0;
    float diamPos = 1.0 / (2.0 * r * s2);
    float theta = sin(t) * (PI / 2.0);
    //float anglePos = (1.0 - sqrt(1.0 - 2.0 * (sin(theta)*sin(theta)))) / (2.0 * r * sin(theta));
    //float anglePos = 2.0 * r;
    float anglePos = sqrt((diamPos - (diamPos * diamPos * (r * s2)) - (diamPos * diamPos * (r * s2) * sin(theta)))
                         / (r + r * sin(theta)));
    vec2 p1 = vec2(diamPos, -anglePos);
    vec2 p2 = vec2(diamPos, anglePos);
    float a = 0.0;
    float b = 1.0;
    float c = 1.0;
    float d = 0.0;
    vec2 offset = vec2(r * s2,r * s2);
    // vec2 invPos = invMobiusTransform(a,b,c,d,pos);

    vec3 col = colorMap(segment(invMobiusTransform(a,b,c,d,z + offset), p1, p2) );

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    uv *= tanh(10. * cos(length(uv) + 0.5 * iTime));
    uv.x += 0.1 * cos(10. * uv.y + iTime);
    float a = atan(uv.y, uv.x);
    float d = length(uv) + 0.5
    + 0.5 * tanh( (0. + 1. * tanh(4. * cos(length(uv) + 96. * a + 2. * iTime))) 
                   * cos(80. * length(uv) +  10. * iTime) );
    d = d * (1.5 + tanh(2. * cos(0. * d + 2.* length(uv) * 
    (0. + tanh(1. * cos(192. * a + iTime))) + 3. * a+  1. * iTime)));
   // d *= 0.22;
    float s = step(d,0.4);

    vec3 col = vec3(s);

    fragColor = vec4(col,1.0);
}

