    k=clamp(k,0.01,1.0);
    float h = max( k-abs(a.w-b.w), 0.0 )/k;
	h=h*h*k*0.25;

	float invs=(1.0/colorContrast)/k; 
	colorSmooth=1.0/colorSmooth;
	vec3 c=mix(a.xyz,b.xyz,clamp((a.w*colorSmooth+shift+0.5-colorSmooth*0.5)*invs,0.0,1.0));

	return vec4(c,min( a.w, b.w ) - h);
