#define PI 3.1415326

mat2 rotate2D(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float distanceTo(vec3 p, float waveNum) {
    float frequency = exp(waveNum);
    //p.xz *= rotate2D(PI/3.);
    return abs(cos(p.x*frequency*PI)+sin(p.z*frequency*PI))/frequency;
}

float totalDistance(vec3 p) {
    // Initial distace
    float total = p.y;
    // This loop determines the number of waves being combined
    for(float j=0.; j < mod(iTime, 15.); j++) {
        // Add noise to initial surface
        total += distanceTo(p, j);
    }
    return total;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float i;
    float eyeDist, minDist;

    vec3 eyePos = vec3(0, 0, -1);
    vec3 ray = vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.x, 1.);
    ray = normalize(ray);

    // Camera motion
    eyePos.yz *= rotate2D(5.);
    eyePos.xz *= rotate2D(iTime);
    ray.yz *= rotate2D(5.);
    ray.xz *= rotate2D(iTime);

    for(minDist = 1.; i<100. && minDist>0.001; i++) {
        // Point to check
        vec3 p = eyePos+eyeDist*ray;

        minDist = totalDistance(p);
        // Move point forward
        eyeDist += minDist*.25;
    }
    i += 2.*(minDist*1000.+eyePos.z);
    fragColor = vec4(0.);
    fragColor += 600./(i*i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord-iResolution.xy/2.)/iResolution.yy)*10.;
    vec3 col=vec3(0);
    col=vec3(.1,.4+.05*uv.y,.5+.1*uv.y);
    float t=iTime*3.;
    vec2 ps[7];
    for(int k=0;k<6;k++)
    {
        ps[k]=vec2(0);
    }

    for(float f=0.;f<1.;f+=0.005)
    {
        for(int k=0;k<6;k++)
        {
            vec2 p=ps[k];
            float j=float(k);
            float r=sin(f*48.+sin(t+j*3.1));
            r*=r;
            float w=clamp((1.-length(uv-p*.03)*4.-f*.6-r*r*.1)*5.,0.,1.);
            vec3 c=vec3(.6,.4,.2);
            col=mix(col, (-r+2.)*c*(uv.y-p.y*.03+.05)*2.5*(1.+f)+vec3(.5,.3,.3), w);
            float af=t*.1+j+sin(f*12.*(11.7+j*1.71)*.05-t*(26.3+j*1.87)*.02+j*3.1+117.)*f*5.;
            ps[k]+=vec2(sin(af), cos(af))*1.7;
        }
    }
    fragColor = vec4(col,1.0);
}

