    int i;

    float q = smoothstep(1.0, 0.98, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))
               - smoothstep(0.98, 0.8, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))
            +smoothstep(0.8, 0.78, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))+
               - smoothstep(0.78, 0.6, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))
            +smoothstep(0.6, 0.58, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))+
               - smoothstep(0.58, 0.4, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))
            +smoothstep(0.4, 0.38, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))+
               - smoothstep(0.38, 0.2, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))
            +smoothstep(0.2, 0.18, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)))+
               - smoothstep(0.18, 0.0, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/2.0)));
            //-smoothstep(0.2, 0.1, abs(sin(sqrt(uv.x*uv.x + uv.y*uv.y)-iTime/3.0)));
    // Time varying pixel color
    vec3 col = vec3(q,0.4,0.6);


    // Output to screen
    fragColor = vec4(col,1.0);
}

