void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time=iTime;
    vec2 resolution=iResolution.xy;

    vec2 uv = -1.0 + 2.0 * gl_FragCoord.xy/resolution.xy;

    uv.x *= resolution.x/resolution.y;
	uv *=length(uv+vec2(sin(time)*.5,cos(time)*.5))*.3;
	uv /=length(5.*uv+vec2(sin(.3*time)*.75,cos(.8*time)*.75))*3.;

	float v=length(uv)*3.;
    v=clamp(0.,1.,v);


    uv.xy +=sin(uv.xy*4.)*.1;
	uv.xy +=sin(uv.xy*12.)*.1;
	uv.xy +=sin(time+length(uv)+.5+sin(time*.2)*8.)*.1;
	uv.xy +=sin(uv.xy*32.)*sin(sin(time*.5)*uv.xy*12.)*.1;


	float uvL=length(uv);

	uv.xy +=sin(1.7*time+uv.xy*2.)*.13;
	float r=length(uv);
	r=clamp(r,0.,1.);
	uv.xy +=sin(1.4*time+uv.xy*2.)*.25;
	float g=length(uv);
	g=clamp(g,0.,1.);
	uv.xy +=sin(1.5*time+uv.xy*4.)*.57;
	float b=length(uv);
	b=clamp(b,0.,1.);


	uvL=smoothstep(.2,.23,uvL);
	uvL=2.75-exp(uvL);
	uvL=max(v,uvL);
    fragColor = vec4(vec3(uvL)*vec3(r*.89,g*.87,b*.9)*v,1.);
}

#define hash(x) fract(485.375 * cos(375.843 * x + 881.482))
void brushedDisk(in vec2 uv, inout vec3 color) {
    float r = length(uv), a = atan(uv.y, uv.x);
    bool mask = r < 0.5;
    r *= 1.5 * iResolution.y;
    float bands = mix(hash(floor(r)), hash(ceil(r)), smoothstep(0.0, 1.0, fract(r)));
    float glint = 0.65 + 0.35 * sin(4.0 * a);
    if (mask) color = vec3(bands * glint);
}

void render(inout vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y + 0.1 * iTime;
    uv = fract(uv * 2.0) - 0.5;
    vec3 color = vec3(0.0);

    brushedDisk(uv - vec2(0.5, -0.5), color);
    brushedDisk(uv + 0.5, color);
    brushedDisk(uv, color);
    brushedDisk(uv - 0.5, color);
    brushedDisk(uv - vec2(-0.5, 0.5), color);

    fragColor.rgb += color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0);
    render(fragColor, fragCoord + vec2(0.000, 0.000));
    render(fragColor, fragCoord + vec2(0.333, 0.000));
    render(fragColor, fragCoord + vec2(0.666, 0.000));
    render(fragColor, fragCoord + vec2(0.000, 0.333));
    render(fragColor, fragCoord + vec2(0.333, 0.333));
    render(fragColor, fragCoord + vec2(0.666, 0.333));
    render(fragColor, fragCoord + vec2(0.000, 0.666));
    render(fragColor, fragCoord + vec2(0.333, 0.666));
    render(fragColor, fragCoord + vec2(0.666, 0.666));
    fragColor /= 9.0;
}

#ifdef GL_ES
precision mediump float;
#endif

#define NUM_LAYERS 5.0
#define NUM_OCTAVES 5


mat2 getRotationMatrix(float angle) {
    float s = sin(angle);
    float c = cos(angle);

    return mat2(c, -s,
                s, c);
}


float random(vec2 pos) {
    pos = fract(pos * vec2(123.34, 456.21));
    pos += dot(pos, pos + 45.32);
    return fract(pos.x * pos.y);
}


float circle(vec2 uv, float radius, float smoothThickness){
    return smoothstep(radius - smoothThickness, radius + smoothThickness, length(uv));
}


float createDrop(vec2 uv, float width) {
    float soft = 0.0;
    float drop = circle(uv, 0.1, 0.001);
    return 1.0 - drop;
}

vec3 createLayer(vec2 uv){
    vec3 result = vec3(0);
    vec2 copies = fract(uv);
    vec2 index = floor(uv);
    for(float y=-1.0; y<=1.0; y++) {
    	for(float x=-1.0; x<=1.0; x++) {
            vec2 offset = vec2(x, y);
    		float rnd = random(index + offset);
            float size = fract(rnd * 345.32);
            float drop = createDrop(copies - offset - vec2(rnd, fract(rnd * 34.0)) + 0.5, 0.005);
            result += drop * size * vec3(0.4706, 0.4706, 0.4706);
        }
    }

    return result;
}



float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float fbm(vec2 x) {
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy / iResolution.xy;

    float aspect = iResolution.x / iResolution.y;
    if(aspect > 1.0) {
        uv-= 0.5;
        uv.x *= aspect;
        uv+= 0.5;
    }
    if(aspect < 1.0) {
        uv-= 0.5;
        uv.y *= 1.0 / aspect;
        uv+= 0.5;
    }


    float time = iTime * 0.01;
    vec3 color = vec3(0.0275, 0.0275, 0.2902);
    mat2 matr = getRotationMatrix(10.0 * 3.14 / 180.0);
    uv = (matr * (uv - 0.5)) + 0.5;
    float strength = smoothstep(0.2, 1.0, uv.y);

    vec2 surface = strength * vec2(
        mix(-0.3, 0.3, fbm(8.0 * uv + iTime * 0.1)), 
        mix(-0.3, 0.3, fbm(1.5 * uv + iTime * 0.1))
    );
    uv = refract(vec2(0.0), surface, 1.0 / 1.333);

    for(float i=0.0; i<1.0; i += 1.0 / NUM_LAYERS) {
        float depth = fract(i);
        float scale = mix(55.0, 10.0, depth);
        vec2 offset = vec2(0.0, mod(time, 1000.0) * (depth + 0.3) * 0.6);
        vec3 layer = createLayer((uv + offset) * scale);
        color = mix(color, vec3(1.0, 1.0, 1.0), layer);
    }

    fragColor = vec4(color, 1.0); 
}

// Rotating Area Lights Lines
// By Ori Hanegby
// Inspired by this thread: 
// https://www.facebook.com/groups/shadertoy/posts/954364335144593
// Line segment code from iq's tutorials: https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
// Tone mapping code from here: https://www.shadertoy.com/view/7scXD4


float line_segment(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba);
}


// In order to calculate the area light I'm treating the line area as continous line of infinite directional light sources
// To calculate how much light each point (uv) receives I'm using two factors: the distance from the line to the point and the integration of 
// all the sin(angle) between the point and the light sources on the line. The integral between angles b and a on the line segment have an easy solution
// -cos(b)+cos(a) 
// cos(b) and cos(a) are given by the dot products between the two edges on of the line that are stored in l1 and l2
// the integral result is multiplied by hh which gives control of the light intensity relative to the distance from the line
// In order to know which side of the line the point resides on I'm using cross product where the sign of the z component indicates the direction
vec2 area_light_intensity(vec2 p, vec2 v1, vec2 v2, float diffusePower, float shinePower)
{  
    vec2 areaLine = normalize(v2-v1);
    float l1 = dot(normalize(p-v1),areaLine);
    float l2 = dot(normalize(p-v2),areaLine);
    vec3 crs=cross(vec3(p-v1,0.),vec3(areaLine,0.));
    float d = line_segment(p,v1,v2);
    float hh=max(0.,pow(max(0.01,shinePower-d),4.5));
    float c=(-l2 + l1)*hh;
    return vec2(pow(c,diffusePower),crs.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv =  (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv*=6.;
    float t=0.;
    float c=0.;
    vec3 col=vec3(0.);
    for (int i=0; i<3; i++){
        t+=1.;
        float angle = iTime/10. + t*5.;

        vec2 line = vec2(sin(angle),cos(angle));
        float s=1.6;    
        vec2 v1 = -line*s;
        vec2 v2 = line*s;  
        vec2 disp = vec2(sin(t*2.1),cos(t*2.1))*2.;
        v1 += disp;
        v2 += disp;
        vec2 intes_w_sign=area_light_intensity(uv, v1, v2, 1., 5.);
        float intens=intes_w_sign.x/pow(2.,7.);
        vec3 tCol = intes_w_sign.y>0.?vec3(1.0,0.25,0.05) * intens:
        vec3(1.0,0.12,0.02) * intens/5.;        
        col += tCol;
    }


    col = col / (1. + col); // Tone mapping
    col = sqrt(col); // Gamma

    fragColor = vec4(col, 1);       
}

