vec4[2] spheres = vec4[2](
vec4(-6.0f,0.0f,5.0f,4.0f),
vec4(6.0f,0.0f,5.0f,4.0f)
);
float plane = -4.0f;
vec3 lPos = vec3(10.0f,8.0f,10.0f);
vec3 lightCol = vec3(1.0f,1.0f,0.5f);
