vec2 g_random(vec2 ip){
	return fract(sin(
		vec2(dot(ip,vec2(127.1,311.7)), dot(ip,vec2(269.5,183.3)))
	)* 44753.976967) *2. - 1.;
}

float g_noise(vec2 st){
	vec2 ip = floor(st);
	vec2 fp = fract(st);

	vec2 u = fp*fp*fp*(fp*(fp*6.-15.)+10.);//使用三次多项式进行插值
	return mix(
		mix(dot(g_random(ip) , fp-vec2(0.,0.)) , dot(g_random(ip+vec2(1.,0.)) , fp-vec2(1.,0.)) ,u.x),
		mix(dot(g_random(ip+vec2(0.,1.)) , fp-vec2(0.,1.)) , dot(g_random(ip+vec2(1.,1.)) ,fp-vec2(1.,1.)) , u.x),
		u.y
	);
}


float circle(vec2 st, vec2 center){
    return distance(st, center);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 beforeColor = vec4(1.0);

    uv.x -= abs(sin(iTime/4.0));
    uv.y -= abs(sin(iTime/2.0))/1.5;
    if(circle(uv, vec2(0,0)) <=0.15){

        beforeColor = vec4(1.000,smoothstep(0.566, 0.98, circle(uv, vec2(0,0))+0.69),0.3, 1.0);
    } 
    uv -= 0.5;


    //To prevent clouds and the sun from moving together, they need a separate coordinate system
    vec2 copy = fragCoord/iResolution.xy;
    copy -= 0.5;
    copy *= vec2(4.,4.);
    copy += g_noise(copy*3.);
    copy.x = sin(iTime/2.0);


    beforeColor += (g_noise(copy) -.5)*.19;
    // Output to screen
    fragColor = beforeColor;
}

#define MAX_STEPS 40
#define MAX_DIST 40.
#define SURF_DIST .005
#define samples 32
#define LOD 2

float hash(vec2 n) {
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 184.5453);
}

float noise(vec2 n) {
    const vec2 d = vec2(0.0, 1.0);
    vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
    return mix(mix(hash(b), hash(b + d.yx), f.x), mix(hash(b + d.xy), hash(b + d.yy), f.x), f.y);
}

float sdSphere( vec3 p, float s ) { return length(p)-s; }


float smin( float a, float b, float k ) {
    float h = max(k-abs(a-b), 0.0);
    return min(a, b) - h*h*0.25/k; 
}

float getDist(vec3 p) {
    float final = MAX_DIST;
    float iTime = iTime; 
    p = p - vec3(0.,0.5, 5.);
    for (int i = 0; i < 10; i++) {
        float fi = float(i)  + floor(float(i) / 5.);
        vec3 pos = p;
        pos.xy += vec2(sin(iTime + fi), cos(iTime + fi * 2.)) * 0.4;
        pos.z += sin(iTime * cos(float(i * 4))) * 0.2;
        float r = sin(fi + 1.) * 0.15;
        float n = min(sin(pos.z * float(i) * 5.), cos(pos.x * pos.y * float(i) * 10.)) * 0.1;
        float bubble = sdSphere(pos + vec3(n) * 0.1 - vec3(0.05), r);

        final = smin(final, bubble, 0.4);
    }


    return final;
}
float rayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float matId = -1.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float res = getDist(p);
        float dS = res;
        dO += dS;

        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 normals(vec3 p, float of ) {
	float d = getDist(p);
    vec2 e = vec2(of, 0);

    vec3 n = d - vec3(
        getDist(p-e.xyy),
        getDist(p-e.yxy),
        getDist(p-e.yyx));

    return normalize(n);
}

float diffuse(vec3 p, vec3 n, vec3 lp) {
    vec3 l = normalize(lp-p);
    float dif = clamp(dot(n, l), 0., 1.);

    return dif;
}

float specular(vec3 rd, vec3 ld, vec3 n) {    
    vec3 reflection = reflect(-ld, n);
    float spec = max(dot(reflection, -normalize(rd)), 0.); 
    return spec;
}

float gaussian(vec2 i) {
const float sigma = float(samples) * .25;
    return exp( -.5* dot(i/=sigma,i) ) / ( 6.28 * sigma*sigma );
}

vec4 blur(sampler2D sp, vec2 U, vec2 scale) {
    const int  sLOD = 1 << LOD;
    vec4 O = vec4(0);
    int s = samples/sLOD;

    for ( int i = 0; i < s*s; i++ ) {
        vec2 d = vec2(i%s, i/s)*float(sLOD) - float(samples)/2.;
        O += gaussian(d) * texture(sp, U + scale * d);
    }

    return O / O.a;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mo = (iMouse.xy-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    float iTime = iTime * 2.;

	vec3 ro = vec3(.0, 0., 1.);
    vec3 rd = normalize(vec3(uv.x, uv.y + 0.2 , 2.));
    vec3 ld =  vec3(0., 0., 1.);
    float d = rayMarch(ro, rd);
    vec3 p = ro + rd * d;
    vec3 n = normals(p, 0.03);
    float dif = diffuse(p, n, ld); 
    float spec = specular(rd, ld, n) * 0.1;
    float fresnel = smoothstep(0.5, 0.2, dot(-rd, n));
    vec3 dispersion = vec3(noise(n.xy * 2.7), noise(n.xy * 3.), noise(n.xy * 3.3)) * 0.4; 

    vec2 camUV = fragCoord / iResolution.xy;
    vec3 cam1 = texture(iChannel0, camUV).xyz * 0.9;
    camUV += n.xy * 0.05 * dif;
    vec3 cam2 = blur(iChannel0, camUV, vec2(0.002)).xyz * 0.9;

    col = dif * cam2;
    col += spec;
    col += cam2 * 0.15;         
    col += dispersion;
    col += fresnel * 0.2;

    if (d > MAX_DIST) { col = vec3(cam1);  }

    fragColor = vec4(col, 1.0);
}

