#define FIX_LO_PIC  0  // = 1 to freeze image in shredded part of picture

float PrSphDf (vec3 p, float r);
float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
vec3 HsvToRgb (vec3 c);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Hashff (float p);
float Hashfv3 (vec3 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir, qHit;
float tCur, dstFar, pWid, hMax, hFrac, hCur, nCyc, tCyc, tFrac;
int idObj;
const int mxStep = 200;
const float pi = 3.1415927;

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, sWid, fx;
  dMin = dstFar;
  q = p;
  q.y -= hFrac - hCur;
  d = PrRoundBoxDf (q, vec3 (pWid, hMax - hCur, 0.), 0.005);
  q.y += hCur;
  DMINQ (1);
  sWid = 0.1;
  q = p;
  d = abs (q.x) - pWid;
  q.y -= - hMax + hFrac - hCur;
  fx = floor (q.x / sWid);
  q.x -= sWid * (fx + 0.5);
  q.xz = Rot2D (q.xz, 0.2 * pi * (Hashff (fx + 17.33 * nCyc) - 0.5) * (q.y - hCur));
  d = max (d, PrRoundBoxDf (q, vec3 (0.5 * sWid - 0.008, hCur, 0.), 0.005));
  q.x = p.x;
  q.y += hCur - hMax;
  DMINQ (1);
  q = p;
  q.y -= hFrac;
  d = SmoothMax (PrRoundBoxDf (q, vec3 (pWid + 0.15, hMax + 0.15, 0.02), 0.02),
     - PrBox2Df (q.xy, vec2 (pWid, hMax)), 0.02);
  DMINQ (2);
  q.z -= 0.01;
  d = PrBoxDf (q, vec3 (pWid, hMax, 0.01));
  DMINQ (3);
  q = p;
  d = 10. - q.z;
  DMINQ (4);
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float FracDf (vec3 p)
{  // (KIFS fractal simplified from leon's "Taste of Noise 7")
  vec3 ip, a;
  float s, gSize, f, w;
  p.x += 0.02 * tFrac;
  gSize = 3.;
  ip = floor (p / gSize);
  p -= (ip + 0.5) * gSize;
  a = vec3 (0.1, 0.2, 0.3) + 0.5 * length (p) + 0.1 * p + ip;
  w = 5. + 3. * sin (2. * pi * Hashfv3 (ip) + 0.7 * tFrac);
  s = gSize;
  f = 0.1;
  for (int j = 0; j < 5; j ++) {
    p.xz = abs (p.xz) - w * f;
    p.yz = Rot2D (p.yz, a.x);
    p.xz = Rot2D (p.xz, a.y);
    p.yx = Rot2D (p.yx, a.z);
    s = SmoothMin (s, PrSphDf (p, f), f);
    f *= 0.5;
  }
  return 0.5 * s;
}

vec2 FracRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  int nStep;
  dHit = 0.;
  nStep = 0;
  for (int j = VAR_ZERO; j < mxStep; j ++) {
    d = FracDf (ro + dHit * rd);
    ++ nStep;
    if (d < 0.01 || dHit > dstFar) break;
    dHit += d;
  }
  if (nStep == mxStep) nStep = 0;
  return vec2 (dHit, float (nStep));
}

vec3 FracNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = FracDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 FracCol (vec2 p)
{
  vec3 ro, rd, col, vn;
  vec2 ff;
  float dstFrac;
  ro = vec3 (0., 0., -10.);
  rd = normalize (vec3 (p, 5.));
  ff = FracRay (ro, rd);
  col = vec3 (0.);
  if (ff.x < dstFar) {
    ro += ff.x * rd;
    if (ff.y > 0.) {
      vn = FracNf (ro);
      col = HsvToRgb (vec3 (mod (ff.y / float (mxStep) + 0.03 * tFrac, 1.), 0.9, 1.)) * vn.z * vn.z;
      col = pow (clamp (col, 0., 1.), vec3 (0.7));
    }
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, t;
  pWid = 1.;
  hMax = 1.3;
  hFrac = 0.7 * hMax;
  t = fract (tCur / tCyc);
  nCyc = floor (tCur / tCyc);
  hCur = hMax * (0.1 + 0.85 * (1. - smoothstep (0.75, 0.8, t)) * smoothstep (0.1, 0.4, t)) - 0.15;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) {
      col4 = vec4 (1., 1., 1., 0.);
      if (qHit.z < 0.) {
        tFrac = tCur;
#if FIX_LO_PIC
        if (ro.y < hFrac - hMax) tFrac = nCyc * tCyc;
#endif
        qHit.y += 0.05;
        if (PrBox2Df (qHit.xy, vec2 (pWid)) < -0.05) col4 = vec4 (FracCol (qHit.xy / pWid +
           vec2 (1., 1.1)), -1.);
      }
    } else if (idObj == 2) {
      col4 = vec4 (0.5, 0.4, 0.2, 0.2);
    } else if (idObj == 3) {
      col4 = vec4 (0.6, 0.3, 0.1, 0.) * (0.8 + 0.2 * Fbm2 (64. * ro.xy));
    } else if (idObj == 4) {
      col4 = vec4 (0.4, 0., 0., 0.);
      vn = VaryNf (32. * ro, vn, 1.);
    }
    if (col4.a >= 0.) {
      col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
         col4.a * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
      col *= 1. - smoothstep (0.2, 0.6, dstObj / dstFar);
    } else col = col4.rgb;
  } else {
    col = vec3 (0.);
  }
  return clamp (col, 0., 1.);
}

#define AA  1   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCyc = 15.;
  t = smoothstep (0.15, 0.25, 0.5 - abs (fract (tCur / tCyc) - 0.5));
  if (abs (uv.x) < 1.1 - 0.4 * t) {
    az = 0.;
    el = 0.;
    if (mPtr.z > 0.) {
      az += 2. * pi * mPtr.x;
      el += pi * mPtr.y;
    }
    az = clamp (az, -0.6 * pi, 0.6 * pi);
    el = clamp (el, -0.3 * pi, 0.3 * pi);
    vuMat = StdVuMat (el, az);
    ro = vuMat * mix (vec3 (0., 0.9, -7.), vec3 (0., 0., -12.), t);
    zmFac = 4.5;
    dstFar = 100.;
    ltDir = normalize (vec3 (0.5, 1., -1.));
#if ! AA
    const float naa = 1.;
#else
    const float naa = 3.;
#endif  
    col = vec3 (0.);
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = float (VAR_ZERO); a < naa; a ++) {
      rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
         sr * (0.667 * a + 0.5) * pi), zmFac));
      col += (1. / naa) * ShowScene (ro, rd);
    }
  } else col = vec3 (0.82);
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

// optimized from https://www.shadertoy.com/view/MdXSzS
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy/iResolution.xy)-.5;
	float len = length(uv.xy);

    float t = .01*iTime;
	float time = t  +  (5.+sin(t))*.11 / (len+.07); // spiraling
	float si = sin(time), co = cos(time);
	uv *= mat2(co, si, -si, co);                    // rotation

	float c=0., v1=0., v2=0., v3;  vec3 p;

	for (int i = 0; i < 100; i++) {
		p = .035*float(i) *  vec3(uv, 1.);
		p += vec3(.22,  .3,  -1.5 -sin(t*1.3)*.1);

		for (int i = 0; i < 8; i++)                // IFS
			p = abs(p) / dot(p,p) - 0.659;

		float p2 = dot(p,p)*.0015;
		v1 += p2 * ( 1.8 + sin(len*13.0  +.5 -t*2.) );
		v2 += p2 * ( 1.5 + sin(len*13.5 +2.2 -t*3.) );
	}

	c = length(p.xy) * .175;
	v1 *= smoothstep(.7 , .0, len);
	v2 *= smoothstep(.6 , .0, len);
	v3  = smoothstep(.15, .0, len);

	vec3 col = vec3(c,  (v1+c)*.25,  v2);
	col = col  +  v3*.9;                      // useless: clamp(col, 0.,1.)
	fragColor=vec4(col, 1.);
}

// "Screen Space Curvature Shader" by Evan Wallace:
// http://madebyevan.com/shaders/curvature/
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

// Change shading type
#define CURVATURE 0
#define METAL 0
#define RED_WAX 1


// Shoe Model from my shader.
// "A man from 'A LOST MEMORY'" by iYOYi:
// https://www.shadertoy.com/view/Ws3yW4
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

#define PI 3.14159265
#define TAU (2.0*PI)
#define saturate(x) clamp(x, 0.0, 1.0)

#define MIN_DIST 0.001
#define MAX_DIST 30.0
#define ITERATION 100

#define SHOW_ANIM 0
vec3 ro = vec3(0), rd = vec3(0);

// Cheap Rotation by las:
// http://www.pouet.net/topic.php?which=7931&page=1
#define R(p, a) p=cos(a)*p+sin(a)*vec2(p.y,-p.x)
vec3 rot(vec3 p,vec3 r){
    R(p.xz, r.y);
    R(p.yx, r.z);
    R(p.zy, r.x);
    return p;
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

float fbm(vec2 n, int rep){
    float sum = 0.0;
    float amp= 1.0;
    for (int i = 0; i <rep; i++){
        sum += noise(n) * amp;
        n += n*4.0;
        amp *= 0.25;
    }
    return sum;
}

// SDF functions by iq and HG_SDF
// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// https://mercury.sexy/hg_sdf/
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float vmax(vec3 v){
    return max(max(v.x, v.y), v.z);
}

float sdBox( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdEllipsoid(in vec3 p, in vec3 r) {
    return (length(p/r)-1.0)*min(min(r.x,r.y),r.z);
}

float sdCappedTorus(vec3 p, vec2 r, float per) {
    p.x = abs(p.x);
    vec2 sc = vec2(sin(per),cos(per));
    float k = (sc.y*p.x>sc.x*p.z) ? dot(p.xz,sc) : length(p.xz);
    return sqrt( dot(p,p) + r.x*r.x - 2.0*r.x*k ) - r.y;
}

float sdBox(vec3 p,vec3 b) {
    vec3 d=abs(p)-b;
    return length(max(d,vec3(0)))+vmax(min(d,vec3(0.0)));
}

float fOpUnion(in float a,in float b) {
    return a<b?a:b;
}

vec4 v4OpUnion(in vec4 a,in vec4 b) {
    return a.x<b.x?a:b;
}

float fOpUnionSmooth(float a,float b,float r) {
    vec2 u = max(vec2(r - a,r - b), vec2(0));
    return max(r, min (a, b)) - length(u);
}

float fOpSubstraction(in float a,in float b) {
    return max(-a, b);
}

float fOpSubstractionSmooth( float a,float b,float r) {
    vec2 u = max(vec2(r + b,r + -a), vec2(0));
    return min(-r, max (b, -a)) + length(u);
}

void pElongate(inout float p, in float h ) {
    p = p-clamp(p,-h,h);
}

float sdFoot(vec3 p) {
	float d = MAX_DIST;
	float bsd = length(p), bsr=0.2500;
	if (bsd > 2.*bsr) return bsd-bsr;

	vec3 cpFoot = p;
	{
		vec3 q = cpFoot;
#if SHOW_ANIM
        float patapata = -q.z*(sin(iTime*5.)*.5+.05)+cos(iTime*5.)*.5;
#else
        float patapata = -.3;
#endif
        q.yz*=mat2(cos(-q.z*1.25+patapata+vec4(0,11,33,0)));
        cpFoot=q;
	}
	vec3 cpFoot_Main = cpFoot;
	cpFoot_Main.xyz += vec3(0.0000, 0.0000, 0.1273);
	pElongate(cpFoot_Main.y, 0.0125);
	{
		vec3 q=cpFoot_Main;
        vec3 pq=q;pq.yz *= mat2(cos(.6 + vec4(0, 11, 33, 0)));
        float ycl = smoothstep(.002,.2,q.y);
        float zcl = 1.-smoothstep(-.2,.5,q.z);
        float zcl2 = smoothstep(-.2,.0,q.z);
        q.z+=fbm(vec2(pq.x*20.5,pq.y*80.), 1)*.075*ycl*zcl*zcl2;
        cpFoot_Main=q;
	}

    // Shoe
    float d1,d2;
	d1 = sdEllipsoid(rot(cpFoot_Main+vec3(-0.0005, 0.0274, 0.1042), vec3(0.0818, -0.6861, 0.0566)), vec3(0.1102, 0.1233, 0.1214));
	d1 = fOpUnionSmooth(sdEllipsoid(rot(cpFoot_Main+vec3(0.0028, -0.0093, -0.1258), vec3(-0.0291, -0.2744, -0.0364)), vec3(0.0870, 0.2295, 0.0880)), d1, 0.1438);
	d1 = fOpSubstractionSmooth(sdBox(cpFoot+vec3(0.0000, -0.194, 0.0019), vec3(0.1676, 0.0551, 0.1171)), d1, 0.0100);
    d1 = fOpSubstractionSmooth(dot(cpFoot_Main, vec3(0,1,0)) - .001, d1, 0.0080);
	d1 = fOpSubstraction(sdBox(rot(cpFoot+vec3(0.0000, 0.0171, 0.1521), vec3(-1.4413, 0.0000, 0.0000)), vec3(0.1676, 0.0912, 0.0116)), d1);
	d1 = fOpUnionSmooth(sdCappedTorus(cpFoot+vec3(0.0028, -0.1578, 0.0014), vec2(0.0519, 0.0264), 3.1413), d1, 0.0100);

	// Shoe lace
	d2 = sdCappedTorus(rot(cpFoot+vec3(0.0000, -0.0579, 0.1827), vec3(1.5708, 0.0000, 0.0000)), vec2(0.0636, 0.0064), 0.6283);
	d2 = fOpUnion(sdCappedTorus(rot(cpFoot+vec3(0.0000, -0.1001, 0.0608), vec3(2.2401, -0.3407, 0.2843)), vec2(0.0636, 0.0064), 0.6283), d2);
	d2 = fOpUnion(sdCappedTorus(rot(cpFoot+vec3(0.0000, -0.0639, 0.1321), vec3(1.7335, 0.4446, -0.0513)), vec2(0.0636, 0.0064), 0.6283), d2);
	d2 = fOpUnion(sdCappedTorus(rot(cpFoot+vec3(0.0000, -0.1001, 0.0608), vec3(2.2463, 0.3180, -0.2669)), vec2(0.0636, 0.0064), 0.6283), d2);
	d2 = fOpUnion(sdCappedTorus(rot(cpFoot+vec3(0.0000, -0.0639, 0.1321), vec3(1.7334, -0.4468, 0.0515)), vec2(0.0636, 0.0064), 0.6283), d2);

	return min(d1,d2);
}

float sdScene(vec3 p) {
    return sdFoot(p);
}

float intersect() {
    float d = MIN_DIST;

    for (int i = 0; i < ITERATION; i++) {
        vec3 p = ro + d * rd;
        float res = sdScene(p);
        res*=.5;
        if (abs(res) < MIN_DIST)break;
        d += res;
        if (d >= MAX_DIST) return MAX_DIST;
    }
    if(d>MAX_DIST) return MAX_DIST;
    return d;
}

// from iq's shader
vec3 normal(vec3 p) {
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ ) {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*sdScene(p+0.0005*e);
    }
    return normalize(n);
}

// Camera localized normal
vec3 campos, camup;
vec3 localNormal(vec3 p) {
    vec3 n = normal(p), ln;
    vec3 side = cross(campos, camup);
    return vec3(dot(n,  side), dot(n,  camup), dot(n,  -rd));
}

// "camera": create camera vectors.
void camera(vec2 uv) {
    const float pY = .5;
    const float cL = 10.;
    const vec3 forcus = vec3(0,.08,-.137);
    const float fov = .015;

    vec3 up = vec3(0,1,0);
    vec3 pos = vec3(0,0,0);
    pos.xz = vec2(sin(iTime*.6),cos(iTime*.6))*cL;
    if(iMouse.z>.5)
        pos = vec3(sin(iMouse.x/iResolution.x*TAU),sin(iMouse.y/iResolution.y*TAU),cos(iMouse.x/iResolution.x*TAU))*cL;
    vec3 dir = normalize(forcus-pos);
    vec3 target = pos-dir;
    vec3 cw = normalize(target - pos);
    vec3 cu = normalize(cross(cw, up));
    vec3 cv = normalize(cross(cu, cw));
	campos = cw, camup = cv;
    mat3 camMat = mat3(cu, cv, cw);
    rd = normalize(camMat * normalize(vec3(sin(fov) * uv.x, sin(fov) * uv.y, -cos(fov))));
    ro = pos;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = (uv*2.-1.);
    uv.x *= iResolution.x / iResolution.y;

    camera(uv);
    float hit = intersect();
    vec3 p = ro + hit * rd;

    vec3 n = localNormal(p);
    float depth = distance(ro, p)/MAX_DIST;

    // I've mostly just copied and pasted Evan's code.
    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
	// Compute curvature
	vec3 dx = dFdx(n);
	vec3 dy = dFdy(n);
	vec3 xneg = n - dx;
	vec3 xpos = n + dx;
	vec3 yneg = n - dy;
	vec3 ypos = n + dy;
	float curvature = (cross(xneg, xpos).y - cross(yneg, ypos).x) * 2.0 / (depth*5.);

	// Compute surface properties
    #if CURVATURE
        vec3 light = vec3(0.0);
        vec3 ambient = vec3(curvature + 0.5);
        vec3 diffuse = vec3(0.0);
        vec3 specular = vec3(0.0);
        float shininess = 0.0;
    #elif METAL
        float corrosion = clamp(-curvature * 3.0, 0.0, 1.0);
        float shine = clamp(curvature * 5.0, 0.0, 1.0);
        vec3 light = normalize(vec3(0.0, 1.0, 10.0));
        vec3 ambient = vec3(0.15, 0.1, 0.1)*.5;
        vec3 diffuse = mix(mix(vec3(0.3, 0.25, 0.2), vec3(0.45, 0.5, 0.5), corrosion),
        vec3(0.5, 0.4, 0.3), shine) - ambient;
        vec3 specular = mix(vec3(0.0), vec3(1.0) - ambient - diffuse, shine);
        float shininess = 128.0;
    #elif RED_WAX
        float dirt = clamp(0.25 - curvature * 4.0, 0.0, 1.0);
        vec3 light = normalize(vec3(0.0, 1.0, 10.0));
        vec3 ambient = vec3(0.05, 0.015, 0.0);
        vec3 diffuse = mix(vec3(0.4, 0.15, 0.1), vec3(0.4, 0.3, 0.3), dirt) - ambient;
        vec3 specular = mix(vec3(0.15) - ambient, vec3(0.0), dirt);
        float shininess = 32.0;
    #endif

    // Compute final color
    float cosAngle = dot(n, light);
    fragColor.rgb = ambient +
    diffuse * max(0.0, cosAngle) +
    specular * pow(max(0.0, cosAngle), shininess);
    //~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

    fragColor.rgb = pow(fragColor.rgb*1.5, vec3(.9));
    if(depth>.9) fragColor.rgb = vec3(.125);
	fragColor.a = 1.;
}

/*

    Extruded Pixelated Spiral
    -------------------------

	On Shadertoy I often come across very simple 2D examples that I like.
    Sometimes, if I think the base pattern is interesting enough, I might
    extrude it just to see what it looks like. Extruded imagery is a bit
    of a computer graphics cliche, but I like it.

    This particular image is loosely based on a minimal spiral image by
    Foxic -- The link is below, for anyone interested. To adhere to the 
    spirit of the original, I've extruded it in a pixelized manner and kept 
    roughly the same palette. However, it looks pretty interesting in other 
    colors too.

    Technically, there's not a lot to this. Hopefully, it'll run fine on
    most systems. It'd be a lot more efficient to render the 2D spiral on
    a backbuffer first, but I wanted to keep things simple.



	Inspired by:

	// I like the minimal design.
    Pixelized IceCreamSwirl - foxic
	https://www.shadertoy.com/view/NdVXWz

    // Tater's been putting up some pretty nice shaders lately.
    Spiraled Layers - Tater
    https://www.shadertoy.com/view/Ns3XWf


*/


// Global tile scale.
vec2 scale = vec2(1./8., 1./8.);

// Off the rows by half a cell to produce a brickwork feel. The staggered 
// effect can also make a quantized image look smoother.
#define ROW_OFFSET

// Use hexagon pylons instead of squares.
//#define HEXAGON

// Boring out holes on alternate blocks to give it more of a tech feel and
// to provide a little extra visual stimuli. Without the holes, the image
// looks cleaner, but less interesting, I feel.
#define HOLES

// Raising the faces of the pylon tops. I find it can help bounce the light 
// off the surface in a more reflective way.
//#define RAISED

// Putting a ridge decoraction on the pylon tops.        
//#define RIDGES


// The hexagons must use offset rows.
#ifdef HEXAGON
#ifndef ROW_OFFSET
#define ROW_OFFSET
#endif
#endif

// Max ray distance.
#define FAR 20.



// Scene object ID to separate the mesh object from the terrain.
float objID;


// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// IQ's vec2 to float hash.
float hash21(vec2 p){  return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453); }


// Height map value.
float hm(in vec2 p){ 


    // Render the swirl by adding polar coordinates. You can apply the logarithm to
    // the length for a different kind of feel, but I'll leave it in its current form.
    float swirl = fract(atan(p.y, p.x)/6.2831 + length(p)*1.25 - iTime/4.);
    // Alternative octagon swirl -- Other metrics are also possible.
    /*
    vec2 ap = abs(p);
    float oct =  max(max(ap.x, ap.y), (ap.x + ap.y)*.7071);
    float swirl = fract(atan(p.y, p.x)/6.2831 + oct*1.5 - iTime/4.);
    */

    //swirl = abs(swirl - .5)*2.; swirl = smoothstep(.15, .85, swirl);
    swirl = sin(swirl*6.2831)*.5 + .5;


    // In the form above, the height transitions between zero and one.
    // The following simply manipulates the way in which that happens.

    // Number of quantization levels.
    #define lNum 4.
    float iswirl = floor(swirl*lNum*.9999)/lNum;

    // Arrange for the pylons to smoothly transition between quantization
    // levels whilst honoring the pixelated look... I could have described
    // that better, but hopefully, you know what I mean. :D
    return mix(iswirl, iswirl + 1./lNum, smoothstep(0., 1., swirl));
    // Popping unnaturally from one state to the next.
    //return iswirl*lNum/(lNum - 1.); 
    // Continuous, smooth motion.
    //return swirl; 


}


// IQ's extrusion formula.
float opExtrusion(in float sdf, in float pz, in float h){

    vec2 w = vec2( sdf, abs(pz) - h );
  	return min(max(w.x, w.y), 0.) + length(max(w, 0.));

    /*
    // Slight rounding. A little nicer, but slower.
    const float sf = .015;
    vec2 w = vec2( sdf, abs(pz) - h - sf/2.);
  	return min(max(w.x, w.y), 0.) + length(max(w + sf, 0.)) - sf;
    */
}

#ifdef HEXAGON
// Signed distance to a regular hexagon, with a hacky smoothing variable thrown
// in. -- It's based off of IQ's more exact pentagon method.
float sHexS(in vec2 p, float r, in float sf){


      const vec3 k = vec3(-.8660254, .5, .57735); // pi/6: cos, sin, tan.

      // X and Y reflection.  
      p = abs(p); 
      p -= 2.*min(dot(k.xy, p), 0.)*k.xy;

      r -= sf;
      // Polygon side.
      return length(p - vec2(clamp(p.x, -k.z*r, k.z*r), r))*sign(p.y - r) - sf;

}
#endif

/*
// IQ's unsigned box formula.
float sBoxS(in vec2 p, in vec2 b, in float sf){

  return length(max(abs(p) - b + sf, 0.)) - sf;
}
*/

// IQ's signed box formula.
float sBoxS(in vec2 p, in vec2 b, in float sf){

  vec2 d = abs(p) - b + sf;
  return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - sf;
}


// A regular extruded block grid.
//
// The idea is very simple: Produce a normal grid full of packed square pylons.
// That is, use the grid cell's center pixel to obtain a height value (read in
// from a height map), then render a pylon at that height.

vec4 blocks(vec3 q3){



    // Brick dimension: Length to height ratio with additional scaling.
    vec2 l = scale;
	vec2 s = scale*2.;
    #ifdef HEXAGON
    vec2 hSc = vec2(1, scale.y/scale.x*2./1.732);
    #endif

    float minSc = min(scale.x, scale.y);

    // Distance.
    float d = 1e5;
    // Cell center, local coordinates and overall cell ID.
    vec2 p, ip;

    // Individual brick ID.
    vec2 id = vec2(0); 

    // Four block corner postions.
    #ifdef ROW_OFFSET
    // Offset rows.
    vec2[4] ps4 = vec2[4](vec2(-.25, .25), vec2(.25), vec2(.5, -.25), vec2(0, -.25)); 
    #else
    vec2[4] ps4 = vec2[4](vec2(-.25, .25), vec2(.25), vec2(.25, -.25), vec2(-.25)); 
    #endif

    float data = 0.; // Extra data.

    for(int i = min(0, iFrame); i<4; i++){


        // Local coordinates.
        p = q3.xy;
        ip = floor(p/s - ps4[i]) + .5; // Local tile ID.

        // Correct positional individual tile ID.
        vec2 idi = (ip + ps4[i])*s;

        p -= idi; // New local position.


        // The extruded block height. See the height map function, above.
        float h = hm(idi);

        h *= .1;


        #ifdef HEXAGON
        // Hexagon option: Multiply scale by "vec2(1, 1.732/2.),"
        // and have the ROW_OFFSET define turned on.
        float di2D = sHexS(p.yx, minSc/1.732 - .008, .02);
        #else
        float di2D = sBoxS(p, l/2. - .008, .02);        
        //float di2D = length(p) - l.x/2. + .004;
        #endif


        #ifdef HOLES
        // Boring out some of the boxes.
        if((i&1)==0) di2D = max(di2D, -(di2D + minSc/4.));
        #endif

        // The extruded distance function value.
        float di = opExtrusion(di2D, (q3.z + h - .5), h + .5);


        #ifdef RAISED
        // Raised tops.
        di += di2D*.25;
        #endif

        #ifdef RIDGES
        // Putting ridges on the faces.
        di += sin(di2D/minSc*6.2831*3.)*.005;
        #endif

        /*
        // Lego.
        float cap = length(p) - scale.x/4.;
        cap = opExtrusion(cap, (q3.z + h - .5 + .035), h + .5);
        di = min(di, cap); //di = max(di, -cap)
        */



        // If applicable, update the overall minimum distance value,
        // ID, and box ID. 
        if(di<d){
            d = di;
            id = idi;
            // Extra data. In this case, the 2D distance field.
            data = di2D;
        }

    }

    // Return the distance, position-base ID and box ID.
    return vec4(d, id, data);
}


// Block ID -- It's a bit lazy putting it here, but it works. :)
vec4 gID;

// The extruded image.
float map(vec3 p){

    // Floor.
    float fl = -p.z + .1;

    // The extruded blocks.
    vec4 d4 = blocks(p);
    gID = d4; // Individual block ID.


    // Overall object ID.
    objID = fl<d4.x? 1. : 0.;

    // Combining the floor with the extruded object.
    return  min(fl, d4.x);

}


// Basic raymarcher.
float trace(in vec3 ro, in vec3 rd){

    // Overall ray distance and scene distance.
    float t = 0., d;

    for(int i = min(0, iFrame); i<96; i++){

        d = map(ro + rd*t);
        // Note the "t*b + a" addition. Basically, we're putting less emphasis on accuracy, as
        // "t" increases. It's a cheap trick that works in most situations... Not all, though.
        if(abs(d)<.001 || t>FAR) break; // Alternative: 0.001*max(t*.25, 1.), etc.

        t += d*.7; 
    }

    return min(t, FAR);
}


// Standard normal function. It's not as fast as the tetrahedral calculation, but more symmetrical.
vec3 getNormal(in vec3 p, float t) {
	const vec2 e = vec2(.001, 0);
	return normalize(vec3(map(p + e.xyy) - map(p - e.xyy), map(p + e.yxy) - map(p - e.yxy),	
                          map(p + e.yyx) - map(p - e.yyx)));
}


// Cheap shadows are hard. In fact, I'd almost say, shadowing particular scenes with limited 
// iterations is impossible... However, I'd be very grateful if someone could prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, vec3 n, float k){

    // More would be nicer. More is always nicer, but not always affordable. :)
    const int maxIterationsShad = 24; 

    ro += n*.0015; // Coincides with the hit condition in the "trace" function.  
    vec3 rd = lp - ro; // Unnormalized direction ray.

    float shade = 1.;
    float t = 0.; 
    float end = max(length(rd), .0001);
    //float stepDist = end/float(maxIterationsShad);
    rd /= end;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, 
    // the lowest number to give a decent shadow is the best one to choose. 
    for (int i = min(iFrame, 0); i<maxIterationsShad; i++){

        float d = map(ro + rd*t);
        shade = min(shade, k*d/t);
        //shade = min(shade, smoothstep(0., 1., k*h/dist)); // Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += clamp(d, .01, stepDist), etc.
        t += clamp(d, .01, .25); 


        // Early exits from accumulative distance function calls tend to be a good thing.
        if (d<0. || t>end) break; 
    }

    // Sometimes, I'll add a constant to the final shade value, which lightens the shadow a bit --
    // It's a preference thing. Really dark shadows look too brutal to me. Sometimes, I'll add 
    // AO also just for kicks. :)
    return max(shade, 0.); 
}


// I keep a collection of occlusion routines... OK, that sounded really nerdy. :)
// Anyway, I like this one. I'm assuming it's based on IQ's original.
float calcAO(in vec3 p, in vec3 n){

	float sca = 3., occ = 0.;
    for( int i = 0; i<5; i++ ){

        float hr = float(i + 1)*.15/5.;        
        float d = map(p + n*hr);
        occ += (hr - d)*sca;
        sca *= .7;
    }

    return clamp(1. - occ, 0., 1.);  
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    // Screen coordinates.
	vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    #ifdef HEXAGON
    scale *= vec2(2./1.732, 1);
	#endif

	// Camera Setup.
    // Tilted camera, just to prove it's 3D. :)
	//vec3 ro = vec3(0, -1.3, -2.2); // Camera position, doubling as the ray origin.
	//vec3 lk = ro + vec3(0, .12, .25);//vec3(0, -.25, iTime);  // "Look At" position.
	// Front on camera.
    vec3 ro = vec3(0, 0, -2.2); // Camera position, doubling as the ray origin.
	vec3 lk = ro + vec3(0, 0, .25);//vec3(0, -.25, iTime);  // "Look At" position.

    // Light positioning. One is just in front of the camera, and the other is in front of that.
 	vec3 lp = ro + vec3(-.25, .5, 1);// Put it a bit in front of the camera.


    // Using the above to produce the unit ray-direction vector.
    float FOV = 1.333; // FOV - Field of view.
    vec3 fwd = normalize(lk-ro);
    vec3 rgt = normalize(vec3(fwd.z, 0., -fwd.x )); 
    // "right" and "forward" are perpendicular, due to the dot product being zero. Therefore, I'm 
    // assuming no normalization is necessary? The only reason I ask is that lots of people do 
    // normalize, so perhaps I'm overlooking something?
    vec3 up = cross(fwd, rgt); 

    // rd - Ray direction.
    //vec3 rd = normalize(fwd + FOV*uv.x*rgt + FOV*uv.y*up);
    vec3 rd = normalize(uv.x*rgt + uv.y*up + fwd/FOV);

    // Swiveling the camera about the XY-plane.
	rd.xy *= rot2( sin(iTime)/32. );



    // Raymarch to the scene.
    float t = trace(ro, rd);

    // Save the block field value, block ID and 2D data field value.
    vec4 svGID = gID;

    float svObjID = objID;


    // Initiate the scene color to black.
	vec3 col = vec3(0);

	// The ray has effectively hit the surface, so light it up.
	if(t < FAR){


    	// Surface position and surface normal.
	    vec3 sp = ro + rd*t;
        vec3 sn = getNormal(sp, t);

            	// Light direction vector.
	    vec3 ld = lp - sp;

        // Distance from respective light to the surface point.
	    float lDist = max(length(ld), .001);

    	// Normalize the light direction vector.
	    ld /= lDist;



        // Shadows and ambient self shadowing.
    	float sh = softShadow(sp, lp, sn, 16.);
    	float ao = calcAO(sp, sn); // Ambient occlusion.
        //sh = min(sh + ao*.25, 1.);

	    // Light attenuation, based on the distances above.
	    float atten = 1./(1. + lDist*.05);


    	// Diffuse lighting.
	    float diff = max( dot(sn, ld), 0.);
        //diff = pow(diff, 4.)*2.; // Ramping up the diffuse.

    	// Specular lighting.
	    float spec = pow(max(dot(reflect(ld, sn), rd ), 0.), 32.); 

	    // Fresnel term. Good for giving a surface a bit of a reflective glow.
        float fre = pow(clamp(1. - abs(dot(sn, rd))*.5, 0., 1.), 2.);

		// Schlick approximation. I use it to tone down the specular term. It's pretty subtle,
        // so could almost be aproximated by a constant, but I prefer it. Here, it's being
        // used to give a hard clay consistency... It "kind of" works.
		float Schlick = pow( 1. - max(dot(rd, normalize(rd + ld)), 0.), 5.);
		float freS = mix(.15, 1., Schlick);  //F0 = .2 - Glass... or close enough. 


        // Obtaining the texel color. 
	    vec3 texCol = vec3(.6);   

        // The extruded grid.
        if(svObjID<.5){

            float h = hm(svGID.yz);

            //texCol = mix(vec3(.05), vec3(1), h); // White.
            texCol = mix(vec3(.05), vec3(1.4, .2, .6), h); // Pink.
            //texCol = mix(vec3(.05), vec3(.5, 1, .25), h); // Green.
            //texCol = mix(vec3(.05), vec3(.25, .7, 1.5), h); // Blue.
            //texCol = mix(vec3(.05), mix(vec3(1.4, .2, .6), vec3(2, .3, .3), diff*diff*1.5), h);

            // Extra blending options.
            //texCol = mix(texCol, texCol.xzy, h);
            //texCol = mix(texCol, texCol.zyx, clamp((ld.y - ld.x) - .5, 0., 1.));
            //texCol = mix(texCol.zyx, texCol, diff*diff*1.5);


            /*
            // Dark edges.
            //vec2 lCoord = sp.xy - svGID.yz;
            vec2 sc = scale;
            float lw = .006;
            float dS = abs(svGID.w) - lw; // 2D face field value.
            //texCol = mix(texCol, min(texCol.xzy*1.35 + .1, 1.), (1. - smoothstep(0., .005, dS + lw)));
            dS = max(dS, abs(sp.z + h*.1*2.) - lw/2.); // Just the rim.
            texCol = mix(texCol, vec3(0), (1. - smoothstep(0., .005, dS))*.8);
            */

        }
        else {

            // The dark floor in the background. Hidden behind the pylons, but
            // you still need it.
            texCol = vec3(0);
        }



        // Combining the above terms to produce the final color.
        col = texCol*(diff*sh + .3 + vec3(.25, .5, 1)*fre*0. + vec3(1, .97, .92)*spec*freS*2.*sh);

        // Shading.
        col *= ao*atten;



	}


    // Rought gamma correction.
	fragColor = vec4(sqrt(max(col, 0.)), 1);

}

const float PI = 3.14159;

float sdCircle(vec2 st, float r) { 
return length(st) - r; 
} 

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float stroke(float x, float w){ 
    w *= .5; 
    return 1.-smoothstep(w-.01,w+.01,abs(x)); 
} 

float fill(float sdf){
    return 1.-smoothstep(-.01,.01,sdf);
}

mat2 rotate2d(float angle){ 
    mat2 rot = mat2(cos(angle),-sin(angle), sin(angle),cos(angle)); 
    return rot; 
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(.4);
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv = (uv-vec2(1.,.5)) * rotate2d(iTime * .25);
    uv = fract(uv);
    float zoomMod = 6.;
    uv *= zoomMod;

    float timeIndex = mod(iTime * 1.5, zoomMod);
    for (int i = 0; i < 6; i++){
        float j = float(i);
        float rowIndex = step(j,uv.y) * 1.-step(j+1.,uv.y);
        float colIndex = step(j,uv.x) * 1.-step(j+1.,uv.x);

        if (floor(timeIndex) < 3.) {
            if (j == floor(timeIndex)) {
                uv.x += rowIndex * -mod(timeIndex,1.);
            }
            if (mod(j+2.,zoomMod) == floor(timeIndex)) {
                uv.x += rowIndex * mod(timeIndex,1.);
            }
        } else {
            if (j == floor(timeIndex)) {
                uv.y += colIndex * -mod(timeIndex,1.);
            }
            if (mod(j+2.,zoomMod) == floor(timeIndex)) {
                uv.y += colIndex * mod(timeIndex,1.);
            }
        }
    }

    vec2 st = (fract(uv)-.5)*2.;

    float turnMod = PI * .5;
    for (int i = 0; i < 6; i++){
        float j = float(i);
        float rowIndex = step(j,uv.y) * 1.-step(j+1.,uv.y);
        float colIndex = step(j,uv.x) * 1.-step(j+1.,uv.x);

        if (floor(timeIndex) < 3.) {
            if (j == floor(timeIndex)) {
                st = st * rotate2d(mod(timeIndex,1.) * turnMod * rowIndex);
            }
            if (mod(j+2.,zoomMod) == floor(timeIndex)) {
                st = st * rotate2d(-mod(timeIndex,1.) * turnMod * rowIndex);
            }
        } else {
            if (j == floor(timeIndex)) {
                st = st * rotate2d(mod(timeIndex,1.) * turnMod * colIndex);
            }
            if (mod(j+2.,zoomMod) == floor(timeIndex)) {
                st = st * rotate2d(-mod(timeIndex,1.) * turnMod * colIndex);
            }
        }
    }

    float ring = abs(sdCircle(st, .4))-.12;
    for(int i = 0; i < 8; i++){
        float j = float(i);
        st = st * rotate2d(PI*.25);
        float square = sdBox(st-vec2(0.,.48),vec2(.07,.1))-.1;
        ring = min(ring, square);
    }

    col += fill(ring);
    col = mix(col,vec3(.05),stroke(ring,.1));
    fragColor = vec4(col,1.0);
}

float hash(float n) { return fract(sin(n) * 1e4); }
float hash(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

float noise(float x) {
	float i = floor(x);
	float f = fract(x);
	float u = f * f * (3.0 - 2.0 * f);
	return mix(hash(i), hash(i + 1.0), u);
}

float noise(vec2 x) {
	vec2 i = floor(x);
	vec2 f = fract(x);

	float a = hash(i);
	float b = hash(i + vec2(1.0, 0.0));
	float c = hash(i + vec2(0.0, 1.0));
	float d = hash(i + vec2(1.0, 1.0));

	vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float time = iTime / 2.;

    uv.x += uv.y + sin(time + cos(time+uv.x));
    uv.y += uv.x + cos(time + sin(time+uv.x));

    float f = .5*noise(10.*uv);

    vec3 col = mix(vec3(.9,.0,0.), vec3(0.0,.0,.0), f);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define RotateX(v,a) v.yz *= mat2(cos(a),sin(a),-sin(a),cos(a))
#define RotateY(v,a) v.xz *= mat2(cos(a),sin(a),-sin(a),cos(a))
#define RotateZ(v,a) v.xy *= mat2(cos(a),sin(a),-sin(a),cos(a))

#define MIN_MARCH_DIST 0.0001
#define MAX_MARCH_STEPS 500
#define MAX_VOLUME_STEPS 5000
#define VOLUME_STEP_SIZE 0.01

// Simplex 2D noise
//
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}


float saturate (float x)
{
    return min(1.0, max(0.0,x));
}
vec3 saturate (vec3 x)
{
    return min(vec3(1.,1.,1.), max(vec3(0.,0.,0.),x));
}

// --- Spectral Zucconi --------------------------------------------
// By Alan Zucconi
// Based on GPU Gems: https://developer.nvidia.com/sites/all/modules/custom/gpugems/books/GPUGems/gpugems_ch08.html
// But with values optimised to match as close as possible the visible spectrum
// Fits this: https://commons.wikimedia.org/wiki/File:Linear_visible_spectrum.svg
// With weighter MSE (RGB weights: 0.3, 0.59, 0.11)
vec3 bump3y (vec3 x, vec3 yoffset)
{
	vec3 y = vec3(1.,1.,1.) - x * x;
	y = saturate(y-yoffset);
	return y;
}
vec3 spectral_zucconi (float w)
{
    // w: [400, 700]
	// x: [0,   1]
	float x = saturate((w - 400.0)/ 300.0);
	const vec3 cs = vec3(3.54541723, 2.86670055, 2.29421995);
	const vec3 xs = vec3(0.69548916, 0.49416934, 0.28269708);
	const vec3 ys = vec3(0.02320775, 0.15936245, 0.53520021);

	return bump3y (	cs * (x - xs), ys);
}

vec3 spectral_zucconi6 (float w)
{
	// w: [400, 700]
	// x: [0,   1]
	float x = saturate((w - 400.0)/ 300.0);

	const vec3 c1 = vec3(3.54585104, 2.93225262, 2.41593945);
	const vec3 x1 = vec3(0.69549072, 0.49228336, 0.27699880);
	const vec3 y1 = vec3(0.02312639, 0.15225084, 0.52607955);

	const vec3 c2 = vec3(3.90307140, 3.21182957, 3.96587128);
	const vec3 x2 = vec3(0.11748627, 0.86755042, 0.66077860);
	const vec3 y2 = vec3(0.84897130, 0.88445281, 0.73949448);

	return
		bump3y(c1 * (x - x1), y1) +
		bump3y(c2 * (x - x2), y2) ;
}

vec4 Volume(vec3 pos)
{
    float rand = 80.;

    //float vol = dot(normalize(pos),vec3(1,1,1));
    float vol = normalize(pos.x);
    vec3 col = mix(vec3(1.0,0.0,0.0),vec3(0.0,0.0,1.0),step(0.0,vol));

    vol = smoothstep(0.6,1.0,abs(vol));


	//return vec4(snoise(vec2(1.0,pos.x)));
    //float noise = (1.0/(1.0+exp(-2.*snoise(vec2(pos.x/6.-rand)))) - 0.5 ) * 2.0;
    //noise -=  (1.0/(1.0+exp(-2.*snoise(vec2(pos.x/5.-rand)))) - 0.5 ) * 2.0;
    //noise *=7.;
    //noise = clamp(noise,0.0,1.0);
    return vec4(spectral_zucconi((pos.x/3.+1.0)/2.0*200.0+450.0),clamp(0.12-length(vec2(pos.y,pos.z)/8.),0.0,1.0));
    //return vec4(1.0,1.0,1.0,0.0002);
}

vec3 MarchVolume(vec3 orig, vec3 dir)
{
    //Ray march to find the cube surface.
    float t = 0.0;
    vec3 pos = orig;
    for(int i = 0;i < MAX_MARCH_STEPS;i++)
    {
        pos = orig + dir * t;
        float dist = 100.0;

        dist = min(dist, 12.0-length(pos));
        dist = min(dist, max(max(abs(pos.x/5.),abs(pos.y)),abs(pos.z))-1.0);//length(pos)-1.0);

        t += dist;

        if(dist < MIN_MARCH_DIST){break;}
    }

    //Step though the volume and add up the opacity.
    vec4 col = vec4(0.0);
    for(int i = 0;i < MAX_VOLUME_STEPS;i++)
    {
    	t += VOLUME_STEP_SIZE;

    	pos = orig + dir * t;

        //Stop if the sample becomes completely opaque or leaves the volume.
        if(max(max(abs(pos.x/5.),abs(pos.y)),abs(pos.z))-1.0 > 0.0) {break;}

        vec4 vol = Volume(pos);
        vol.rgb *= vol.w;

        col += vol;
        //col = clamp(col,0.0,1.0);
    }

    col.rgb = (1.0/(1.0+exp(-0.3*col.rgb)) - 0.5 ) * 2.0;

    return col.rgb;///2.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.y;

    vec3 dir = normalize(vec3(uv-res/2.0,1.0));
    vec3 orig = vec3(0,0,-9.5);


    RotateX(dir,radians(iMouse.y));
    RotateX(orig,radians(iMouse.y));
    RotateY(dir,radians(-iMouse.x));
    RotateY(orig,radians(-iMouse.x));


    vec3 color = MarchVolume(orig,dir);
    vec2 seed = fragCoord.xy;
    float x = (seed.x / 3.14159 + 4.) * (seed.y / 13. + 4.) * ((fract(iTime) + 1.) * 10.);
    float grain = mod((mod(x, 13.) + 1.) * (mod(x, 123.) + 1.), 0.01) - 0.005;


	fragColor = grain*20. + vec4(color, 1.0);
}

// 2D version of https://shadertoy.com/view/ssGSDh
// operate on a Worley grid on surface instead of Worley grid in volume.

// WIP. Many discontinuities to fix.   
// See options and coefs.

#define H(p)       fract(sin((p)*mat2(246.1, 113.5, 271.9, 124.6 ))*43758.5453123)
#define R(p,a)   (p)*mat2( cos(a),-sin(a),sin(a),cos(a) )
#define hue(v)   ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;

    O-=O;
    float r = length(U), y,w=0., l=9., s = 4.;      // s: swirls size
    int i,k;
    if ( r > 1. ) return;

    vec3 p = vec3( U, sqrt(1.-r*r) );          // raytracing point in sphere
    vec2 P0 = vec2( atan(p.z,p.x) - .1*iTime, atan(p.y,length(p.xz)) ),
     // TODO: compress lattitudes near pole to get conform cells 
         P = s*P0, I,F, H,D;
     //  F = abs(fract(P+.5)-.5); y = min(F.x,F.y); O += smoothstep(12./R.y,0.,y); // debug: grid
         I = floor(P), F = fract(P);           // coords in 2D grid
    y = P0.y;                                  // latitude ( to tune swirl direction & amplitude )
    y = 4.*cos(4.*y); 
 // if (mod(I.x+I.y,2.)>0.) y=-y;              // if checkered rotation direction
    P-=P;
#define dist2seed  \
        D = vec2( k%3, k/3 ) -1.;              /* cell offset         */    \
        D += H(I+D)-.5;                        /* random seed point   */    \
        r = length(F-D);                       /* distance to seed    */

    for ( k = 0; k < 9; k++) {                 // visit neighbor cells to find closest seed point
        dist2seed;
     // y = -y;                                // checkered rotation direction
        F  =   R( F-D, y*smoothstep(.5,0.,r) ) + D; P = F+I;
     // P  =   R( F-D, y*smoothstep(.5,0.,r) ) + D+I;  I = floor(P),F = fract(P);
     // P += ( R( F-D, y*smoothstep(.5,0.,r) ) + D+I )/r; w += 1./r;
        r < l ? l = r, i = k : i;              // keep closest dot 
     }
    // P /= w;
/** k = i;
    dist2seed;
 // if ( i%2 < 1 ) y = -y;                                        // checkered rotation direction
    P = R( F-D, y * smoothstep(.5,0.,r) ) + D+I;                  // rotate coords around seed point
/**/       
 // O += vec4(smoothstep(12./R.y,0., l-.2) ); return;             // test: plot seed points

    U = P/s; U.x -= .1*iTime;                                     // surface coordinates
    O += ( iMouse.z > 0. ? vec4(.6) 
             : smoothstep(.8,.2, texture(iChannel0, U*vec2(1,3))))// swirled texture
        * (.5+.5*hue(abs(U.y*2./3.14)))                           // color
        * ( .3 + 1.2* max(0., dot(p,vec3(1,0,1))));               // shading
}

const int AMOUNT = 2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = 20.0 * (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float len;

    for(int i = 0; i < AMOUNT; i++){
        len = length(vec2(coord.x, coord.y));

        coord.x *= coord.x - cos(coord.y + sin(len)) + cos(iTime / 9.0);
        coord.y = coord.y + sin(coord.x + cos(len)) + sin(iTime / 12.0);
    }

    fragColor = vec4(cos(len * 1.1), cos(len * 1.2), cos(len * 1.4), 1.0);
}

const float min_thickness = 0.02;
const float max_thickness = 0.05;
const float freq = 30.0;
const float ampl = 0.1;

float speed = 1.0;
float thickness = 0.02;
float invert = 1.0;

vec3 mask(float min, float max, vec2 uv, vec3 col, vec3 background) {
    const float tollerance = 0.000001;

    float maxStep = smoothstep(max, max + tollerance, uv.y);
    float minStep = smoothstep(min + tollerance, min, uv.y);
    col = mix(col, background, maxStep);
    col = mix(col, background, minStep);

    return col;
}

float sine(float angle, vec2 uv) {
    return sin((angle + speed + uv.x) * freq) * invert * ampl + 0.5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));
    vec3 background = vec3(0);

    speed = sin(iTime) + 1.0;
    thickness = (cos(iTime) / 2.0 + 0.5) * (max_thickness - min_thickness) + min_thickness;
    invert = cos(iTime);

    float wave = sine(iTime, uv) - thickness / 2.0;
    float wave2 = sine(iTime, uv) + thickness / 2.0;

    background = mix(vec3(0), col, -cos(iTime) / 2.0 + 0.5);
    col = mix(col, vec3(0), -cos(iTime) / 2.0 + 0.5);

    col = mask(wave, wave2, uv, col, background);
    fragColor = vec4(col, 1.0);
}

const int AMOUNT = 12;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = 20.0 * (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    float len;

    for(int i = 0; i < AMOUNT; i++){
        len = length(vec2(coord.x, coord.y))*2.0;

        coord.x = coord.x - cos(coord.y + sin(len)) + cos(iTime / 12.0);
        coord.y = coord.y + sin(coord.x + cos(len)) + sin(iTime / 9.0);
    }

    fragColor = vec4(cos(len * 1.1), cos(len * 1.05), cos(len * 1.01), 1.0);
}

const uint antiA = 3u; //raise or lower this

const mat4x3 bgPal = mat4x3(0.5,0.5,0.5,0.5,0.5,0.5,1.0,1.0,1.0,0.0,0.33,0.67);
const mat4x3 sfPal = mat4x3(0.5,0.5,0.5,0.5,0.5,0.5,1.0,1.0,1.0,0.0,0.10,0.20);

const float err = 1e10;
mat3  fA, cA;
vec3  fB, cB;
float fC, cC;

vec3 bgCol;
vec3 sfCol;


vec3 hash3(uint n) 
{
    //https://www.shadertoy.com/view/llGSzw
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uvec3 k = n * uvec3(n,n*16807U,n*48271U);
    return vec3( k & uvec3(0x7fffffffU))/float(0x7fffffff);
}

mat2x3 boxM(uint n) {
    vec3 U = hash3(n), V = hash3(n + 2568758767u);
    U = sqrt(-2.*log(U));
    V *= 2.*3.14159265;
    return mat2x3(U*cos(V), U*sin(V));
}

vec3 pal(float t, mat4x3 a) {
    //https://www.shadertoy.com/view/ll2GD3
    return a[0] + a[1]*cos(2.*3.14159265*(a[2]*t+a[3]));
}

void set(uint n) {
    n *= 100u;
    for (uint i = 0u; i < 3u; ++i) {
        mat2x3 tmp = boxM(n++);
        fA[i] = tmp[0];
        cA[i] = tmp[1];
        fA[i][i] /= sqrt(2.);
        cA[i][i] /= sqrt(2.);
    }

    fB = 2.*(hash3(n++) - 1.);
    cB = 2.*(hash3(n++) - 1.);
    fA *= .3;

    cB *= .3;
    cA *= .2;
    cC = 0.;
    fC = 0.;

    bgCol = pow(pal(hash3(n++).x, bgPal), vec3(.35));
    sfCol = pow(pal(hash3(n++).x, sfPal), vec3(1.));

}

float eval(vec3 x, mat3 A, vec3 B, float C) {
    return dot(x, A*x) + dot(B, x) + C;
}

vec3 grad(vec3 x, mat3 A, vec3 B) {
    return B + A*x + x*A;
}

vec3 param(vec3 x, vec3 d, mat3 A, vec3 B, float C) {
    return vec3(eval(x,A,B,C), dot(grad(x,A,B), d), dot(d, A*d));
}

float func(vec3 x) {
    return eval(x, fA, fB, fC);
}

float cond(vec3 x) { 
    return eval(x, cA, cB, cC);
}

vec3 funcGrad(vec3 x) {
    return grad(x, fA, fB);
}

vec3 condGrad(vec3 x) {
   return grad(x, cA, cB);
}

vec2 solve(vec3 p) {
    float a = p.z, b = p.y, c = p.x;
    float disc = b*b - 4.*a*c;

    if (disc < 0.) return vec2(err);

    vec2 tmp;

    if (false && abs(a) < 1e-6 )
        tmp = vec2(-c/b, err);
    else if (false && abs(c) < 1e-6)
        tmp = vec2(0., -a/b);
    else {
        tmp.x = (-b - sign(b)*sqrt(disc))/2./a;
        tmp.y = c/a/tmp.x;
    }
    if (tmp.y < tmp.x) tmp = tmp.yx;
    if (tmp.x < 0.)    tmp = vec2(tmp.y, err);
    if (tmp.x < 0.)    tmp = vec2(err);
    return tmp;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    const float duration = 3., speed = 1.;
    set(uint(iTime/duration) + 0u);
    fC -= iTime * speed;

    const float delta = 1.5, focal = 1.3;

    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    float pixel = 1./iResolution.y;

    vec3 ro = vec3(0., 0., 4.);

    vec3 colTot = vec3(0.);

    for (uint nAA = 0u; nAA < antiA*antiA; ++nAA) {
        const float aaStep = 1./float(antiA);
        vec2 uv1 = vec2(nAA/antiA, nAA%antiA)*aaStep + .5*aaStep;
        uv1 = (uv1 - .5) * 2.*pixel;
        vec3 rd = normalize(vec3(uv+uv1, -focal));

        vec3 fPar = param(ro, rd, fA, fB, fC);
        vec3 cPar = param(ro, rd, cA, cB, cC);
        vec2 cIsect = solve(cPar);

        vec3 col;

        if (cIsect.x < err) {
            float hit = func(ro+rd*cIsect.x);
            vec2 pot = delta * (floor(hit / delta) + vec2(0., 1.));
            vec4 fIsect = vec4(solve(fPar-vec3(pot.x,0.,0.)), solve(fPar-vec3(pot.y,0.,0.)));

            float t = err;
            for (int i = 0; i < 4; ++i) {
                if (fIsect[i] < min(t, cIsect.y) && fIsect[i] > cIsect.x)
                    t = fIsect[i];
            }

            vec3 pos = ro + t*rd;
            vec3 fGrad = funcGrad(pos)/delta;
            vec3 cGrad = condGrad(pos)/abs(cond(pos));

            float occ = length(fGrad) / length(cGrad);
            occ = sqrt(occ);
            occ = 1. - occ/sqrt(1. + occ*occ);
            occ = sqrt(occ);

            col = t < err ? sfCol * occ : bgCol;
            col = mix(col, sfCol, smoothstep(-2.*pixel, -pixel, -1./cIsect.x));
        }
        else
            col = bgCol;
        colTot += col;
    }
    colTot /= float(antiA*antiA);

    colTot = pow(colTot, vec3(1./2.2));
    fragColor = vec4(colTot,1.0);
}

const float PI = 3.14159;

float sdBox( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float stroke(float x, float w) {
    float d = smoothstep(-.01,0.,x+w*.5) - smoothstep(0.,.01,x-w*.5);
    return clamp(d,0.,1.);
}

float trim(vec2 st, float num) {
    float m = (mod(num,2.) > 0.) ? 1. : -1.;
    return smoothstep(0.,.01,cos(4.*atan(st.y,st.x)+m*num*(iTime*.7)));
}

vec2 rotate(float angle, vec2 st, vec2 pos){
    mat2 rot = mat2(cos(angle),-sin(angle), sin(angle),cos(angle));
    st -= pos;
    return rot * st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    vec2 st = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    for (int i = 0; i < 18; i++){
        float f = float(i);
        float s = stroke(sdBox(rotate(PI*.25,st,vec2(0.)),vec2(.1)*f),.025) * trim(st,f);
        col += s;
    }

    col = clamp(col,vec3(.15),vec3(.95));

    fragColor = vec4(col,1.0);
}

const float pi = 3.1415926535;
float randf(int counter) // -> [0, 1]
{
    return fract(sin(float(counter)) * 43.7585453);
}
vec2 randf2(int counter)
{
    float phi = randf(counter) * 2. * pi;
    return vec2(cos(phi), sin(phi));
}
vec3 randf3(int counter)
{
    float phi = randf(counter) * 2. * pi;
    float cos_theta = 2. * randf(counter * 2) - 1.;
    float sin_theta = sqrt(1. - cos_theta * cos_theta);
    return vec3(cos(phi) * sin_theta, sin(phi) * sin_theta, cos_theta);
}

float g(float t)
{
    return t * t * (3. - 2. * t);
}

float squareSize;
float perlinNoise(vec3 P) // -> [0, 1]
{
    vec3 p = P / squareSize;
    ivec3 i = ivec3(floor(p));
    vec3 r = fract(p);

    const int iL = 10;
    const int iS = iL * iL;
    int i00 = i.x + iL * i.y + iS * i.z;

    float f000 = dot(r - vec3(0., 0., 0.), randf3(i00));
    float f001 = dot(r - vec3(1., 0., 0.), randf3(i00 + 1));
    float f010 = dot(r - vec3(0., 1., 0.), randf3(i00 + iL));
    float f011 = dot(r - vec3(1., 1., 0.), randf3(i00 + iL + 1));
    float f100 = dot(r - vec3(0., 0., 1.), randf3(i00 + iS));
    float f101 = dot(r - vec3(1., 0., 1.), randf3(i00 + iS + 1));
    float f110 = dot(r - vec3(0., 1., 1.), randf3(i00 + iS + iL));
    float f111 = dot(r - vec3(1., 1., 1.), randf3(i00 + iS + iL + 1));
    float f00 = f000 + (f001 - f000) * g(r.x);
    float f01 = f010 + (f011 - f010) * g(r.x);
    float f10 = f100 + (f101 - f100) * g(r.x);
    float f11 = f110 + (f111 - f110) * g(r.x);
    float f0 = f00 + (f01 - f00) * g(r.y);
    float f1 = f10 + (f11 - f10) * g(r.y);
    return f0 + (f1 - f0) * g(r.z) + 0.5;
}

float bell(float x, float s)
{
    return exp2(-x * x * s);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    squareSize = min(iResolution.x, iResolution.y) * 0.3;
    float t = iTime * squareSize;
    vec3 p = vec3(gl_FragCoord.xy, 0.02 * t);
    float f = perlinNoise(p);

    vec2 dF = vec2(dFdx(f), dFdy(f));
    const float lineWidth = 0.6;
    float s = 0.72134752 / (dot(dF, dF) * lineWidth * lineWidth);

    float c = 0.;
    c += 0.2 * bell(f - 0.1, s);
    c += 0.4 * bell(f - 0.2, s);
    c += 0.5 * bell(f - 0.3, s);
    c += 0.6 * bell(f - 0.4, s);
    c += 0.8 * bell(f - 0.5, s);
    c += 0.6 * bell(f - 0.6, s);
    c += 0.5 * bell(f - 0.7, s);
    c += 0.4 * bell(f - 0.8, s);
    c += 0.2 * bell(f - 0.9, s);

    p.z = t * 0.2 + 100.;
    float I = (perlinNoise(p) + 0.3) * 0.7;
    c *= (I * I) * (I * I);

    fragColor = vec4(vec3(0.0, 0.0, 0.0) + vec3(c), 1.);
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 5.0;

    vec2 p1 = vec2(sin(iTime*0.6942),cos(iTime*0.4269));
    vec2 p2 = vec2(cos(iTime*0.888+0.8989),sin(iTime*0.666+69.69));
    vec2 p3 = vec2(cos(iTime*1.069428+69.8989),sin(iTime*0.96942+55.88));


    float d1 = length(uv-p1);
    float d2 = length(uv-p2);
    float d3 = length(uv-p3);

    d1 = sin(d1+iTime*0.9);
    d2 = sin(d2+iTime*0.8);
    d3 = sin(d3 + iTime*0.87);

    float d = min(d1,d2);
    d = min(d,d3);



    vec3 col = vec3(0);

    col = palette(
    d*0.5,
    vec3(0.5),
    vec3(.5),
    vec3(1.),
    vec3(.0,.1,.2));
    col = mix(col, palette(d*0.5,vec3(.5),vec3(.5),vec3(1.),vec3(.0,.33,.66)), d2 *0.5+0.5);
    col = mix(col, palette(d*0.5,vec3(.5),vec3(.5),vec3(1.),vec3(.0,.0,.0)), d1*0.5+0.5);

    fragColor = vec4(col,1.0);
}

// variant of https://shadertoy.com/view/sdGXWD
// another approach here: https://www.shadertoy.com/view/fdVSDW

// --- utils inspired from https://www.shadertoy.com/view/llySRh

#define H(p)     fract( sin( (p) * 78.233 ) * 43758.5453 ) // hash
#define hue(v) ( .88 + .18 * cos( v  + vec3(0,23,21)  ) )  // desaturated hue
#define S(v)     smoothstep(  8./R.y, 0.,   v )            // AA draw
#define s(a,b)   t=a, a=b, b=t                             // swap

float line(vec2 p, vec2 a,vec2 b, out float h) { // h = pos on segment ( possibly out )
    p -= a, b -= a;
    return length( p - b * clamp( h = dot(p, b) / dot(b, b), 0., 1.) );
}

// -----------------------------------------------------------

vec2 f(float t) {                               // curve P(t)
    vec2 P = vec2(0);
    for (float f, i = 0.; i < 6.; i++ )         // Fourier series 
        f = exp2(i)*t + 6.283*H(i) + i*iTime,
        P += vec2(cos(f),sin(f)) / pow(1.4, i); // spectrum 1.4^-f, random phase
    return P;
}
#define D(t)  l = dot( P = f(t) - U, P )        // distance² to curve(t)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, P,
         U = 2.5*( u+u - R ) / R.y;
    float l, d0 = 9., d1=d0, T0,T1,t0,t1,e,h0,h1,h_,c0,c1, t=0., dt = .015, dt2 = dt/4.;
    O-=O;
    for ( ; t < 6.283; t+=dt )                  // Sample the curve and get the 2 closests
        D(t),                                   //   that are not on the same segment.
          l < d0 ?  t<=T0+dt ? d0=l,T0=t        // Prev best was on same segment: don't update 2nd best
                             : ( d1=d0, T1=T0, d0=l, T0=t ) // independant best: save old as 2nd best
        : l < d1 && t> T0+dt ? d1=l,T1=t : t;   // update 2nd best only if not on best segment

    if (iMouse.z > 0.) O += smoothstep(8./R.y, 0., sqrt(min(d0,d1))-.01 ); // test: draw dots

    t0=T0; t1=T1;    
    for ( t = -dt; t < dt; t += dt2 )           // Look more accurately around 1st and second
        D(T0+t), l < d0 ? d0=l,  t0=T0+t : t,   //   closest curve segments.
        D(T1+t), l < d1 ? d1=l,  t1=T1+t : t;

   // draw upstream and dowstream segment for 1st and 2nd best
   // the mess with h & c is to draw the borders pushed only at the right extra angle on the round segment junction
#define  L(t,h,c) ( d0 = line(U, f(t-dt2), f(t), h  ),  h  = (abs(h -.5)-.5)*length(f(t)-f(t-dt2)), \
                    d1 = line(U, f(t+dt2), f(t), h_ ),  h_ = (abs(h_-.5)-.5)*length(f(t)-f(t-dt2)), \
                  c = dot( normalize( f(t) - f(t-dt2) ), normalize( f(t+dt2) - f(t) ) ),            \
                ( d1 < d0 ? h=h_, d1 : d0 ) -.06 ) 
    T0 = L(t0,h0,c0), T1 = L(t1,h1,c1);

if ( cos(t1) < cos(t0) ) s(T0,T1), s(t0,t1), s(h0,h1), s(c0,c1); // z-sort
                                       // ribbon pattern (up to a bit of the joint)         , ribbon color,  ribbon mask
#define B(h,t,T,c) O += (1.-O.a)* vec4( ( h > sqrt(1.-c*c)*(.2+6./R.y) ? 1. : S(T + 6./R.y) ) * hue(t), 1 ) * S(T) // blend ribon pattern*mask
    B(h0,t0,T0,c0);                             // draw top segment
    B(h1,t1,T1,c1);                             // draw bottom segment
    O +=  1.-O.a ;                              // background
}

#define SC (250.0)

vec2 noise(in vec2 uv)
{
    //return sin(uv.x/1.6)+cos(-uv.y*1.6);
    return vec2(sin(uv.x),cos(uv.y))+vec2(1.);
}

#define OCTAVES 8
float fbm1(in vec2 uv,int octaves,float i1,float value1)
{
    //this function generates the terrain height

    float f = 2.15; //change this number to change the spacing of the mountains 
    //Another interesting variation:
    //float f = 2.15+(sin(uv.x/i1)+cos(uv.x/i1))*value1;

    float value = value1;
    float amplitude = i1*value1;
    float freq = i1/f;
    float n1 = 0.;
    vec2 noise2,noise1;
    for (int i = 0; i < octaves; i++)
    {


        noise2 = noise(noise1.yx-uv/freq);
        noise1 = noise2-vec2(value)-noise1.yx*freq;
        freq /= -f;
        n1 = noise1.x+noise1.y;

        value += n1*(amplitude-freq);

        uv -= noise2.yx*freq;
    }

    return value/(4.);
}

float fbm(vec2 uv, int oct){
    float result = 0.;
    //uv /= 2.;
    for(float i = 1.; i < 3.; i++){
        float i1 = i*i;
        result = fbm1(uv,oct,i1,result-result/i1);
        oct /= 2;
    }
    return result;
}

float fbm(in vec2 uv){
    return fbm(uv,OCTAVES);
}

float f(in vec3 p,int iters)
{   
    float h = fbm(p.xz,iters);
    return h;
}

float f(in vec3 p)
{   
    float h = fbm(p.xz,OCTAVES);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos,OCTAVES);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

