// idea:
// change brightness depending on difference in radius from last frame to this one,
// for each cell / circle
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float xr = 40.; float yr = 40.;

    vec2 ipos = vec2(floor(xr * uv.x) + 0.5, floor(yr * uv.y) + 0.5);
    vec2 fpos = vec2(fract(xr * uv.x) - 0.5, fract(yr * uv.y) - 0.5);

    float id = length(ipos);//max(abs(ipos.x), abs(ipos.y));
    float d = 1.- length(fpos);//max(abs(fpos.x), abs(fpos.y));
   // d *= .5;
    // 3.5 30. 3.5 // 30. / 37.
   // float m = 30./37.;
    float a = atan(ipos.y, ipos.x);
    float b = .5 + .5 * cos(iTime + 0.1 * length(ipos));
    //b = b * (1.-b) * 4.;
    float e = 1.8 + 0.5 * cos(-0.8 * iTime + 1. * a + 0.5 * id);
    float e2 = e + cos(0.8 * iTime + 3. * a - 0.5 * id);

    //float e2= cos(0.1 * id + 0.8 * iTime);//
    float c = step(max(0.5, pow(0.5 + 0.5 * (1.-b) * e,12.)), d);//(1.-b) * e);

    float c2 =  step(max(0.5, pow(0.5 + 0.5 * b * e2, 4.)), d);//(1.-b) * e);//step(0.5 + 0.5 * b * e2, d);
    c = smoothstep(-0.1,0.1,c2-c) * c;
    //c *= .5 + .5 * cos(c + iTime);
    //c = clamp(c, 0., 1.);

    vec3 col = pal(e/c + 0.4 * h21(ipos), vec3(0.5), vec3(0.5), vec3(1.5), vec3(0.,0.333,0.666));

    // Time varying pixel color
   // vec3 col = vec3(c * e);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float N21(vec2 p){
    p+=12.245;
    p=fract(p*vec2(1.432342,515.5621));
    p+=dot(p,p+341.3535);
    return fract(p.x*p.y*.12);

}


float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  float r=length(q)-t.y;
  return min(r,length(vec3(abs(p.x),p.y,abs(p.z))-vec3(.17,0,.18))-.06);
}


float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;

  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float crossTorus(vec3 p,vec3 id){
    float w=.035;

    float t1=sdTorus(vec3(abs(p.x)-.5,p.z,abs(p.y)-.5),vec2(.25,w));
    //t1=min(t1,length(p-vec3(.25,-.25,-.25))-.2);



    float t2=sdTorus(vec3(abs(p.x)-.5,p.z,p.y),vec2(.25,w));
    t2=min(t2,sdTorus(vec3(p.x,p.z,abs(p.y)-.5),vec2(.25,w)));



    float r=N21(id.xy+id.z+floor(iTime/3.));
    float rn=N21(id.xy+id.z+floor(iTime/3.)+1.);
    float t=r>.5?t1:t2;
    float tn=rn>.5?t1:t2;

    return mix(t,tn,smoothstep(.8,.9,fract(iTime/3.)));


}

float cube(vec3 p, vec3 id){

    float c=cos(1.57);
    float s=sin(1.57);
    mat2 m=mat2(c,s,-s,c);
    vec2 a=p.yz*m;
    vec2 b=p.xz*m;
    float t=min(crossTorus(p,id),crossTorus(vec3(p.x,a.x,a.y),id));
    t=min(crossTorus(vec3(a.x,p.y,b.y),id),t);
    return max(sdBox(p,vec3(.52)),t);
}

vec3 transform(inout vec3 p){
    float c=cos(sin(iTime/10.));
    float s=sin(sin(iTime/10.));

    mat2 m=mat2(c,s,-s,c);
    p.xy*=m;
    vec3 op=p;
    vec3 r=vec3(1.);
    p=fract(p)-.5;
    return floor(op-p); 
}

float dist(vec3 p){

    vec3 id=transform(p);   
    float cu=cube(p,id);

    return cu;
}

vec3 normal(vec3 p){
    vec2 e=vec2(.001,0.0);
    float d=dist(p);
    vec3 n=vec3(
        d-dist(p-e.xyy),
        d-dist(p-e.yxy),
        d-dist(p-e.yyx));
    return normalize(n);
}



float rayMarch(vec3 ro, vec3 rd){
    float d=0.;
    float td=0.;
    float i=0.;;
    for(;i<50.;i++){
        d=dist(ro+td*rd);
        td+=d;
        if(td>50. || d<.001)
            break;
    }
    if(i>50.)
        return 50.;
    return float(td);    
}

float softshadow( in vec3 ro, in vec3 rd, float k )
{
    float res = 1.0;

    for( float t=.001; t<100.; )
    {
        float h = dist(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

vec3 shade(vec3 p,vec3 rd){
    vec3 n=normal(p);


    vec3 id=transform(p); 

    vec3 col=vec3(.15,.55,.80);
    //WIP color mix
    vec3 col1=normalize(.5+.5*sin(iTime/2.+col*(id.x+id.y*10.)));
    vec3 col2=normalize(.5+.5*sin(iTime/2.+col*((id.x+1.)+id.y*10.)));
    vec3 col3=normalize(.5+.5*sin(iTime/2.+col*((id.x-1.)+id.y*10.)));
    vec3 col4=normalize(.5+.5*sin(iTime/2.+col*((id.x)+(id.y-1.)*10.)));
    vec3 col5=normalize(.5+.5*sin(iTime/2.+col*((id.x)+(id.y+1.)*10.)));
    col1+=col3*smoothstep(.75,1.,length(p.x-vec2(.25)));
    col1+=col2*smoothstep(.75,1.,length(p.x+vec2(.25)));
    col1+=col4*smoothstep(.75,1.,length(p.y-vec2(.25)));
    col1+=col5*smoothstep(.75,1.,length(p.y+vec2(.25)));

    vec3 a=col1/1.5*vec3(abs(dot(n,normalize(vec3(3.,2.,13.)-p))));
    return a;//*softshadow(p+n*.1,normalize(-p+vec3(0,0.,0.-iTime/10.)),2.);
}






