vec2 random(in vec2 _st ){
    _st = vec2(
            dot(_st,vec2(127.326,321.324)),
            dot(_st,vec2(15.31,45.332))
                    );
    return 2.0*fract(sin(_st)*432.23) +1.0; //1.0 ~ 3.0
}
float noise(vec2 st){ //Gradient Noise
    vec2 i = fract(st);//取小数
    vec2 k = floor(st);//向负无穷取整
    vec2 u = i*i*(3.0-2.0*i);//插值函数
    return  mix(
                    mix(dot(random(k + vec2(0.0,0.0)),i - vec2(0.0,0.0)),
                        dot(random(k + vec2(0.0,1.0)),i - vec2(0.0,1.0)),
                         u.y),
                    mix(dot(random(k + vec2(1.0,0.0)),i - vec2(1.0,0.0)),
                        dot(random(k + vec2(1.0,1.0)),i - vec2(1.0,1.0)),
                         u.y),
                       u.x
                   );//二维noise
}

vec3 shape(vec2 st ,float r){
    vec2 uv = st-vec2(0.5);
    uv*=2.0;
    float angle = atan(uv.y,uv.x);
    float m = abs(mod(angle+iTime*2.,3.14*2.)-3.14)/3.6 ;
    r += sin(angle*50.)*noise(uv+iTime*0.2)*.1 * (1.-step(0.5,iMouse.z));
    r += (sin(angle*20.)*.1*pow(m,2.))* (1.-step(0.5,iMouse.z));
    float c = 1.-smoothstep(r,r+0.007,dot(uv,uv));
    vec3 color = 0.5 + 0.5*cos(6.28*(angle/6.28 +vec3(0.3,0.6,0.9)) );
    return c*color;
}
vec3 createCircle(vec2 st,float radius,float width){
    return shape(st,radius) - shape(st,radius-width);
}
