//!
// Processed by 'GLSL Shader Shrinker'
// (https://github.com/deanthecoder/GLSLShaderShrinker)

float pBox(vec3 b, vec3 p) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

mat2 rot(float a) {
	float s = sin(a),
	      c = cos(a);
	return mat2(c, s, -s, c);
}

float hash(float x) { return fract(sin(x) * 43758.545) * 2. - 1.; }
float rnd = 1.0;
float sdf(vec3 p) {
	vec3 z = p;
	float sum = 1.5,
	      d = 1e5;
	     // rnd = 0.;
	for (float i = 0.; i < 11.; i++) {
		float td = pBox(vec3(.5), z) / sum;
		z = abs(z) - vec3(1.2, 1.2, 0);
		rnd = smoothstep(hash(rnd),1.5,2.0)*iTime*0.1;
		d = min(td, d);
		z.xy *= rot(3.1415926 * .2 * rnd);
		z.yz *= rot(3.1415926 * .2 * rnd);
        z.xz *= rot(3.1415926 * .2 * rnd);
		z *= 1.5;
		sum *= 1.5;
	}

	return d;
}

vec3 calcNormal(vec3 pos) {
	const vec2 e = vec2(1, -1) * 58e-5;
	return normalize(e.xyy * sdf(pos + e.xyy) + e.yyx * sdf(pos + e.yyx) + e.yxy * sdf(pos + e.yxy) + e.xxx * sdf(pos + e.xxx));
}

float calcOcclusion(vec3 pos, vec3 nor) {
	float res = 0.;
	vec3 aopos = pos;
	for (int i = 0; i < 4; i++) {
		aopos = pos + nor * .2 * float(i);
		float d = sdf(aopos);
		res += d;
	}

	return clamp(res, 0., 1.);
}

float castRay(vec3 ro, vec3 rd) {
	float t = 0.;
	for (int i = 0; i < 100; i++) {
		vec3 pos = ro + t * rd;
		float h = sdf(pos);
		if (abs(h) < .001) break;
		t += h;
		if (t > 20.) break;
	}

	if (t > 20.) t = -1.;
	return t;
}

vec3 env_color(vec3 dir) {
	if (dir.y > 0.) return mix(vec3(0, .5, 1), vec3(0, .1, .8), dir.y);
	return mix(vec3(0, .5, 1), vec3(.8, .7, .6), pow(-dir.y, .5));
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
	vec2 p = (2. * fragCoord - iResolution.xy) / iResolution.y;
	float t,
	      fi = 10. * iMouse.x / iResolution.x,
	      th = 10. * iMouse.y / iResolution.y;
	vec3 ro = vec3(5. * cos(fi) * sin(th), 5. * sin(th) * sin(fi), 5. * cos(th)),
	     ww = normalize(vec3(0) - ro),
	     uu = normalize(cross(ww, vec3(0, 1, 0))),
	     rd = normalize(p.x * uu + p.y * normalize(cross(uu, ww)) + 1.5 * ww),
	     col = vec3(.25, .72, .75) - .005 * rd.y;
	t = castRay(ro, rd);
	if (t > 0.) {
		col = vec3(1);
		vec3 pos = ro + t * rd,
		     nor = calcNormal(pos);
		float occ = calcOcclusion(pos, nor);
		vec3 sun_dir = normalize(vec3(.8, .4, .2));
		float sun_sha = step(castRay(pos + nor * .001, sun_dir), 0.),
		      sun_dif = clamp(dot(nor, sun_dir), 0., 1.),
		      sky_dif = clamp(.5 + .5 * dot(nor, vec3(0, 1, 0)), 0., 1.),
		      bou_dif = clamp(.5 + .5 * dot(nor, vec3(0, -1, 0)), 0., 1.);
		col = vec3(.18) * vec3(4) * sun_dif * sun_sha;
		col += vec3(.18) * vec3(.5, .8, .9) * sky_dif * occ;
		col += vec3(.18) * vec3(.7, .3, .2) * bou_dif * occ;
	}
	else col = env_color(rd);

	fragColor = vec4(pow(col, vec3(.4545)), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 background = vec3(0.2, 0.2, 0.2);
    vec3 sunColor = vec3(0.996, 0.364, 0.160);
    vec3 innerSun = vec3(1.00,0.65,0.00);
    vec3 ground1 = vec3(0.301, 0.286, 0.274);
    vec3 ground2 = vec3(0.552, 0.447, 0.270);

    vec3 col;
    if (uv.y > 0.3) {
        vec2 sun_pos = vec2(0.5, sin(iTime) * 0.5 - 0.18);
        float dist = distance(sun_pos, uv.xy);
        vec3 rayCol = mix(sunColor, background, dist);

        vec3 innerSunCol = mix(innerSun, rayCol, smoothstep(0.0, 0.02, dist));
        col = innerSunCol;

    } else {
        col = mix(ground1, ground2, uv.y / 0.2);
    }

    fragColor = vec4(col,1.0);
}

// 1. / tanh(a*cos(0)) ensures it hits +-1.
float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float t = thc(3.5, 0.8 * iTime);

    float r = 16. + 8. * t;
    float ix = floor(r * uv.x) + 0.5;
    uv.x = fract(r * uv.x) - 0.5;

    vec2 p = 0.2 * r * vec2(0., thc(2., 2. * iTime + 2. * ix));

    float d = length(vec2(uv.x, r * uv.y) - p);
    float s = step(d, (0.015 - 0.005 * t) * r);

    fragColor = vec4(s);
}

