#define PI 3.1415326

mat2 rotate2D(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

// Distance of point p to wave #waveNum
float distanceTo(vec3 p, float waveNum) {
    float frequency = exp(waveNum);
    return p.y+(abs(cos(p.x*frequency*PI))+sin(p.z*frequency*PI))/frequency;
}

float minimumDist(vec3 p) {
    float minDist = 1000.;
    // This loop determines the number of waves being vizualized
    for(float j=0.; j<mod(iTime, 15.); j++) {
        float curDist = distanceTo(p, j);
        minDist = min(minDist, curDist);
    }
    return minDist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float i;
    float eyeDist, minDist;

    vec3 eyePos = vec3(0, 0, -1);
    vec3 ray = vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.x, 1.);
    ray = normalize(ray);

    // Camera motion
    eyePos.yz *= rotate2D(5.);
    eyePos.xz *= rotate2D(iTime);
    ray.yz *= rotate2D(5.);
    ray.xz *= rotate2D(iTime);

    for(minDist = 1.; i<100. && minDist>0.001; i++) {
        // Point to check
        vec3 p = eyePos+eyeDist*ray;

        minDist = minimumDist(p);
        // Move point forward
        eyeDist += minDist*.25;
    }
    i += 2.*(minDist*1000.+eyePos.z);
    fragColor = vec4(1.-i/100.);
}

