// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define MAX_STEPS 80
#define MAX_DIST 32.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define MATERIAL0 0
#define SPEED 2.0
#define ZERO (min(iFrame,0))

float B3D(vec3 p, vec3 s) {
    p = abs(p)-s;
    return max(max(p.x,p.y),p.z);
}

// https://iquilezles.org/www/articles/fbmsdf/fbmsdf.htm
// https://www.shadertoy.com/view/3dGSWR
float sph( vec3 i, vec3 f, vec3 c )
{
    // random radius at grid vertex i+c (please replace this hash by
    // something better if you plan to use this for a real application)
    vec3  p = 17.0*fract( (i+c)*0.3183099+vec3(0.11,0.17,0.13) );
    float w = fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
    float r = 0.8*w*w;
    // distance to sphere at grid vertex i+c
    return B3D((f-c),vec3(r));
}

// https://iquilezles.org/www/articles/fbmsdf/fbmsdf.htm
float sdBase( in vec3 p)
{
    float t = iTime*0.5;
    p.y-=t;
    vec3 i = floor(p);
    vec3 f = fract(p);
    return min(min(min(sph(i,f,vec3(0,0,0)),
                       sph(i,f,vec3(0,0,1))),
                   min(sph(i,f,vec3(0,1,0)),
                       sph(i,f,vec3(0,1,1)))),
               min(min(sph(i,f,vec3(1,0,0)),
                       sph(i,f,vec3(1,0,1))),
                   min(sph(i,f,vec3(1,1,0)),
                       sph(i,f,vec3(1,1,1)))));
}

vec3 path(float z)
{
    vec3 p = vec3(sin(z) * .6, cos(z * .5), z);
    return p;
}

vec2 GetDist(vec3 p) {
    vec3 prevP = p;

    p.xy -= path(p.z).xy;

    p.z += iTime*SPEED;

    float d = -length(p.xy) + 1.5;

    float d2 = sdBase(p);

    return vec2(max(-d2,d),MATERIAL0);
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(p.x,p.y,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*occ;
    diffCol += col*vec3(0.9)*skyDiff*occ;
    diffCol += col*vec3(0.7)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    if(mat == MATERIAL0){
        col = diffuseMaterial(n,rd,p,vec3(1.0));
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float t = iTime*SPEED;
    vec3 ro = path(t+1.5);

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        col *= exp( -0.0001*d.x*d.x*d.x*d.x );//fog
    } else {
        col = vec3(0.0);   
    }

    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(col,1.0);
}

#define TAU (6.283185307)
#define HEX(x) (vec3((x >> 16) & 255, (x >> 8) & 255, x & 255) / 255.)
#define STRIPECOUNT 10.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = fract(iTime / 4.);
  // Normalized pixel coordinates (from 0 to 1)
  float scale = min(iResolution.x, iResolution.y);
  vec2 uv = (gl_FragCoord.xy - 0.50 * iResolution.xy) / scale;

  float dist = length(uv);
  float angle = atan(uv.y, uv.x);
  // for calculating the parity of the stripe
  float dark = floor(mod(angle * STRIPECOUNT / TAU - 2. * time, 2.));
  // for calculating the stripes’ angle
  float stripeAngle = ((dark - 0.5) * time * TAU) + (0.5 + floor(angle * STRIPECOUNT / TAU - 2. * time) + 2. * time) * TAU / STRIPECOUNT;
  vec2 angleVec = vec2(cos(stripeAngle), sin(stripeAngle));
  // for calculating which palette to use
  float palette = step(fract(
      log(dist + 0.01) + angle / TAU + time * 2.
  ), 0.5);

  const vec3 col0L = HEX(0x840DaE);
  const vec3 col1L = HEX(0x3BCEAC);
  const vec3 col2L = HEX(0xFFD23F);
  const vec3 col3L = HEX(0xEE4266);

  const vec3 col0D = HEX(0x118AB2);
  const vec3 col1D = HEX(0xEF476F);
  const vec3 col2D = HEX(0xFFD166);
  const vec3 col3D = HEX(0x06D6A0);

  vec3 col0 = mix(col0L, col0D, palette);
  vec3 col1 = mix(col1L, col1D, palette);
  vec3 col2 = mix(col2L, col2D, palette);
  vec3 col3 = mix(col3L, col3D, palette);

  float spiral = fract(
      0.7 * log(
          abs(
              dot(uv, angleVec)
          ) + 0.02
      ) + (
          8. * dark * time
      ) + (
          -11. * (1. - dark) * time
      ) + (
          dark * 0.2
      )
  );

  vec3 col = mix(
      mix(
          col0, col1, step(spiral, 0.75)
      ), mix(
          col2, col3, step(spiral, 0.25)
      ), step(spiral, 0.5)
  );
  // Output to screen
  fragColor = vec4(
    col, 1.0
  );
}

// Common
#define PI 3.1415926538

// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
  return fract(sin(seed)*43758.5453);
}

// Cosine direction
vec3 Cosine(in float seed,in vec3 nor)
{
  float u=Hash(78.233+seed);
  float v=Hash(10.873+seed);

  // method 3 by fizzer: http://www.amietia.com/lambertnotangent.html
  float a=6.2831853*v;
  u=2.*u-1.;
  return normalize(nor+vec3(sqrt(1.-u*u)*vec2(cos(a),sin(a)),u));
}

// Rotation matrix around z axis
// a : Angle
mat3 rotate_z(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

// Compute the ray
// m : Mouse position
// p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m,in vec2 p,out vec3 ro,out vec3 rd)
{
  float a=3.*3.14*m.x;
  float le=3.8;

  ro=vec3(20.,0.,5.);
  ro*=rotate_z(3.*3.14*m.x);

  vec3 ta=vec3(0.,0.,1.);
  vec3 ww=normalize(ta-ro);
  vec3 uu=normalize(cross(ww,vec3(0.,0.,1.)));
  vec3 vv=normalize(cross(uu,ww));
  rd=normalize(p.x*uu+p.y*vv+le*ww);
}

