// Transforms
vec3 translateX(vec3 p, float t)
{
    return vec3(p.x + t, p.y,p.z);
}
vec3 translateY(vec3 p, float t)
{
    return vec3(p.x, p.y + t,p.z);
}
vec3 translateZ(vec3 p, float t)
{
    return vec3(p.x, p.y,p.z + t);
}

vec3 rotateX(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(p.x,ca*p.y-sa*p.z,sa*p.y+ca*p.z);
}

vec3 rotateY(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.z,p.y,-sa*p.x+ca*p.z);
}

vec3 rotateZ(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.y,-sa*p.x+ca*p.y,p.z);
}

vec3 scaleX(vec3 p, float s)
{
    return vec3(p.x*s,p.y,p.z);
}
vec3 scaleY(vec3 p, float s)
{
    return vec3(p.x,p.y*s,p.z);
}
vec3 scaleZ(vec3 p, float s)
{
    return vec3(p.x,p.y,p.z*s);
}
vec3 scaleUniform(vec3 p, float s)
{
    return vec3(p.x*s,p.y*s,p.z*s);
}

// Primitives

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : radius
float point(vec3 p,vec3 c,float r)
{
  return length(p-c) - r;
}
vec4 pointColor(vec3 p, vec3 c, float r, vec3 color)
{
  float x = point(p, c, r);
  return vec4(x, color);
}

float sdPlane( vec3 p, vec3 n, float h )
{
  return dot(p,n) + h;
}
vec4 colorPlane( vec3 p, vec3 n, float h, vec3 color)
{
    return vec4(sdPlane(p,n,h), color);
}

// Segment
float segment(vec3 p, vec3 a, vec3 b, float e, float R)
{
  if (dot(b-a, p-a) < 0.0)
  {
    return dot(p-a, p-a);
  }
  else if (dot(b-a, p-b) > 0.0)
  {
    return dot(p-b, p-b);
  }
  else 
  {
    vec3 u = (b-a)/length(b-a);
    float sqLengthAP = dot(p-a, p-a);
    return sqLengthAP - dot(dot((p-a), u), dot((p-a), u));
  }
}

vec4 segmentColor(vec3 p, vec3 a, vec3 b, vec3 color, float e, float R)
{
  float x = segment(p, a, b, e, R);
  return vec4(x, color);
}

// Circle
float circle(vec3 p, vec3 c, float r, float e, float R)
{
  vec3 n = p -c;
  vec3 u = vec3(1.0, 0.0, 0.0);
  float h = dot(n, u);
  float d = dot(n, n);
  float y = d - h * h;
  y = r - sqrt(y);

  return (sqrt(y*y + h*h));
}
vec4 circleColor(vec3 p, vec3 c, float r, vec3 color, float e, float R)
{
  float x = circle(p, c, r, e, R);
  return vec4(x, color);
}

// Disk
float disk(vec3 p, vec3 c, float r)
{
  vec3 n = p -c;
  vec3 u = vec3(1.0, 0.0, 0.0);
  float h = dot(n, u);
  float d = dot(n, n);
  float y = d - h * h;

  if (y < r*r)
    return (abs(h));

  y = r - sqrt(y);
  return (sqrt(y*y + h*h));
}
vec4 diskColor(vec3 p, vec3 c, float r, vec3 color)
{
  float x = disk(p,c,r);
  return vec4(x, color);
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
vec4 colorTorus( vec3 p, vec2 t, vec3 color )
{
  return vec4(sdTorus(p,t), color);
}

vec4 sdRoundBox( vec3 p, vec3 b, float r, vec3 color)
{
  vec3 q = abs(p) - b;
  return vec4(length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r, color);
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
vec4 colorRoundedCylinder(vec3 p, float ra, float rb, float h, vec3 color)
{
    return vec4(sdRoundedCylinder(p,ra,rb,h),color);
}

// Operators

// Blending
// a,b : field function of the sub-trees
float Blend(float a,float b)
{
  return a+b;
}

vec4 BlendColor(vec4 a, vec4 b, float k)
{
  float h = clamp( 0.5 + 0.5*(b.x-a.x)/k, 0.0, 1.0 );
  float value = mix( b.x, a.x, h ) - k*h*(1.0-h);
  vec3 color = (a.yzw * b.x + b.yzw * a.x) / (a.x + b.x);
  return vec4(value, color);
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a,float b)
{
  return min(a,b);
}
vec4 UnionColor(vec4 a, vec4 b)
{
  if (a.x < b.x)
    return a;
  return b;
}

float Intersection(float a, float b)
{
  return max(a,b);
}

float Difference(float a, float b)
{
  return max(a,-b);
}

float BlendMinus(float a, float b)
{
  return a - b;
}
vec4 BlendMinusColor(vec4 a, vec4 b)
{
  return vec4(a.x - b.x, a.yzw);
}

