struct Closest {
    int i;
    float d;
};

vec2 mul(vec2 a, vec2 b) { return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x); }
vec2 inv(vec2 a) { return vec2(a.x, -a.y) / (a.x*a.x + a.y*a.y); }

const int deg = 10;
vec2 roots[deg];

vec2 f(vec2 a) {
    vec2 ret = vec2(1.0, 0.0);
    for (int i = 0; i < deg; i++) {
        ret = mul(ret, a-roots[i]);
    }
    return ret;
}

vec2 fp(vec2 a) {
    vec2 sum = vec2(0.0, 0.0);
    for (int i = 0; i < deg; i++) {
        sum += inv(a-roots[i]);
    }
    return inv(sum);
}

Closest getClosest(vec2 u) {
    Closest closest = Closest(0, distance(u, roots[0]));
    for (int i = 1; i < deg; i++) {
        float d = distance(u, roots[i]);
        if (d < closest.d) {
            closest.i = i;
            closest.d = d;
        }
    }
    return closest;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 a = 2.5 * (fragCoord - iResolution.xy / 2.0) / min(iResolution.x, iResolution.y);
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    for (int i = 0; i < deg; i++) {
        float rot = 0.1 * iTime * (float(i)+1.0);
        roots[i] = vec2(cos(rot), sin(rot));
    }

    vec2 u = a;
    Closest c;
    for (int i = 0; i < 100; i++) {
        u -= fp(u);
        c = getClosest(u);
        if (c.d < 0.1) { break; }
    }

    fragColor.rgb = vec3(float(c.i) / float(deg));

    for (int i = 0; i < deg; i++) {
        if (distance(roots[i], a) < 0.05) {
            fragColor.r = 1.0;
        }
    }
}

