float PrCapsDf (vec3 p, float r, float h);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float IFbm1 (float p);
float Fbm2 (vec2 p);

vec4 szFlr;
vec3 ltDir, qHit, pvU[5], pvD[5];
float dstFar, tCur, twrScl, flSpc, nFlr, twrRad, bSizeV, cIdV;
int idObj;
const int idFlr = 1, idStr = 2, idRl = 3, idStn = 4, idCln = 5, idMovBU = 6, 
   idMovHU = 7, idMovBD = 8, idMovHD = 9, idWall = 10, idRfEx = 11, idGrEx = 12;
const float pi = 3.1415927, sqrt2 = 1.41421;

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float GObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, stSpc, xLim1, xLim2, bRad, r, a, x;
  bool topFlr, botFlr;
  dMin = dstFar / twrScl;
  p /= twrScl;
  r = length (p.xz) - twrRad;
  d = r - 20.;
  if (d < 1.) {
    a = 2. * pi * ((floor (6. * atan (p.x, - p.z) / (2. * pi)) + 0.5) / 6.);
    stSpc = 6.;
    xLim1 = abs (dot (p.xz, sin (a + vec2 (0.5 * pi, 0.)))) - 22.;
    xLim2 = xLim1 + 16.;
    bRad = 0.5 * 0.5;
    topFlr = (cIdV == 2. * nFlr - 1.);
    botFlr = (cIdV == 0.);
    if (topFlr) {
      d = length (max (abs (vec2 (p.y + 0.5 * szFlr.w, r + szFlr.z + stSpc)) -
         vec2 (0.5 * szFlr.w, szFlr.z), 0.));
      DMIN (idFlr);
      d = max (length (vec2 (p.y + 0.4, abs (r + szFlr.z + stSpc - 0.5)) - (szFlr.z - 0.1)) -
         bRad, - xLim2);
      DMIN (idRl);
    }
    d = max (length (max (abs (vec2 ((topFlr ? - p.y : abs (p.y)) - flSpc,
       r - 0.4 * (szFlr.z + stSpc))) - vec2 (szFlr.w, 1.4 * (szFlr.z + stSpc)), 0.)), - xLim1);
    DMIN (idFlr);
    d = max (length (max (abs (vec2 (p.y + szFlr.w, r)) - vec2 (szFlr.w, 2. * szFlr.z + stSpc +
       0.5), 0.)), xLim2);
    DMIN (idFlr);
    p.zx = Rot2D (p.zx, a);
    p.z = abs (p.z) - twrRad;
    x = abs (p.x) - szFlr.x;
    for (float sz = -1.; sz <= 1.; sz += 2.) {
      if (! topFlr || sz < 0.) {
        q.x = x;
        q.yz = p.yz - sz * vec2 (szFlr.y - szFlr.w, - (szFlr.z + stSpc));
        d = abs (q.y) - (szFlr.y - szFlr.w - 0.005);
        q.xy = vec2 (q.x + sz * q.y, - sz * q.x + q.y) / sqrt2;
        d = max (max (max (q.y - 0.5 * sqrt2 - abs (0.5 * sqrt2 - mod (q.x, sqrt2)),
           abs (q.z) - szFlr.z), -1. - q.y), d);
        DMIN (idStr);
      }
    }
    d = max (length (vec2 (p.y + flSpc - 4., abs (r - szFlr.z) - (2. * szFlr.z + stSpc -
       0.8))) - bRad, - xLim1);
    DMIN (idRl);
    d = max (length (vec2 (p.y - 3.5, abs (r - 0.4) - (2. * szFlr.z + stSpc - 0.1))) - bRad, xLim2);
    DMIN (idRl);
    q = vec3 (x - 4., p.y + 0.5 * flSpc, abs (p.z - (szFlr.z + stSpc)) - szFlr.z);
    d = max (length (vec2 ((q.x + q.y) / sqrt2, q.z)) - bRad, abs (x) - 8.);
    DMIN (idRl);
    q.xz = vec2 (x + 4., abs (p.z + szFlr.z + stSpc) - szFlr.z);
    if (! botFlr) {
      d = max (length (vec2 ((q.x - (p.y + 1.5 * flSpc)) / sqrt2, q.z)) - bRad, abs (x) - 8.);
      DMIN (idRl);
    }
    if (! topFlr) {
      d = max (length (vec2 ((q.x - (p.y - 0.5 * flSpc)) / sqrt2, q.z)) - bRad, abs (x) - 8.);
      DMIN (idRl);
    }
    x = abs (p.x);
    q.x = x - 22.;
    d = min (length (vec2 (q.x, p.y + flSpc - 4.)), length (vec2 (x - 6., p.y - 3.5))) - bRad;
    d = max (d, max (abs (p.z) - (2. * szFlr.z + stSpc), szFlr.z - abs (abs (p.z) - (szFlr.z + stSpc))));
    DMIN (idRl);
    q.yz = vec2 (p.y + flSpc - 2.5, abs (abs (p.z) - (szFlr.z + stSpc)) - szFlr.z);
    d = PrCapsDf (q.xzy, 0.7 * 0.7, 2. + 0.2);
    DMIN (idStn);
    if (! topFlr) {
      d = length (vec2 (q.x, p.z)) - 0.8 * 0.8;
      DMIN (idCln);
    }
    d = PrCapsDf (vec3 (x - 6., p.y - 2.5, q.z).xzy, 0.7 * 0.7, 2. + 0.2);
    DMIN (idStn);
    dMin *= 0.7;
  } else dMin = d;
  return twrScl * dMin;
}

float GObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d, eps, sy;
  eps = 0.0005;
  dHit = eps;
  if (rd.y == 0.) rd.y = 0.001;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    cIdV = floor (p.y / bSizeV);
    sy = (bSizeV * (cIdV + step (0., rd.y)) - p.y) / rd.y;
    d = abs (sy) + eps;
    if (cIdV >= 0. && cIdV < 2. * nFlr) {
       d = min (GObjDf (p - vec3 (0., bSizeV * (cIdV + 0.5), 0.)), d);
    }
    dHit += d;
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps || p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 GObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  cIdV = floor (p.y / bSizeV);
  p.y -= bSizeV * (cIdV + 0.5);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, r, wz, h;
  int idb;
  dMin = dstFar;
  p.y -= bSizeV * (nFlr - 0.5);
  p /= twrScl;
  h = bSizeV * (nFlr - 0.5) / twrScl;
  q = p;
  r = length (q.xz) - twrRad;
  q.y -= 1.3 * flSpc + h;
  q.xz = Rot2D (q.xz, 2. * pi * ((floor (3. * atan (q.z, - q.x) / (2. * pi)) + 0.5) / 3.));
  d = dot (vec2 (q.x, abs (q.z)), sin (0.09 * pi + vec2 (0., 0.5 * pi)));
  q.x = r + szFlr.z + 5.;
  d = max (d, length (max (abs (q.xy) - vec2 (2.1, 0.3 * flSpc), 0.)) - 0.02);
  DMINQ (idRfEx);
  q = p;
  q.y -= 0.25 * flSpc - h;
  q.xz = Rot2D (q.xz, 2. * pi * ((floor (3. * atan (q.z, - q.x) / (2. * pi)) + 0.5) / 3.));
  wz = q.z;
  d = abs (abs (q.z) - 18.) - 5.;
  q.x = r + szFlr.z + 1.3;
  d = max (d, length (max (abs (q.xy) - vec2 (2.9, 0.3 * flSpc), 0.)) - 0.02);
  DMINQ (idGrEx);
  q = p;
  q.y -= 0.4 * flSpc - h;
  d = max (length (max (abs (vec2 (r - 2. * szFlr.z - 15., q.y)) -
     vec2 (0.5, 0.4 * flSpc), 0.)) - 0.02, 0.2 - length (max (abs (vec2 (abs (wz) - 40., q.y)) -
     vec2 (4., 0.3 * flSpc), 0.))); 
  DMINQ (idWall);
  return twrScl * dMin;
}

vec4 SphHit (vec3 ro, vec3 rd, float r)
{
  vec3 vn;
  float b, d, w, dMin;
  dMin = dstFar;
  b = dot (rd, ro);
  w = b * b - dot (ro, ro) + r * r;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0.) {
      dMin = d;
      vn = (ro + d * rd) / r;
    }
  }
  return vec4 (dMin, vn);
}

vec4 CapsHit (vec3 ro, vec3 rd, float r, float h)
{
  vec3 s, vn;
  vec2 e;
  e = vec2 (-1., 1.);
  float dMin, d, a, b, w, drz;
  dMin = dstFar;
  ro.yz = ro.zy * e;
  rd.yz = rd.zy * e;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - r * r);
  if (w > 0.) {
    d = - b - sqrt (w);
    d /= max (a, 1e-6);
    s = ro + d * rd;
    if (d > 0. && abs (s.z) < h) {
      dMin = d;
      vn = vec3 (s.xy, 0.);
    } else {
      drz = - h * sign (s.z);
      ro.z += drz;
      b = dot (rd, ro);
      w = b * b - (dot (ro, ro) - r * r);
      if (w > 0.) {
        d = - b - sqrt (w);
        if (d > 0.) {
          dMin = d;
          vn = ro + d * rd;
        }
      }        
    }
  }
  vn.yz = - vn.zy * e;
  return vec4 (dMin, vn);
}

void PathVerts ()
{
  pvU[0] = vec3 (4., - flSpc, 5.);
  pvU[1] = vec3 (-14., - flSpc, -2. * szFlr.z - 5.);
  pvU[2] = vec3 (4., 0., - szFlr.z - 5.);
  pvU[3] = vec3 (-4., 0., szFlr.z + 5.);
  for (int k = 0; k < 4; k ++) pvU[k] += vec3 (1., 0., - twrRad);
  pvU[4] = pvU[0];
  pvU[2].xz = Rot2D (pvU[2].xz, - pi / 6.);
  pvU[3].xz = Rot2D (pvU[3].xz, - pi / 6.);
  pvU[4].xz = Rot2D (pvU[4].xz, - 2. * pi / 6.);
  pvU[4].y += 2. * flSpc;
  pvD[0] = vec3 (14., flSpc, -2. * szFlr.z - 5.);
  pvD[1] = vec3 (-4., flSpc, 5.);
  pvD[2] = vec3 (4., 0., szFlr.z + 5.);
  pvD[3] = vec3 (-4., 0., - szFlr.z - 5.);
  for (int k = 0; k < 4; k ++) pvD[k] += vec3 (-1., 0., - twrRad);
  pvD[4] = pvD[0];
  pvD[2].xz = Rot2D (pvD[2].xz, - pi / 6.);
  pvD[3].xz = Rot2D (pvD[3].xz, - pi / 6.);
  pvD[4].xz = Rot2D (pvD[4].xz, - 2. * pi / 6.);
  pvD[4].y -= 2. * flSpc;
}

vec4 MovObjHit (vec3 ro, vec3 rd)
{
  vec4 dc4;
  vec3 s[5], s1, s2, ss, vn;
  float dMin, d, r, t, h, iDir;
  int idb, iUD;
  dMin = dstFar;
  h = bSizeV * (nFlr - 0.5);
  ro.y -= h;
  h += 0.5 * flSpc * twrScl;
  iDir = 1.;
  for (float iMul = 0.; iMul < 6.; iMul ++) {
    iDir = 1. - iDir;
    idb = (iDir == 0.) ? idMovBU : idMovBD;
    for (int j = 0; j < 5; j ++) s[j] = (iDir == 0.) ? pvU[j] : pvD[j];
    t = fract (0.3 * tCur + 0.5 * iDir);
    r = 2. * t - 0.5 * (sign (t - 0.5) + 1.);
    for (int iSec = 0; iSec < 6; iSec ++) {
      s1 = (t < 0.5) ? mix (s[0], s[1], r) : mix (s[1], s[2], r);
      s2 = (t < 0.5) ? mix (s[2], s[3], r) : mix (s[3], s[4], r);
      iUD = 1;
      for (float iRep = 0.; iRep < 6.; iRep ++) {
        iUD = 1 - iUD;
        ss = (iUD == 0) ? s1 : s2;
        ss.y += (4. - 12. * mod (iRep, 3.) + 4. * mod (iMul, 3.)) * flSpc *
           sign (0.5 - iDir) + 2.9;
        dc4 = CapsHit (ro - ss * twrScl, rd, 1.2 * twrScl, 1.5 * twrScl);
        if (dc4.x < dMin && abs (ro.y + dc4.x * rd.y - flSpc * twrScl + 3.8 * twrScl) <
           h + 3.8 * twrScl) {
          dMin = dc4.x;
          vn = dc4.yzw;
          idObj = idb;
        }
        ss.y += 3.7;
        dc4 = SphHit (ro - ss * twrScl, rd, 1.1 * twrScl);
        if (dc4.x < dMin && abs (ro.y + dc4.x * rd.y - flSpc * twrScl + 3.8 * twrScl) <
           h + 3.8 * twrScl) {
          dMin = dc4.x;
          vn = dc4.yzw;
          idObj = idb + 1;
        }
      }
      for (int j = 0; j < 5; j ++) {
        s[j].xz = Rot2D (s[j].xz, - 2. * pi / 6.);
        s[j].y += 2. * flSpc * sign (0.5 - iDir);
      }
    }
  }
  return vec4 (dMin, normalize (vn));
}

float ObjAO (vec3 p)
{
  vec3 q, w, s[5], s1, s2;
  float d, r, t, iDir;
  int iUD;
  d = dstFar;
  p.y -= bSizeV * (nFlr - 0.5);
  p /= twrScl;
  iDir = 1.;
  for (float iMul = 0.; iMul < 6.; iMul ++) {
    iDir = 1. - iDir;
    q.xz = p.xz;
    for (int j = 0; j < 5; j ++) s[j] = (iDir == 0.) ? pvU[j] : pvD[j];
    t = fract (0.3 * tCur + 0.5 * iDir);
    r = 2. * t - 0.5 * (sign (t - 0.5) + 1.);
    for (int iSec = 0; iSec < 6; iSec ++) {
      s1 = (t < 0.5) ? mix (s[0], s[1], r) : mix (s[1], s[2], r);
      s2 = (t < 0.5) ? mix (s[2], s[3], r) : mix (s[3], s[4], r);
      iUD = 1;
      for (float iRep = 0.; iRep < 6.; iRep ++) {
        iUD = 1 - iUD;
        q.y = p.y - (4. - 12. * mod (iRep, 3.) + 4. * mod (iMul, 3.)) * flSpc * sign (0.5 - iDir);
        q.y -= 3.;
        w = q - ((iUD == 0) ? s1 : s2);
        if (abs (w.y) < 4.) d = min (d, length (w.xz));
      }
      for (int j = 0; j < 5; j ++) {
        s[j].xz = Rot2D (s[j].xz, - 2. * pi / 6.);
        s[j].y += 2. * flSpc * sign (0.5 - iDir);
      }
    }
  }
  return 0.6 + 0.4 * smoothstep (1.3, 2.6, d);
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0002 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 ObjCol (vec3 ro, vec3 vn)
{
  vec4 col4;
  if (idObj == idFlr) {
    col4 = vec4 (0.5, 0.2, 0., 0.1);
    if (vn.y > 0.99) col4 *= 0.8 + 0.2 * smoothstep (0., 0.02,
       abs (mod (0.25 * length (ro.xz) / twrScl + 0.5, 1.) - 0.5) - 0.07);
  } else if (idObj == idStr) {
    col4 = vec4 (0.6, 0.3, 0.1, 0.1);
  } else if (idObj == idRl) {
    col4 = vec4 (0.9, 0.9, 1., 0.2);
  } else if (idObj == idStn) {
    col4 = vec4 (0.8, 0.8, 1., 0.2);
  } else if (idObj == idCln) {
    col4 = vec4 (1., 0., 1., 0.2) * (0.8 + 0.2 * smoothstep (0., 0.02, abs (mod (ro.y / twrScl +
       0.5, 1.) - 0.5) - 0.07));
  } else if (idObj == idMovBU) {
    col4 = vec4 (0.8, 0.8, 0., 0.2);
  } else if (idObj == idMovHU) {
    col4 = vec4 (0.8, 0.8, 0., -1.);
  } else if (idObj == idMovBD) {
    col4 = vec4 (0., 0.8, 0.8, 0.2);
  } else if (idObj == idMovHD) {
    col4 = vec4 (0., 0.8, 0.8, -1.);
  } else if (idObj == idWall) {
    col4 = vec4 (0.8, 0.4, 0.2, 0.) * (0.8 + 0.2 * smoothstep (0., 0.02, 
       abs (mod (64. * atan (qHit.z, - qHit.x) / pi + 0.5, 1.) - 0.5) - 0.05));
  } else if (idObj == idRfEx) {
    col4 = vec4 (0.8, 0.5, 0.3, 0.1) * (0.8 + 0.2 * smoothstep (0., 0.02, abs (mod (qHit.y +
       0.5, 1.) - 0.5) - 0.07));
    if (abs (qHit.y + 0.6) < 4.1 && qHit.x > 0. && abs (abs (qHit.z) - 2.6) < 1.7)
       col4 = (qHit.z > 0.) ? vec4 (0.6, 0.6, 0., -1.) : vec4 (0., 0.6, 0.6, -1.);
  } else if (idObj == idGrEx) {
    col4 = vec4 (0.8, 0.5, 0.3, 0.1) * (0.8 + 0.2 * smoothstep (0., 0.02, abs (mod (qHit.y +
       0.5, 1.) - 0.5) - 0.07));
    if (abs (qHit.y + 0.6) < 4.1 && abs (qHit.z) - 18. > 0. && abs (qHit.x) < 1.7)
       col4 = (qHit.z > 0.) ? vec4 (0.6, 0.6, 0., -1.) : vec4 (0., 0.6, 0.6, -1.);
  }
  return col4;
}

vec3 GrndNf (vec2 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return vec3 (normalize (vec3 (Fbm2 (p) - vec2 (Fbm2 (p + e.xy),
     Fbm2 (p + e.yx)), 4. * e.x)).xzy);
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  if (rd.y > -0.02 && rd.y < 0.02 * (0.1 + 0.9 * IFbm1 (32. * atan (rd.z, - rd.x) / pi))) {
    col = 0.8 * vec3 (0.35, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((200. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * ltDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = mix (vec3 (0.3, 0.4, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, dc4;
  vec3 col, vn;
  float dstObj, dstObjG, dstObjM, s;
  int idObjG, idObjM;
  PathVerts ();
  dstObjG = GObjRay (ro, rd);
  idObjG = idObj;
  dc4 = MovObjHit (ro, rd);
  dstObjM = dc4.x;
  idObjM = idObj;
  dstObj = ObjRay (ro, rd);
  if (min (dstObj, min (dstObjM, dstObjG))  < dstFar) {
    if (dstObj < min (dstObjM, dstObjG)) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
    } else if (dstObjG < dstObjM) {
      dstObj = dstObjG;
      idObj = idObjG;
      ro += dstObj * rd;
      vn = GObjNf (ro);
    } else {
      dstObj = dstObjM;
      idObj = idObjM;
      ro += dstObj * rd;
      vn = dc4.yzw;
    }
    col4 = ObjCol (ro, vn);
    if ((idObj == idFlr || idObj == idStr) && vn.y > 0.) col4.rgb *= ObjAO (ro);
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
    else col = col4.rgb * (0.6 - 0.4 * dot (vn, rd));
  } else if (rd.y < 0.) {
    ro += (- ro.y / rd.y) * rd;
    s = length (ro.xz);
    if (s < (twrRad + 2. * szFlr.z + 15.) * twrScl) {
      col = vec3 (0.2, 0.3, 0.2) * (0.7 + 0.3 * Fbm2 (4. * ro.xz));
    } else {
      vn = GrndNf (0.5 * ro.xz);
      col = (0.4 * mix (vec3 (0.8, 1., 0.5), vec3 (0.7, 0.9, 0.5), 0.2 +
         0.8 * smoothstep (0.3, 0.7, Fbm2 (4. * ro.xz)))) *
         (0.2 + 0.8 * max (dot (vn, ltDir), 0.));
      col *= 0.7 + 0.3 * smoothstep (0., 0.5, s - 9.3);
      col = mix (col, 0.8 * vec3 (0.35, 0.4, 0.5), pow (1. + rd.y, 16.));
    }
    col *= 0.7 + 0.3 * smoothstep (0., 2., abs (s - (twrRad + 2. * szFlr.z + 15.) * twrScl));
  } else {
    col = SkyBgCol (ro, rd);
  }
  return clamp (col, 0., 1.);
}

#define AA  0   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr, t;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  twrScl = 0.3;
  szFlr = vec4 (14., 8.5, 4., 0.5);
  flSpc = 2. * szFlr.y - szFlr.w;
  nFlr = 4.;
  twrRad = 60.;
  bSizeV = 2. * flSpc * twrScl;
  az = 0.;
  el = -0.05 * pi;
  ro = vec3 (0., 1.9 * flSpc * nFlr * twrScl, -120.);
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    ro.y += 1.9 * 2. * flSpc * nFlr * twrScl * mPtr.y;
  } else {
    az += 0.05 * pi * tCur;
    ro.y += 1.8 * flSpc * nFlr * twrScl * (2. * SmoothBump (0.25, 0.75, 0.22,
       mod (0.022 * tCur, 1.)) - 1.);
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * ro;
  zmFac = 2.2 / (1. - 0.85 * abs (mod (az + pi, 2. * pi) - pi) / pi);
  dstFar = 150.;
  ltDir = vuMat * normalize (vec3 (0.5, 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float IFbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 10.;
  for (int j = 0; j < 3; j ++) {
    f += floor (a * Noiseff (p));
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / (1.75 * a));
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

//concentric extrusion
//2021
//do

const int steps = 250;
float eps = 0.001;
float dmin = 0.;
float dmax = 750.;
const int aa = 2;

const int octaves = 5;
float hurst = 0.5;

const int seed = 123457;

float h11(float p) {
    uvec2 n = uint(int(p)) * uvec2(uint(int(seed)),2531151992.0);
    uint h = (n.x ^ n.y) * uint(int(seed));
    return float(h) * (1./float(0xffffffffU));
}

float h21(vec2 p) {
    uvec2 n = uvec2(ivec2(p)) * uvec2(uint(int(seed)),2531151992.0);
    uint h = (n.x ^ n.y) * uint(int(seed));
    return float(h) * (1./float(0xffffffffU));
}

float n2(vec2 x) { 

    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f * f * (3.0 - 2.0 * f);  
    float n = p.x + p.y * 57.;  

    return mix(mix(h11(n+0.),h11(n+1.),f.x),
               mix(h11(n+57.),h11(n+58.),f.x),f.y);  
}

float f2(vec2 x) {

    float s = 0.;
    float h = exp2(-hurst);     
    float f = 1.;
    float a = 0.5;

    for(int i = 1; i < octaves; i++) {

        s += a * n2(f * x);
        f *= 2.;
        a *= h;
    }    

    return s;
}

mat2 rot(float a) {

    float c = cos(a);
    float s = sin(a);

    return mat2(c,-s,s,c);
}

mat3 camOrthographic(vec3 ro,vec3 ta,float r) {

     vec3 w = normalize(ta - ro); 
     vec3 p = vec3(sin(r),cos(r),0.);           
     vec3 u = normalize(cross(w,p)); 
     vec3 v = normalize(cross(u,w));

     return mat3(u,v,w); 
} 

vec2 opu(vec2 d1,vec2 d2) {
    return (d1.x < d2.x) ? d1 : d2;
} 

float extr(vec3 p,float d,float h) {
    vec2 w = vec2(d,abs(p.z) - h);
    return min(max(w.x,w.y),0.) + length(max(w,0.));
}

float sdif(float d1,float d2,float k) {
    float h = clamp(.5-.5*(d2+d1)/k,0.,1.);
    return mix(d2,-d1,h) +k*h +(1.-h);
}

vec2 scene(vec3 p) {

    vec2 res = vec2(1.,0.);

    float d = 0.;

    d = extr(p,sin(length(p.xy)*3.),1.5);
    res = opu(res,vec2(sdif(d,p.z,.5)*.5,2.));  
    return res;

}

vec2 rayScene(vec3 ro,vec3 rd) {

    float d = -1.0;
    float s = dmin;
    float e = dmax;  

    for(int i = 0; i < steps; i++) {

        vec3 p = ro + s * rd;
        vec2 dist = scene(p);

        if(abs(dist.x) < eps || e <  dist.x ) { break; }
        s += dist.x;
        d = dist.y;

        }

        if(e < s) { d = -1.0; }
        return vec2(s,d);

}

float shadow(vec3 ro,vec3 rd) {

    float res = 1.0;
    float t = 0.005;
    float ph = 1e10;

    for(int i = 0; i < 95; i++ ) {

        float h = scene(ro + rd * t  ).x;

        float y = h * h / (2. * ph);
        float d = sqrt(h*h-y*y);         
        res = min(res,125. * d/max(0.,t-y));
        ph = h;
        t += h;

        if(res < eps || t > 5.) { break; }

        }

        return clamp(res,0.0,1.0);

}

vec3 calcNormal(vec3 p) {

    vec2 e = vec2(1.0,-1.0) * eps;

    return normalize(vec3(
    vec3(e.x,e.y,e.y) * scene(p + vec3(e.x,e.y,e.y)).x +
    vec3(e.y,e.x,e.y) * scene(p + vec3(e.y,e.x,e.y)).x +
    vec3(e.y,e.y,e.x) * scene(p + vec3(e.y,e.y,e.x)).x + 
    vec3(e.x,e.x,e.x) * scene(p + vec3(e.x,e.x,e.x)).x

    ));

}

vec3 renderScene(vec3 ro,vec3 rd) {

vec2 d = rayScene(ro, rd);

vec3 col = vec3(1.);

if(d.y >= 0.) {

vec3 p = ro + rd * d.x;
vec3 n = calcNormal(p);
vec3 l = normalize(vec3(10.));

vec3 h = normalize(l - rd);
vec3 r = reflect(rd,n);

col += vec3(f2(p.xz+f2(p.yx*3.)*5.));

float amb = clamp(0.5 + 0.5 * n.y,0.,1.);

float dif = clamp(dot(n,l),0.0,1.0);

float fre = pow(clamp(1. + dot(n,rd),0.0,1.0),2.0);
float ref = smoothstep(-.2,.2,r.y);

vec3 linear = vec3(0.);

dif *= shadow(p,l);
ref *= shadow(p,r);

linear += dif * vec3(.5);
linear += amb * vec3(0.01,0.05,0.05);

col = col * linear;
col = mix(col,vec3(1.),1.-exp(-0.00001 *d.x*d.x*d.x)); 

}

return col;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord) {

vec3 color = vec3(0.);
vec3 ro = vec3(10.,10.,15.);
vec3 ta = vec3(0.0);

ro.xy *= rot(iTime*.1);

for(int k = 0; k < aa; ++k) {
    for(int l = 0; l < aa; ++l) {

    vec2 o = vec2(float(l),float(k)) / float(aa) - .5;

    vec2 uv = (2. * (fragCoord.xy + o) -
    iResolution.xy) / iResolution.y; 

    mat3 cm = camOrthographic(ro,ta,0.);
    vec3 rd = cm * normalize(vec3(uv.xy,2.));

    vec3 col = renderScene(ro,rd);    

    col = pow(col,vec3(.4545));
    color += col;
    }
}

color /= float(aa*aa);
fragColor = vec4(color,1.0);

}

precision highp float;
const float PI=3.141592;

mat2 rot(float t){
  return mat2(cos(t),-sin(t),sin(t),cos(t));
}

float sphere(vec3 p){
  p.xz=mod(p.xz, 4.)-2.;
  return length(p-vec3(0.,1.,0))-1.;
}

float plane(vec3 p){
  return p.y;
}

float map(vec3 p){
  return min(sphere(p),plane(p));
}

vec3 normal(vec3 p){
  vec2 e=vec2(1,0)*0.01;
  return normalize(vec3(
    map(p+e.xyy)-map(p-e.xyy),
    map(p+e.yxy)-map(p-e.yxy),
    map(p+e.yyx)-map(p-e.yyx)
    ));
}

// https://blog.mmacklin.com/2010/06/10/faster-fog/
float InScatter(vec3 start, vec3 dir, vec3 lightPos, float d)
{
   vec3 q = start - lightPos;
   float b = dot(dir, q);
   float c = dot(q, q);
   float s = 1.0 / max(0.001,sqrt(c - b*b));
   float x = d*s;
   float y = b*s;
   float l = s * atan(x/max(0.001,1.+(x+y)*y));
   return l;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 st = (fragCoord.xy - iResolution.xy * 0.5)/min(iResolution.x,iResolution.y);
  vec3 col;
  vec3 cam=vec3(0,4,0);
  cam.z += iTime;
  vec3 ray=cam;
  vec3 dir=normalize(vec3(st,.5));
  dir.yz *= rot(0.3);

  vec3 lightpos = vec3(0,8,-4);
  lightpos.xz *= rot(iTime);
  lightpos.z += cam.z+16.;

  float totald=0.;
  for(int i=0;i<128;i++)
  {
    float d = map(ray);
    if (d<0.001)
    {
      vec3 n=normal(ray);
      vec3 l=normalize(lightpos-ray);
      float lightDist = length(lightpos-ray);
      vec3 lightColor = vec3(1,1,1)/(lightDist*lightDist)*10.;
      col=lightColor*max(0.,dot(n,l));
      float ocr=.6;
      float oc=max(0.,1.-map(ray+l*ocr)/ocr);
      oc=exp(-2.*pow(oc,2.));
      col*=oc;
      break;
    }
    totald+=d;
    ray += dir*d;
  }

  float s = InScatter(cam, dir, lightpos,totald);
  float fog=0.01;
  col*=exp(-totald*fog);
  col+= s;
  fragColor = vec4(col,1);
}

void mainImage(out vec4 o, vec2 u){
   o += length(u= u/iResolution.x-0.25) - o;
   o *= 4. / exp(6.*fract(atan(u.xyxx, u.xxyx)/0.393 - .2/o - iTime));
}

