vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
}

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(0., 1., -1.));

    vec3 camStep = vec3(-1., 0., 0.) * (iTime+12.)/4.;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;


    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    ro.y += fbm(ro.xz,OCTAVES)-1.8;

    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));

    if (length(iMouse.xy) > 40.0) {
        rd.yx *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
        rd.xz *= rot(3.14-iMouse.x/iResolution.x*3.14*2.0);
    }


    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

#define TAU (6.283185307)
#define HEX(x) (vec3((x >> 16) & 255, (x >> 8) & 255, x & 255) / 255.)
#define STRIPECOUNT 10.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = fract(iTime / 4.);
  // Normalized pixel coordinates (from 0 to 1)
  float scale = min(iResolution.x, iResolution.y);
  vec2 uv = (gl_FragCoord.xy - 0.50 * iResolution.xy) / scale;

  float dist = length(uv);
  float angle = atan(uv.y, uv.x);
  // for calculating the parity of the stripe
  float dark = floor(mod(angle * STRIPECOUNT / TAU + 2. * time, 2.));
  // for calculating the stripes’ angle
  float stripeAngle = (dark - .5) * time * TAU + (0.5 + floor(angle * STRIPECOUNT / TAU + 2. * time) - 2. * time) * TAU / STRIPECOUNT;
  vec2 angleVec = vec2(cos(stripeAngle), sin(stripeAngle));
  // for calculating which palette to use
  float palette = step(fract(
      log(dist + 0.01) + angle / TAU + time * 2.
  ), 0.5);

  const vec3 col0L = HEX(0x840DaE);
  const vec3 col1L = HEX(0x3BCEAC);
  const vec3 col2L = HEX(0xFFD23F);
  const vec3 col3L = HEX(0xEE4266);

  const vec3 col0D = HEX(0xFFD166);
  const vec3 col1D = HEX(0xEF476F);
  const vec3 col2D = HEX(0x118AB2);
  const vec3 col3D = HEX(0x06D6A0);

  vec3 col0 = mix(col0L, col0D, palette);
  vec3 col1 = mix(col1L, col1D, palette);
  vec3 col2 = mix(col2L, col2D, palette);
  vec3 col3 = mix(col3L, col3D, palette);

  float spiral = fract(
      0.7 * log(
          abs(
              dot(uv, angleVec)
          ) + 0.02
      ) + (
          3.2 * tan((0.5 - time) * TAU * 0.4)
      ) + (
          16. * time
      )
  );

  vec3 col = mix(
      mix(
          col0, col1, step(spiral, 0.75)
      ), mix(
          col2, col3, step(spiral, 0.25)
      ), step(spiral, 0.5)
  );
  // Output to screen
  fragColor = vec4(
    col, 1.0
  );
}

#define PI 3.14159265
#define MIN_DIAM .3
#define MAX_DIAM .7

float hash21(vec2 id) {
    return fract(length(sin(id * 356.453)) * 34566.4325);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    float a = PI / 4.;
    float c = cos(a);
    float s = sin(a);

    uv *= mat2 (c, -s, s, c);
    uv *= 10.0;

    vec2 gv = fract(uv);
    vec2 id = floor(uv);
    float m = 0.;

    float nd = 1.;

    for (float y = -nd; y <= nd; y++) {
        for (float x = -nd; x <= nd; x++) {

            vec2 offset = vec2(x, y);

            float d = length(gv - offset);
            float dist = length(id + offset);

            float truc = sin(dist - iTime * 2.) * .5 + .5;

            vec2 r = vec2(
                mix(MIN_DIAM, MAX_DIAM, truc)
            );
            r.y -= .00009;

            m += smoothstep(r.x, r.y, d) * .3;
        }
    }

    vec3 col = vec3(m, m*.6, m*.5);
    // col += mod(m, 2.);
    // col.gb += id / 8.;
    // col.r += length(uv);
    fragColor = vec4(col, 1.0);
}

