// rotation matrix
mat2 rot(float a) { return mat2(cos(a),-sin(a),sin(a),cos(a)); }

// global variable
float material;

// sdf
float map (vec3 p)
{
    // time
    float t = iTime * 0.1;

    // rotation parameter
    vec3 angle = vec3(78.,68.,78.) + t + p;

    // kif
    const int count = 5;
    float a = 1.0;
    float scene = 1000.;
    float shape = 1000.;
    for (int index = 0; index < count; ++index)
    {
        // fold
        p = abs(p)-.5*a;

        // rotate
        p.xz *= rot(angle.y/a);
        p.yz *= rot(angle.x/a);
        p.yx *= rot(angle.z/a);

        // sphere
        shape = length(p)-.6*a;

        // materialing
        material = shape < scene ? float(index) : material;

        // add
        scene = min(scene, shape);

        // falloff
        a /= 1.89;
    }

    // shell
    scene = abs(scene);

    // surface details
    float d = length(p);
    float details = abs(sin(d*20.))*0.05;
    scene -= details;

    return scene;
}

// return color from pixel coordinate
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // reset color
    fragColor = vec4(0);
    material = 0.0;

    // camera coordinates
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;
    vec3 eye = vec3(0,0,-1);
    vec2 mouse = iMouse.xy / iResolution.xy;
    eye.xz *= rot(0.4+mouse.x);
    eye.xy *= rot(0.6-mouse.y);
    vec3 z = normalize(-eye);
    vec3 x = normalize(cross(z, vec3(0,1,0)));
    vec3 y = normalize(cross(x, z));
    vec3 ray = normalize(vec3(z * 0.5 + uv.x * x + uv.y * y));
    vec3 pos = eye + ray * .5;

    // raymarch
    const int steps = 20;
    for (int index = steps; index > 0; --index)
    {
        // volume estimation
        float dist = map(pos);
        if (dist < 0.01)
        {
            // Inigo Quilez color palette (https://iquilezles.org/www/articles/palettes/palettes.htm)
            vec3 tint = vec3(0.2)+vec3(0.8)*cos(vec3(1,2,3)*material*0.2-length(pos)*2.);

            // pixel color
            float shade = float(index)/float(steps);
            fragColor.rgb = tint * shade;

            break;
        }

        // raymarch
        pos += ray * dist;
    }
}

float map(vec3 pos) {
    vec4 sphere = vec4(0.0, 0.0, 2.0, 0.75);
    return distance(pos, sphere.xyz) - sphere.w;
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // center coordinates
    vec2 uv = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    // setup camera
    vec3 camera_origin = vec3(0.0, 1.0, 0.0);
    vec3 look_at_point = vec3(0.0, 0.0, 2.0);
    float distance_to_image_plane = 1.5;

    // camera coordinate frame
    vec3 gaze = look_at_point - camera_origin;
    vec3 ww = normalize(gaze);
    vec3 uu = normalize(cross(vec3(0, 1.0, 0), ww));
    vec3 vv = normalize(cross(ww, uu));



    // ray direction
    vec3 ray_direction = normalize(uv.x * uu + uv.y * vv + ww * distance_to_image_plane);

    // ray march
    float ray_march_distance = 0.0;
    int max_ray_march_iterations = 256;
    float min_distance = 0.001;
    float max_distance = 500.0;
    for (int i = 0; i < max_ray_march_iterations; i++) {
        vec3 p = ray_march_distance * ray_direction + camera_origin;
        float distance_to_scene = map(p);
        ray_march_distance += distance_to_scene;
        if (distance_to_scene < min_distance || ray_march_distance > max_distance)
            break;

    }

    // Black background
    vec3 col = vec3(0.0);


    // lamp
    if (ray_march_distance < max_distance) {
        vec4 lamp = vec4(cos(iTime), 4., sin(iTime), 1);
        vec3 p = ray_march_distance * ray_direction + camera_origin;
        vec3 n = calcNormal(p);
        float illum = clamp(dot(n, normalize(lamp.xyz - p)), 0.0, 1.0) * lamp.w;
        //illum = sqrt(illum);
        col += illum;
    }



    // Output to screen
    fragColor = vec4(col,1.0);
}

