vec3 l = vec3(1.);

//Yonatan clouds/mountains combined field
vec2 field(in vec3 p) {

	float s=2.,e,f,o;
    vec2 p2;

	for(e=f=p.y;s<8.;s*=-1.45)
            p2 = sin((p.xz+vec2(p2.y,p2.x+s))*s*.5)/s,
            //p.xz*=rot(s),
            e+=abs(dot((sin((p-vec3(0,0,iTime))*s)/s),.4*l)),
            f+=abs(dot(p2,l.xz)),
            p.xz += p2;
	o = 1.+ (f>.001?e:-exp(-f*f));
    return vec2(max(o,0.),min(f,max(e,.07)));
}


vec3 raycast( in vec3 ro, vec3 rd )
{
    float t = 2.5;
    float dt = .035;
    vec3 col= vec3(0.);
    for( int i=0; i<100; i++ )
	{                
        vec2 v = field(ro+t*rd);  
        float c=v.x, f=v.y;
        t+=dt*f;
        dt *= 1.03;
        col = .95*col+ .09*vec3(c*c*c, c*c, c);	
    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;


    // camera

    vec3 ro = vec3(2.);

    ro.yz*=rot(-1.5); 
    ro.y +=3.;
    ro.xz*=rot(0.1*t);

    vec3 ta = vec3( 0.0 , 0.0, 0.0 );
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 rd = normalize( p.x*uu + p.y*vv + 4.0*ww );
    ro.x -=t*.4;

	// raymarch 

    vec3 col = raycast(ro,rd);


	// shade

    col =  .5 *(log(1.+col));
    col = clamp(col,0.,1.);
    fragColor = vec4( col, 1.0 );

}

precision mediump float;

mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float i,e,f,s,g,k=.01;    
    float o;    
    o++;    
    for(int i=0;i<100;i++)
    {
      s=2.;      
      g+=min(f,max(.03,e))*.3;      
      vec3 p=vec3((fragCoord.xy-iResolution.xy/s)/iResolution.y*g,g-s);
      p.yz*=rotate2D(-.8);
      p.y*=2.5;
      p.z+=iTime*1.3;
      e=p.y;
      f=p.y;
      for(;s<50.;)
      {
        s/=.66;
        p.xz*=rotate2D(s);
        e+=abs(dot(sin(p*s)/s,p-p+0.6));
        f+=abs(dot(sin(p.xz*s*.33+(iTime*0.5))/s,iResolution.xy/iResolution.xy));
      }

      if(f>k*k)
        o+=e*o*k;
      else
        o+=-exp(-f*f)*o*k;


    }
    fragColor = vec4(o*vec4(0.33,0.7,0.85,1.0));
}

// Tumble by Kristian Sivonen (ruojake)
// CC BY-SA 4.0 (https://creativecommons.org/licenses/by-sa/4.0/)

#define sat(x) (clamp((x), 0., 1.))

float hash12(vec2 p)
{
	return fract(sin(dot(p, vec2(1.433, 1.512))) * 4313.441);
}

float hash13(vec3 p)
{
	return fract(sin(dot(p, vec3(1.433, 1.512, 2.421))) * 4313.441);
}

mat2 rot(float a)
{
    float s = sin(a),
          c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

float noise(vec2 p)
{
	vec2 i = floor(p);
    vec2 f = smoothstep(0.,1.,p-i);
    const vec2 o = vec2(1.,0.);

    return mix(mix(hash12(i),hash12(i+o),f.x),
               mix(hash12(i+o.yx),hash12(i+1.),f.x),
               f.y);
}

float noise3(vec3 p)
{
	vec3 i = floor(p);
    vec3 f = smoothstep(0.,1.,p-i);
    const vec2 o = vec2(1.,0.);

    return mix(
               mix(mix(hash13(i),hash13(i+o.xyy),f.x),
                   mix(hash13(i+o.yxy),hash13(i+o.xxy),f.x),
                   f.y),
               mix(mix(hash13(i+o.yyx),hash13(i+o.xyx),f.x),
                   mix(hash13(i+o.yxx),hash13(i+1.),f.x),
                   f.y),
               f.z);
}

float noiseBiplanar(vec3 n, float scale)
{
    vec2 uv0, uv1;
    if(n.z < n.x && n.z < n.y)
    {
        n = n.zxy;
    }
    else if (n.y < n.x && n.y < n.z)
    {
        n = n.yxz;
    }
    uv0 = n.xz;
    uv1 = n.xy;
    float t = smoothstep(.025, .975, n.z / (n.z + n.y));
    return mix(noise(uv0 * scale + scale * 2.), noise(uv1 * scale + scale * 2.), t);
}

float fur(vec3 p)
{
    float len = length(p);
    p = normalize(p);
    float thickness = noiseBiplanar(p, 140.);
    return len - 2. + (thickness * len) * .25;
}

vec3 spin(vec3 p, float d)
{
    float t = iTime * .5 - d * d * .125;
    p.xz *= rot(sin(t * 2.));
    p.zy *= rot(sin(t * 1.5));
    return p;
}

float scene(vec3 p)
{
    float d = length(p);
    if(d <= 2.)
    {
        return fur(spin(p, d));
    }
    return d;
}

vec3 normal(vec3 p, float d)
{
    vec2 e = vec2(.0001, 0);
    return normalize(d - vec3(
        scene(p - e.xyy),
        scene(p - e.yxy),
        scene(p - e.yyx)));
}

float shadow(vec3 ro, vec3 rd, float maxDist)
{
    float res = 1.;
    float d = 0.;
    float t = .01;
    for(int i = 0; i < 30 && t < maxDist; ++i)
    {
        d = scene(ro + rd * t);
        t += d;
    	if(abs(d) < .00001)
            return 0.;
    }
    return 1.;
}

vec3 color(vec3 p)
{
    float d = length(p);
    p = normalize(p);

    float a = noiseBiplanar(spin(p, d).xyz, 20.);
    vec3 c = vec3(1., .01, .8) * (a * .8 + .2);
    return erot(c, normalize(vec3(1)), fract(a * 11.) * 2. - 1.);
}

void img( inout vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-iResolution.xy * .5)/iResolution.y;

    vec3 rd = normalize(vec3(uv, 1.));
    vec3 ro = vec3(0, 0, -4);
    vec3 p;

    float d, t = 2.;
    float i = 0.;
    // shorten the raymarching step at grazing angles
    float tangentBias = 1. / (1. + smoothstep(.25, .5, length(uv)) * .75);
    for(; i < 1. && t < 6.; i += 1./32.)
    {
        p = ro + rd * t;
        d = scene(p);
        t += d * tangentBias * sat(length(p) * .5);
        if(abs(d) < .0001)
            break;
    }

    vec3 col = vec3(.025, .025, .035);
    if (t < 6.)
    {

        vec3 n = normal(p, d);
        vec3 dirL = normalize(vec3(1, 1, -2));
        n.xz *= rot(iTime * .4);
        float l = dot(max(n, 0.), vec3(.4,.5,.3));
        l *= shadow(p + n * .01, vec3(0,1,0), 10.);
        col = color(p);
        float lum = dot(col, vec3(.2,.7,.1));
        col = mix(col * l + vec3(0.,.001,.002), vec3(1., .98, .95) * l, l*l*(.5 + lum));
        float ao = (1. - max(i - l*l, 0.) * .8);
        col *= ao;
    }

    fragColor += vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(0);
    img(fragColor, fragCoord);
    vec2 o = vec2(.03125, .015625);
    for(float i = 0.; i < 4.; i++)
    {
        img(fragColor, fragCoord + o);
        o = vec2(o.y,-o.x);
    }
    fragColor /= 5.;

    fragColor.rgb = pow(fragColor.rgb, vec3(1./2.2));
    float vignette = length((fragCoord - iResolution.xy * .5) / iResolution.xy) * 2.;
    fragColor.rgb *= 1. - vignette * vignette * vignette * .15;
    fragColor.rgb += noise3(vec3(fragCoord, iFrame)) * .01 - .005;
}

const int deg = 3;
vec2 roots[deg];

vec2 mul(vec2 a, vec2 b) {
    return vec2(
        a.x*b.x - a.y*b.y,
        a.x*b.y + a.y*b.y
    );
}

vec2 div(vec2 a, vec2 b) {
    return mul(a, vec2(b.x, -b.y))/(b.x*b.x+b.y*b.y);
}

vec2 inv(vec2 a) {
    return vec2(a.x, -a.y) / (a.x*a.x + a.y*a.y);
}

vec2 f(vec2 a) {
    vec2 ret = vec2(1.0, 0.0);
    for (int i = 0; i < deg; i++) {
        ret = mul(ret, a-roots[i]);
    }
    return ret;
}

vec2 fp(vec2 a) {
    vec2 sum = vec2(0.0, 0.0);
    for (int i = 0; i < deg; i++) {
        sum += inv(a-roots[i]);
    }
    return inv(sum);
}

vec4 col(vec2 a) {
    return vec4(1.0/(1.0+0.1*abs(a.x)), 1.0/(1.0+0.21*abs(a.y)), 0.0, 1.0);
}

