#define no_roots 4
#define anti_alias 1
#define no_iterations 15

vec2 complex_reciprocal(vec2 z){ //computes 1/z
    float div = z.x * z.x + z.y * z.y;
    float div_r = 1.0f / div;
    vec2 result = vec2(z.x * div_r, -z.y * div_r);
    return result;
}

/*
 * Newton-rhapsody based root finding:
 * x_n+1 = x_n - f(x)/f'(x)
 * 
 * interestingly the reciprocal of f(x)/f'(x) is the logarithmic derivative:
 * d/dx ln( f(x) ) = f'(x) / f(x)
 * 
 * If we think of the polynomial in terms of its roots we have:
 * 
 * P(x) = (x - x1) * (x - x2) * ... * (x - xn)
 * thus d/dx ln( P(x) ) = d/dx ln[ (x - x1) * (x - x2) * ... * (x - xn) ]
 * = d/dx [log(x - x1) + log(x - x2) + ... + log(x - xn)]
 * = 1/(x - x1) + 1/(x - x2) + ... + 1/(x - xn)
 * 
 * which means that the reciprocal of this sum is equal to f(x)/f'(x)
 * So we can compute the newton iteration step directly from the zeros of our polynomial.
 * 
 */
vec2 newton_iteration(vec2 z, const vec2 roots[no_roots]){
    vec2 sum = vec2(0.0f);
    for(int i = 0; i < no_roots; i++){
        vec2 delta = vec2(z.x - roots[i].x, z.y - roots[i].y); //calculate x - x0
        float div = delta.x * delta.x + delta.y * delta.y;     //calculate the divisior for calculating the complex reciprocal
        if(div < 0.0001f){                                     //if the divisor is close to zero, we've already found a root.
            return z;
        }
        float div_r = 1.0f/div;
        vec2 complex_reciprocal_delta = vec2(delta.x * div_r, -delta.y * div_r); //calculate the complex reciprocal
        sum += complex_reciprocal_delta;                      //add the complex reciprocal to our sum
    }
    vec2 result = complex_reciprocal(sum);                    //calculate the complex reciprocal of the sum
    return z - result;                                        //do the newton "step"
}

int find_closest_root(vec2 z, const vec2 roots[no_roots]){
    int index = 0;
    float dist = 10000.0f;
    for(int i = 0; i < no_roots; i++){
        float dx = (z.x - roots[i].x);
        float dy = (z.y - roots[i].y);
        float d = dx * dx + dy * dy;
        if(d < dist){
            dist = d;
            index = i;
        }
    }
    return index;
}

vec2 calculate_coord(vec2 c){
    vec2 normalized_coord = c/iResolution.xy;
    float t = iTime;
    normalized_coord -= vec2(0.5f) + vec2(sin(t*0.1f)*0.3f, cos(t*0.1f)*0.3f);
    normalized_coord *= 3.0 - cos(t*0.2)*2.0f;
    return normalized_coord;
}

vec3 sample_no_AA(vec2 c, vec2 roots[no_roots], vec3 colors[no_roots]){
    vec2 z1 = calculate_coord(c);
    for(int i = 0; i < no_iterations; i++){
        z1 = newton_iteration(z1, roots);
    }
    int index1 = find_closest_root(z1, roots);

    vec3 col = colors[index1];
    return col;
}

vec3 sample_AA(vec2 c, vec2 roots[no_roots], vec3 colors[no_roots]){
    vec2 z1 = calculate_coord(c + vec2( 0.114f, -0.377f));
    vec2 z2 = calculate_coord(c + vec2( 0.351f,-0.105f));
    vec2 z3 = calculate_coord(c + vec2(-0.123f,0.342f));
    vec2 z4 = calculate_coord(c + vec2(-0.360f, -0.140f));
    for(int i = 0; i < no_iterations; i++){
        z1 = newton_iteration(z1, roots);
        z2 = newton_iteration(z2, roots);
        z3 = newton_iteration(z3, roots);
        z4 = newton_iteration(z4, roots);
    }
    int index1 = find_closest_root(z1, roots);
    int index2 = find_closest_root(z2, roots);
    int index3 = find_closest_root(z3, roots);
    int index4 = find_closest_root(z4, roots);

    vec3 col = colors[index1] + colors[index2] + colors[index3] + colors[index4];
    col /= 4.0f;
    return col;
}

