float _LightPower = 1.3;

#define PI 3.14159265

//RGB To HSV
vec3 RGBToHSV(vec3 col)
{                
    vec4 k = vec4(0., -1./3., 2./3., -1.);
    vec4 p = mix(vec4(col.bg, k.wz), vec4(col.gb, k.xy), step(col.b, col.g));
    vec4 q = mix(vec4(p.xyw, col.r), vec4(col.r, p.yzx), step(p.x, col.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    float hue = abs(q.z + (q.w - q.y)/(6. * d + e));
    float sat = d / (q.x + e);
    float val = q.x;
    return vec3(hue, sat, val);
}

//https://www.shadertoy.com/view/MsS3Wc
//HSV To RGB
vec3 HSVToRGB(float hue, float saturation, float value)
{
    vec3 rgb = clamp(abs(mod(hue * 6. + vec3(0.,4.,2.),6.)-3.)-1., 0., 1.);
    return value * mix(vec3(1.), rgb, saturation);
}

vec2 ToPolarCoord(vec2 pos)
{
    pos = pos - 0.5;
    float len = length(pos);
    float a = acos(pos.x/len) * pos.y / abs(pos.y) / PI / 2.;
    return vec2(a * _Num + sin(len * _TwistFactor), len * _Value);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 1.;
    vec2 uv = fragCoord/iResolution.xy;

    uv = ToPolarCoord(uv);   
    uv.x = uv.x + iTime * _Speed;
    uv.y = pow(uv.y, _LightPower);

    vec3 rgb = HSVToRGB(uv.x, uv.y, 1.);

    fragColor = vec4(rgb,1.0);
}

#define NUM_OCTAVES 5

vec2 random2(vec2 st) {
	st = vec2(dot(st, vec2(127.1, 311.7)),
		dot(st, vec2(269.5, 183.3)));
	return -1.0 + 2.0 * fract(sin(st) * 43758.5453123 * 0.3897);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st) {
	vec2 i = floor(st);
	vec2 f = fract(st);

	vec2 u = f * f * (3.0 - 2.0 * f);

	return mix(mix(dot(random2(i + vec2(0.0, 0.0)), f - vec2(0.0, 0.0)),
		dot(random2(i + vec2(1.0, 0.0)), f - vec2(1.0, 0.0)), u.x),
		mix(dot(random2(i + vec2(0.0, 1.0)), f - vec2(0.0, 1.0)),
			dot(random2(i + vec2(1.0, 1.0)), f - vec2(1.0, 1.0)), u.x), u.y);
}

float fbm(vec2 x) {
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
	// Rotate to reduce axial bias
	mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

mat2 rotate(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 mouse = iMouse.xy/iResolution.x * 8.0;

    if (mouse.x <= 0.0) mouse.x = 4.0;

    float zoom = mouse.x;
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
    vec2 gv = fract(uv+0.5) - random2(uv);
    uv *= zoom;
    float dist = length(uv);
    float angle = atan(uv.y, uv.x);
    float c = fbm(vec2(cos(angle+iTime), sin(angle+dist)) / 2.0) * 2.0;
    float f = (1.0 - sqrt(1.0 - dist)) / dist;

    vec3 col = vec3(0.0);

    vec2 nuv = uv;
    nuv *= rotate(iTime);

    // Neutron star
    col += vec3(0.7, 0.9, 1.0) * smoothstep(0.17, 0.15, dist);
    col += max((fbm(nuv*f*250.0)*2.0+0.5) * smoothstep(0.16, 0.15, dist) * vec3(0.7, 0.9, 1.0), 0.0);

    // Flaring
    col += vec3(smoothstep(1.0, c, dist)) * vec3(0.7, 0.9, 1.0) * smoothstep(0.15, 0.16, dist);

    // Spikes
    col += vec3(0.1 / abs(uv.y)) * smoothstep(2.0, -5.0, dist) * vec3(0.7, 0.9, 1.0) * smoothstep(1.0, 10.0, zoom);

    // Glow
    col += vec3(smoothstep(1.1, 0.0, dist / 4.0) / 3.0) * vec3(0.7, 0.9, 1.0) * smoothstep(0.15, 0.16, dist);

    // Stars
    col += vec3(0.01 / length(gv));

    // Output to screen
    fragColor = vec4(col,1.0);
}

