float pi = acos(-1.);
vec3 ppp;
vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;
    vec3 op = p;
    p.xz *= rot(sin(p.y/4.));
    p.xz = abs(p.xz)-3.1;
    p.xz *= rot(p.y/2.);
    p.xz = abs(p.xz)-1.;
    p.xz *= rot(sin(p.y));
    vec3 pp = p;
    p.xz *= rot(p.y * pi/3. + iTime);
    p.y = sin(p.y * pi/3. + iTime);
    o = length(vec2(length(p.xy)-1.,p.z))-.3;
 	pp.z += sin(pp.y * pi/3. + iTime)*1.;
    ppp = pp;
    //o = min(o,length(pp.xz)-.6);
    float r = sin(iTime + pp.y/3.) * .5 + .5;
    float d = length(pp.xz)-r*.7;
    if(o > d)
    {
        o = d;
    	id = 1.;
        ppp = pp;
    }

    o = mix(max(length(op)-6.,o),o,.9);
    //o = (length(op)-3.)/o;
    o/=1.7;

    return vec2(o,id);
}

vec2 march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i< 99 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.01)
        {
            return vec2(depth,d.y);
        }
        if(depth > 30.)break;
        depth += d.x;
    }
    return vec2(-depth , 0.);

}
