float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle( vec2 p, float r )
{ return length(p) - r;}

float sdTriangleIsosceles( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}


vec4 construccion(vec2 uv,vec3 col)
{

    float d2= sdTriangleIsosceles(vec2(uv.x, -uv.y)-vec2(-14.0,0.0), vec2(2.0,3.0) );
    float d3=sdBox(uv-vec2(0.0,-7.0), vec2(15.0,2.0) );
    float d4=sdBox(uv-vec2(11.0,-3.0), vec2(3.0,2.0) );
    float d5=sdBox(uv-vec2(-9.5,-4.0), vec2(5.0,1.0) );
    float d6= sdTriangleIsosceles(vec2(uv.x, -uv.y)-vec2(-4.5,3.0), vec2(2.0,3.0) );

    float dtot=unionSDF(d2, d3);
    dtot=unionSDF(dtot, d4);
    dtot=unionSDF(dtot, d5);
    dtot=unionSDF(dtot, d6);

    col= DrawFig(vec3(1.0), col, dtot );
    return  vec4(col,dtot);
}

vec3 paisajeLineas(vec2 p, vec3 col)
{   vec2 uv=p;
    p.y=opRep1D(p.y, 0.025 );
    float d1= sdBox(p, vec2(16.0,0.0032) );
