const float DROPSPEED = 6.;
const vec2 DROPSPACING = vec2(.03, .04);
const float DROPSIZE = .015;
const float PERIOD = 1./DROPSPEED;
const float STROBEDUR = .1*PERIOD;
const vec3 DROPCOLOR = vec3(.5,.8,1);

float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

// Put any binary function in here which can change over time
bool getImage(vec2 p, float t) {
    float s = .2*sin(6.28*t)+1.;
    return sdCircle(p*vec2(s,1./s), .3) < 0.;
}

struct Coords {
    vec2 local;
    vec2 global;
};

Coords getDropCoords(vec2 p, float t, float t2) {
    vec2 local = mod(p + vec2(0, t*DROPSPEED), DROPSPACING) - .5*DROPSPACING;
    local += vec2(0, (t2-t)*DROPSPEED);
    vec2 global = p - local;
    return Coords(local, global);
}

float getDropsDist(vec2 p) {
    float prevStrobeTime = floor(iTime/PERIOD)*PERIOD;
    float nextStrobeTime = prevStrobeTime + PERIOD;
    vec2 local = getDropCoords(p, iTime, iTime).local;
    vec2 prevStrobePos = getDropCoords(p, iTime, prevStrobeTime).global;
    vec2 nextStrobePos = getDropCoords(p, iTime, nextStrobeTime).global;
    bool prevStrobeInImage = getImage(prevStrobePos, prevStrobeTime);
    bool nextStrobeInImage = getImage(nextStrobePos, nextStrobeTime);
    bool dropInImage = prevStrobeInImage || nextStrobeInImage;
    float dropDist = sdCircle(local, DROPSIZE/2.);
    return dropInImage ? dropDist : 1.;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 drop = smoothstep(.005, -.005, getDropsDist(uv)) * DROPCOLOR;
    float strobe = step(mod(iTime+.5*STROBEDUR, PERIOD), STROBEDUR);
    float lighting = mix(.2, 1., strobe);

    vec3 col = drop * lighting;
    // Uncomment to view image directly
    //col = float(getImage(uv, iTime)) * DROPCOLOR;

    fragColor = vec4(col, 1.0);
}

