// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Slice(p,a) dot(p,vec2(cos(a),sin(a)))
#define S(p,d,b) smoothstep(fwidth(p.y),0.0,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define MATERIAL_OUTSOLE 0
#define MATERIAL_MIDSOLE1 1
#define MATERIAL_MIDSOLE2 2
#define MATERIAL_UPPER 3

#define ZERO (min(iFrame,0))

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

// IQ's 3D noise function. It's faster than the FBM and enough for me to design.
// The compile is also faster.
float noise3d( vec3 p )
{
	const vec3 s = vec3(27, 111, 57);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); 
    //p *= p*p*(p*(p*6. - 15.) + 10.); // option
    h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float sdSimpleCylinder(vec3 p, float r, float h){
    float d = length(p.xz)-r;
    d = max(abs(p.y)-h,d);
    return d;
}

float baseOutsole(vec3 p, float h){
    vec3 prevP = p;

    p.xz*=Rot(radians(17.0));
    p.z*=0.4;
    p.x*=0.57;
    p.x+=0.03;
    p.z-=0.07;

    float d =sdSimpleCylinder(p,0.1,h);

    p = prevP;
    p.xy*=Rot(radians(90.0));
    p.y*=1.5;
    p.z*=1.2;
    float d2 = sdCappedCylinder(p-vec3(0.0,0.0,-0.25),h,0.2);
    d = opSmoothUnion(d, d2,0.13);

    p = prevP;
    d = max(abs(p.y)-h,d);

    return (d)*0.6;
}

float outsole(vec3 p){
    vec3 prevP = p;
    float d = baseOutsole(p,0.02);

    // herringbone traction
    p.xz*=2.3;
    p.xz = mod(p.xz,0.1)-0.05;
    p.x=abs(p.x)-0.03;
    p.xz*=Rot(radians(45.0));
    float d2 = sdBox(p,vec3(0.01,0.02,0.06))*0.6;

    d = max(d,d2);
    p = prevP;
    d2 = baseOutsole(p-vec3(0.0,0.01,0.0),0.01);
    d = min(d,d2);

    return d;
}

vec2 midsole(vec3 p){
    float d = baseOutsole(p-vec3(0.0,0.06,0.0),0.04);

    p.yz*=Rot(radians(-3.0));
    float mask = p.y-0.07;
    d = max(mask,d);

    vec2 model = vec2(d,MATERIAL_MIDSOLE1);

    return model;
}

float upperBase(vec3 p){
    vec3 prevP = p;

    p.x*=1.2;
    p.z*=0.8;

    float d =sdCappedCone(p-vec3(0.0,0.18,-0.15),0.13,0.15,0.09);

    p = prevP;
    p.xz*=Rot(radians(15.0));
    p.yz*=Rot(radians(-3.0));
    p.x*=1.2;
    p.z*=0.8;

    float d2 = sdEllipsoid(p-vec3(-0.05,0.1,0.13),vec3(0.2,0.08,0.2));
    d = opSmoothUnion(d,d2,0.1);

    p = prevP;
    p.xz*=Rot(radians(15.0));
    p.x*=1.2;
    p.z*=0.8;
    p.yz*=Rot(radians(-35.0));
    d2 = sdEllipsoid(p-vec3(0.0,0.21,-0.1),vec3(0.1,0.04,0.12));
    d = opSmoothUnion(d,d2,0.15);

    p = prevP;
    p.yz*=Rot(radians(-15.0));
    d2 = sdBox(p-vec3(0.0,0.14,-0.378),vec3(0.02,0.11,0.0005))-0.005;
    d = opSmoothUnion(d,d2,0.03);

    p = prevP;
    p.yz*=Rot(radians(-3.0));
    d2 = baseOutsole(p-vec3(0.0,0.08,0.0),0.01);
    d = opSmoothUnion(d,d2,0.015);

    return d;
}

float upper(vec3 p){
    vec3 prevP = p;
    float d = upperBase(p);
    p.x*=1.4;


    float d2 = upperBase(p-vec3(0.0,-0.02,0.0));
    d = max(-d2,d);

    p = prevP;

    p.x*=1.8;
    p.z*=0.9;

    d2 =sdCappedCone(p-vec3(0.0,0.3,-0.14),0.13,0.15,0.09)*0.6;
    d = max(-d2,d);

    return d*0.8;
}



vec2 GetDist(vec3 p) {
    p+=noise3d(p*300.0)*0.001;
    vec3 prevP = p;

    p.y+=0.075;
   // p.z+=0.1;
    float d = outsole(p);

    vec2 model = vec2(d,MATERIAL_OUTSOLE);

    vec2 model2 = midsole(p);


    d = upper(p);

    vec2 model3 =vec2(d,MATERIAL_UPPER);

    return combine(model,combine(model2,model3));

}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,2,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

vec3 upperTex(vec2 uv, vec3 col){
        vec2 prevUV = uv;
        uv = mod(uv,0.08)-0.04;
        uv.y-=0.03;
        uv.x=abs(uv.x)-0.05;
        uv*=Rot(radians(45.0));

        float d = B(uv,vec2(0.01,0.08));
        col = mix(col,vec3(0.2,0.3,0.5),S(prevUV,d,0.0));
        return col;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    float nn = noise3d(p*160.0)*0.05;
     float nn2 = noise3d(p*20.0)*0.2;
    if(mat == MATERIAL_OUTSOLE){
        col = diffuseMaterial(n,rd,p,vec3(1.0,1.0,0.9))+nn;
    } else if(mat == MATERIAL_MIDSOLE1){
        col = diffuseMaterial(n,rd,p,vec3(0.4,0.3,0.5))+nn;
    } else if(mat == MATERIAL_MIDSOLE2){
        col = diffuseMaterial(n,rd,p,vec3(0.6,0.6,0.6))+nn;
    } else if(mat == MATERIAL_UPPER){

        col = diffuseMaterial(n,rd,p,vec3(0.3,0.5,0.6)-upperTex(p.xz,col)+nn2+nn2);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
