///----------------------------------------------------------------------------:

///----------------------------------|
/// Координаты(верхнего левого угла) | 
/// квадрата и его размер.           |
/// (уже нормированные)              |
///----------------------------------:
vec3 Q1 = vec3(0.2 , 0.5, 0.3);
vec3 Q2 = vec3(0.51, 0.5, 0.2);
vec3 Q3 = vec3(0.72, 0.5, 0.1);
///----------------------------------.

const vec4  RED = vec4(0.6, 0. , 0. , 1.);
const float PI  = 3.14;

vec4 tonsin(float minn, float maxx, float a)
{   a = a - minn;
    float x = PI / (maxx - minn);
    float c  = 0.8  * sin(a * x)+0.1;
    return vec4(0.1, c, 0.1, 0.9);
}

///----------------------------------|
/// Пиксели фигуры.                  |
///----------------------------------:
vec4 colorquad(vec2 pos, vec3 q)
{   float k = iResolution.y / iResolution.x;

    if(     q.x < pos.x && pos.x < (q.x+q.z) &&
            q.y < pos.y && pos.y < (q.y+q.z*k)  )
    {   
        return tonsin(q.x, q.x+q.z, pos.x);
    }
    return vec4(0.0);
}

vec2 get_center(vec3 q)
{   return vec2(q[0] + q.z / 2., q[1]+q[2]/3.56);
}

///----------------------------------|
/// Обводка.                         |
///----------------------------------:
vec4 colorborder(vec2 pos, vec3 q)
{   
    vec4 t = colorquad(pos, q);
    if(t.a > 0.) return t;

    float k = iResolution.y / iResolution.x;

    vec2 center = get_center(q);
    vec2 skyle  = (center - pos) * 0.9;

    pos = skyle + center;

    if(     q.x < pos.x && pos.x < (q.x+q.z) &&
            q.y < pos.y && pos.y < (q.y+q.z*k)  )
    {   
        return RED;
    }
    return vec4(0.0);
}

