void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord/iResolution.xy;
         uv = vec2(1.- uv.y+cos(iTime)*0.1, uv.x+sin(iTime)*0.1);

    fragColor   = colorborder(uv, Q1)
                + colorborder(uv, Q2)
                + colorborder(uv, Q3);

}

bool IsInLosange(vec3 o, vec3 p, float r)
    {
        if((abs(o.x - p.x) + abs(o.y - p.y) + abs(o.z - p.z))*0.333333<r )
            return true;
        else
            return false;
    }

bool IsInCircle(vec3 o, vec3 p, float r)
    {
        if(sqrt((o.x-p.x)*(o.x-p.x) + (o.y-p.y)*(o.y-p.y))<r)
            return true;
        else
            return false;
    }

bool IsInBedo(vec3 o, vec3 p, float r)
    {
        if(abs(o.x - p.x)<r && abs(o.y - p.y)<r/2.0 && abs(o.z - p.z)<r)
            return true;
        else
            return false;
    } 




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0,0,0);

    vec3 o = vec3(0.5,0.5,0.3);

    vec3 eye1= vec3(sin(iTime + 0.5)*0.1+0.3,+sin(iTime +0.25+1.57)*0.1+0.6,0.3);

    vec3 eye2= vec3(sin(iTime + 0.5)*0.1+0.7,+sin(iTime +0.25+1.57)*0.1+0.6,0.3);

    float r = 0.1;

    if(IsInLosange(o, vec3(uv.xy,0.0),0.3))
        col = 0.5 + 0.5*cos(iTime + uv.xyx+vec3(0,2,4));

    if(IsInCircle(vec3(0.3,0.6,0.5), vec3(uv.xy,0.0),0.15))
        col = 0.5 + 0.5*sin(iTime + uv.xyx+vec3(0,2,4));

    if(IsInCircle(vec3(0.7,0.6,0.5), vec3(uv.xy,0.0),0.15))
        col = 0.5 + 0.5*sin(iTime + uv.xyx+vec3(0,2,4));

    if(IsInCircle(vec3(0.5,0.2,0.5), vec3(uv.xy,0.0),0.2))
        col = 0.5 + 0.5*sin(iTime + uv.xyx+vec3(0,2,4));

    if(IsInCircle(eye1, vec3(uv.xy,0.0),0.05))
        col = vec3(1,1,1);

    if(IsInCircle(eye2, vec3(uv.xy,0.0),0.05))
        col = vec3(1,1,1);


    if(IsInBedo(vec3(0.3,0.2,0.0), vec3(uv.xy,0.0),r))
        col = vec3(0.4,0.15,0.15);

    if(IsInBedo(vec3(0.2,0.2,0.0), vec3(uv.xy,0.0),r))
        col = vec3(1,1,1);

    // Output to screen
    fragColor = vec4(col,1.0);

}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    float a = atan(uv.y, uv.x);
    float r = length(uv);

    // uncomment me
    // r = length(uv) * (1. + min(abs(1./uv.x), abs(1./uv.y)));
    // r *= r;

          uv = vec2(cos(1. * r + a + iTime), sin(2. * r + a - iTime));
    vec2 uv2 = vec2(cos(2. * r + a - iTime), sin(4. * r + a + iTime));

    float b = .5 + .5 * cos(0.8 * iTime);   
    uv = mix(uv, uv2, b);

    float val = smoothstep(.26, .34, (.3 + .05 * cos(iTime)) * length(cos(2. * uv)));
    val *= 0.95 + h21(uv);

    fragColor = vec4(val);
}

#define pi 3.1415926535
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

#define STEPS 200
#define MDIST 100.0

//some neat animation
//#define FUN_MODE

//Reverse time
//#define iTime -iTime

//Change to 2.0 for AA
#define AA 1.0

vec3 rdg = vec3(0);
float ext(vec3 p, float s, float h){
  vec2 b = vec2(s,abs(p.y)-h);
  return min(max(b.x,b.y),0.)+length(max(b,0.));
}
float h11(float a) {
    a+=0.65343;
    return fract(fract(a*a*12.9898)*43758.5453123);
}
float diplane(vec3 p,vec3 b,vec3 rd){
    vec3 dir = sign(rd)*b;   
    vec3 rc = (dir-p)/rd;
    return rc.z+0.01;
}
float lim(float p, float s, float lima, float limb){
    return p-s*clamp(round(p/s),lima,limb);
}
float idlim(float p, float s, float lima, float limb){
    return clamp(round(p/s),lima,limb);
}
float lim2(float p, float s,  float limb){
    return p-s*min(round(p/s),limb);
}
float idlim2(float p, float s, float limb){
    return min(round(p/s),limb);
}
float spiral(vec2 p, float t, float m, float scale, float size, float expand){
    size-=expand-0.01;
    //Offset Spiral To the left
    t = max(t,0.);

    p.x+=pi*-t*(m+m*(-t-1.));
    t-=0.25;

    vec2 po = p;
    //Move Spiral Up
    p.y+=-t*m-m*0.5;

    //Counter the rotation
    p*=rot(t*pi*2.+pi/2.);

    //Polar Map
    float theta = atan(p.y,p.x);
    theta = clamp(theta,-pi,pi);
    p = vec2(theta,length(p));

    //Create Spiral
    p.y+=theta*scale*0.5;

    //Duplicate Line outwards to fill spiral
    float py = p.y;
    float id = floor((p.y+m*0.5)/m);
    p.y = lim(p.y,m,0.,floor(t));

    //Line SDF of the spiral
    float a = abs(p.y)-size;

    //Calcuate moving outer spiral segment
    p.y = py;
    p.x -= pi;
    p.y -= (floor(t)+1.5)*m-m*0.5;
    float b = max(abs(p.y),abs(p.x)-(pi*2.)*fract(t)+size );

    //The unrolled Line SDF
    a = min(a,b-size);
    b = abs(po.y)-size;
    b = max(po.x*30.,b);

    //Combine Them
    a = min(a,b);

    return a;
}
vec3 map(vec3 p){
    vec2 a = vec2(1);
    vec2 b = vec2(1);
    float c = 0.;
    float t = iTime; 

    float size = 0.062; //Thickness of spiral curls
    float scale = size-0.01; //Space between spiral curls

    float expand = 0.04; //Corner Rounding Amount 

    float m2 = size*6.0; //Repetition Sizes
    float m = pi*scale; //Repetition Sizes

    float ltime = 10.0; //How often the spirals rolls repeat

    p.y-=(t/ltime)*size*6.; //Move everything upwards so it stays in frame

    p.x-=3.;  //small offset for framing

    float width = 0.5; //Lane Width
    float count = 6.0; //Number of spirals (x2)

    float modwidth = width*2.0+0.04+0.06;

    float id3 = idlim(p.z,modwidth,-count,count);
    t+=h11(id3*0.76)*8.0; //it took like 15 minutes to find this seed
    p.z = lim(p.z,modwidth,-count,count);

    #ifdef FUN_MODE
        scale+=(sin(t)*0.5+0.5)*0.05;
        m = pi*scale;
    #endif

    float to = t;
    vec3 po = p;

    //Spiral 1
    float stack = -floor(t/ltime);
    float id2 = idlim2(p.y,m2,stack);
    t+=id2*ltime;
    p.y = lim2(p.y,m2,stack);
    a.x = spiral(p.xy,t,m,scale,size,expand);
    c = a.x;

    a.x = min(a.x,max(p.y+size*5.,p.x));//Artifact Removal

    //Spiral 2
    p = po;
    t = to;
    p.y+=size*2.0;
    t-=ltime/3.0;
    stack = -floor(t/ltime);
    id2 = idlim2(p.y,m2,stack);
    t+=id2*ltime;
    p.y = lim2(p.y,m2,stack);
    b.x = spiral(p.xy,t,m,scale,size,expand);
    c = min(c,b.x);
    a=(a.x<b.x)?a:b;

    a.x = min(a.x,max(p.y+size*5.,p.x));//Artifact Removal

    //Spiral 3
    p = po;
    t = to;
    p.y+=size*4.0;
    t-=2.*ltime/3.0;
    stack = -floor(t/ltime);
    id2 = idlim2(p.y,m2,stack);    
    t+=id2*ltime;
    p.y = lim2(p.y,m2,stack);
    b.x = spiral(p.xy,t,m,scale,size,expand);
    c = min(c,b.x);
    a=(a.x<b.x)?a:b;

    a.x = min(a.x,max(p.y+size*5.,p.x)); //Artifact Removal

    a.x = ext(po.yzx,a.x,width-expand*0.5+0.02)-expand;
    //SDF without intersection boundries for AO
    c = ext(po.yzx,c,width-expand*0.5+0.02)-expand; 

    //Intersection distance to plane between each lane
    b.x = diplane(po ,vec3(modwidth)*0.5, rdg); //Artifact Removal
    b.y = 0.;

    a=(a.x<b.x)?a:b; //Artifact Removal

    return vec3(a,c);
}
vec3 norm(vec3 p){
    vec2 e = vec2(0.01,0);
    return normalize(map(p).x-vec3(
    map(p-e.xyy).x,
    map(p-e.yxy).x,
    map(p-e.yyx).x));
}
void render( out vec4 fragColor,in vec2 fragCoord){
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(5,1.8,-12)*1.2;
    ro.zx*=rot(0.09);

    //Mouse control
    if(iMouse.z>0.5){
    ro.yz*=rot(0.5*(iMouse.y/iResolution.y-0.5));
    ro.zx*=rot(-0.5*(iMouse.x/iResolution.x-0.5));
    }
    //Camera Setup
    vec3 lk = vec3(-2.5,0.,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*2.0+uv.x*r+uv.y*cross(f,r));  
    rdg = rd;

    vec3 p = ro;
    vec3 d;
    float dO = 0.;
    bool hit = false;

    //Raymarcher
    for(int i = 0; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);
        dO+=d.x;
        if(d.x<0.001||i==STEPS-1){
            hit = true;
            break;
        }
        if(dO>MDIST){
            dO = MDIST;
            break;
        }
    }
    //Color Surface
    if(hit&&d.y!=0.){
        vec3 ld = normalize(vec3(0.5,0.4,0.9));
        vec3 n = norm(p);
        rdg = ld;
        float shadow = 1.;
        for(float h = 0.09; h<7.0;){
            vec3 dd = map(p+ld*h+n*0.005);
            if(dd.x<0.001&&dd.y==0.0){break;}
            if(dd.x<0.001){shadow = 0.0; break;}
            shadow = min(shadow,dd.z*30.0);
            h+=dd.x;
        }
        shadow = max(shadow,0.8);
        #define AO(a,n,p) smoothstep(-a,a,map(p+n*a).z)
        float ao = AO(0.05,n,p)*AO(.1,n,p);
        ao = max(ao,0.1);
        n.xz*=rot(4.*pi/3.);
        col = n*0.5+0.5;
        col = col*shadow;
        col*=ao;
    }
    //Color Background
    else{
        col = mix(vec3(0.355,0.129,0.894),vec3(0.278,0.953,1.000),clamp((rd.y+0.05)*2.0,-0.15,1.5));
    }
    //Gamma Approximation
    col = sqrt(col);
    fragColor = vec4(col,0.0);  
}

//External AA (check render function for usual code)
#define ZERO min(0.0,iTime)
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float px = 1.0/AA; vec4 col = vec4(0);
    if(AA==1.0) {render(col,fragCoord); fragColor = col; return;}
    for(float i = ZERO; i <AA; i++){
        for(float j = ZERO; j <AA; j++){
            vec4 col2;
            vec2 coord = vec2(fragCoord.x+px*i,fragCoord.y+px*j);
            render(col2,coord);
            col.rgb+=col2.rgb;
        }
    }
    col/=AA*AA;
    fragColor = vec4(col);
}

float tri(float y){
    return abs(2.*fract(y)-1.);
}

vec4 texPiso(vec2 uv){
    vec4 col1 = vec4(0.24,0.,0.,1.);
    vec4 col2 = vec4(0.8,0.8,0.7,1.);
    return tri(uv.y*6.+tri(fract(uv.x)*3.)) > 0.5 ? col1 : col2;
}

vec4 texCort(vec2 uv, float mod){
    vec4 col1 = vec4(0.5,0.,0.,1.);
    vec4 col2 = vec4(0.8,0.8,0.7,1.);
    return vec4(vec3(tri(uv.y*(5.-mod)+tri(fract(uv.x)*3.))),1.)*col1;
}

float luma(vec3 rgb){
    vec3 y = vec3(0.2126,0.7152,0.0722);
    return dot(rgb,y);
}

