void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = gl_FragCoord.xy/iResolution.xy - .5; 

    uv.x *= iResolution.x / iResolution.y; 

    // oscilamos la posicion de la cámara en z
    //vec3 from = vec3(0., 0., -10. + sin(iTime * .5) * 5.);
    //vec3 from = vec3(0., 0., -10. + (iTime * .25) * 5.);
    vec3 from = vec3(0., 0., -10. + (iTime * .25) * 5.);
    //vec3 dir = normalize(vec3(uv, 1.));
    vec3 dir = normalize(vec3(uv, 0.75));
    //una forma simple de rotar la cámara
    //es rotando en los mismos ejes tanto from como dir
    from.xz *= rot(iTime*.0);
    dir.xz *= rot(iTime*.0);

    vec3 col = march(from, dir);

    fragColor = vec4(col, 1.);
}

// Common
#define PI 3.1415926538

// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
  return fract(sin(seed)*43758.5453);
}

// Cosine direction
vec3 Cosine(in float seed,in vec3 nor)
{
  float u=Hash(78.233+seed);
  float v=Hash(10.873+seed);

  // method 3 by fizzer: http://www.amietia.com/lambertnotangent.html
  float a=6.2831853*v;
  u=2.*u-1.;
  return normalize(nor+vec3(sqrt(1.-u*u)*vec2(cos(a),sin(a)),u));
}

// Rotation matrix around z axis
// a : Angle
mat3 rotate_z(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

// Compute the ray
// m : Mouse position
// p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m,in vec2 p,out vec3 ro,out vec3 rd)
{
  float a=3.*3.14*m.x;
  float le=3.8;

  ro=vec3(20.,0.,5.);
  ro*=rotate_z(3.*3.14*m.x);

  vec3 ta=vec3(0.,0.,1.);
  vec3 ww=normalize(ta-ro);
  vec3 uu=normalize(cross(ww,vec3(0.,0.,1.)));
  vec3 vv=normalize(cross(uu,ww));
  rd=normalize(p.x*uu+p.y*vv+le*ww);
}

// Main

const int Steps=150;
const float Epsilon=.1;// Marching epsilon
const float T=.5;

const float rA=10.;// Maximum and minimum ray marching or sphere tracing distance from origin
const float rB=40.;


// ..::= Transform =::.. //

vec3 translateX(vec3 p, float t)
{
    return vec3(p.x + t, p.y,p.z);
}
vec3 translateY(vec3 p, float t)
{
    return vec3(p.x, p.y + t,p.z);
}
vec3 translateZ(vec3 p, float t)
{
    return vec3(p.x, p.y,p.z + t);
}

vec3 rotateX(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(p.x,ca*p.y-sa*p.z,sa*p.y+ca*p.z);
}

vec3 rotateY(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.z,p.y,-sa*p.x+ca*p.z);
}

vec3 rotateZ(vec3 p,float a)
{
  float sa=sin(a);
  float ca=cos(a);
  return vec3(ca*p.x+sa*p.y,-sa*p.x+ca*p.y,p.z);
}

vec3 scaleX(vec3 p, float s)
{
    return vec3(p.x*s,p.y,p.z);
}
vec3 scaleY(vec3 p, float s)
{
    return vec3(p.x,p.y*s,p.z);
}
vec3 scaleZ(vec3 p, float s)
{
    return vec3(p.x,p.y,p.z*s);
}
vec3 scaleUniform(vec3 p, float s)
{
    return vec3(p.x*s,p.y*s,p.z*s);
}

// Smooth cubic falloff function
// x : distance
// R : radius
float falloff(float x,float R)
{
  float u=clamp(x/R,0.,1.);
  float v=(1.-u*u);
  return v*v*v;
}


// ..::= Primitives =::.. //

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : radius
float point(vec3 p,vec3 c,float e,float R)
{
  return e*falloff(length(p-c),R);
}
vec4 pointColor(vec3 p, vec3 c, vec3 color, float e, float R)
{
  float x = point(p, c, e, R);
  return vec4(x, color);
}

// ==== Segment ==== //
// p : point
// a : first point of segment
// b : second point of segment
// e : energy associated to skeleton
// R : radius
float segment(vec3 p, vec3 a, vec3 b, float e, float R)
{
  if (dot(b-a, p-a) < 0.0)
  {
    return e*falloff(dot(p-a, p-a), R);
  }
  else if (dot(b-a, p-b) > 0.0)
  {
    return e*falloff(dot(p-b, p-b), R);
  }
  else 
  {
    vec3 u = (b-a)/length(b-a);
    float sqLengthAP = dot(p-a, p-a);
    return e*falloff( sqLengthAP - dot(dot((p-a), u), dot((p-a), u)), R );
  }
}
vec4 segmentColor(vec3 p, vec3 a, vec3 b, vec3 color, float e, float R)
{
  float x = segment(p, a, b, e, R);
  return vec4(x, color);
}

// ==== Circle ==== //
// p : point
// c : center of the circle
// r : radius of the circle
// e : energy associated to skeleton
// R : radius
float circle(vec3 p, vec3 c, float r, float e, float R)
{
  vec3 n = p -c;
  vec3 u = vec3(1.0, 0.0, 0.0);
  float h = dot(n, u);
  float d = dot(n, n);
  float y = d - h * h;
  y = r - sqrt(y);

  return e*falloff(sqrt(y*y + h*h), R);
}
vec4 circleColor(vec3 p, vec3 c, float r, vec3 color, float e, float R)
{
  float x = circle(p, c, r, e, R);
  return vec4(x, color);
}

// ==== Disk ==== //
// p : point
// c : center of disk
// r : radius of disk
// e : energy associated to skeleton
// R : radius
float disk(vec3 p, vec3 c, float r, float e, float R)
{
  vec3 n = p -c;
  vec3 u = vec3(1.0, 0.0, 0.0);
  float h = dot(n, u);
  float d = dot(n, n);
  float y = d - h * h;

  if (y < r*r)
    return e*falloff(abs(h), R);

  y = r - sqrt(y);
  return e*falloff(sqrt(y*y + h*h), R);
}
vec4 diskColor(vec3 p, vec3 c, float r, vec3 color, float e, float R)
{
  float x = disk(p,c,r,e,R);
  return vec4(x, color);
}

// Utility function used for box distance function
float box_plan(float coord_p, float coord_a, float coord_b)
{
  float result;
  if (coord_p > coord_a && coord_p < coord_b)
    result = 0.;
  else if (coord_p < coord_a)
    result = coord_a - coord_p;
  else if (coord_p > coord_b)
    result = coord_p - coord_b;

  return result;
}
// ==== Box ==== //
// p : point
// a : min corner point of the box
// b : max corner point of the box
// e : energy associated to skeleton
// R : radius
float box(vec3 p, vec3 a, vec3 b, float e, float R)
{
  float xval = box_plan(p.x, a.x, b.x); // x
  float yval = box_plan(p.y, a.y, b.y); // y
  float zval = box_plan(p.z, a.z, b.z); // z

  return e*falloff( (xval*xval + yval*yval + zval*zval), R );
}
vec4 boxColor(vec3 p, vec3 a, vec3 b, vec3 color, float e, float R)
{
  float x = box(p, a, b, e, R);

  return vec4(x, color);
}


// ..::= Operators =::.. //

// ==== Blending ==== //
// a,b : field function of the sub-trees
float Blend(float a,float b)
{
  return a+b;
}
vec4 BlendColor(vec4 a, vec4 b)
{
  if ((a.x + b.x) != 0.)
  {
    vec3 color = (a.yzw * a.x + b.yzw * b.x) / (a.x + b.x);
    return vec4(a.x + b.x, color);
  }
   return vec4(a.x + b.x, a.yzw);
}

// ==== Union ==== //
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a,float b)
{
  return max(a,b);
}
vec4 UnionColor(vec4 a, vec4 b)
{
  if (a.x > b.x)
    return a;
  return b;
}

// ==== Intersection ==== //
float Intersection(float a, float b)
{
  return min(a,b);
}

// ==== Difference ==== //
float Difference(float a, float b)
{
  return min(a, 2.*T - b);
}
vec4 DifferenceColor(vec4 a, vec4 b)
{
  return vec4(Difference(a.x,b.x), a.yzw);
}


// ..::= Scene's objects =::.. //

vec4 torso (vec3 p)
{
    vec4 torso;

    // Neck
    vec4 neck = segmentColor(p, vec3(0.,0.,-1.), vec3(0.,0.,-1.5), vec3(0.25,0.25,0.25),0.6,0.8);

    // Body
    vec4 body = boxColor(p, vec3(-0.3,-0.5,-0.8), vec3(0.3,0.5,0.8), vec3(1.0,0.8,0.0),1.0,1.0);
    body = BlendColor(body, pointColor(p, vec3(1.0,0.0,0.4), vec3(1.0,0.8,0.0), -1.2,1.0));
    body = BlendColor(body, pointColor(p, vec3(0.0,1.0,0.4), vec3(1.0,0.8,0.0), -1.0,1.0));
    body = BlendColor(body, pointColor(p, vec3(0.0,-1.0,0.4), vec3(1.0,0.8,0.0), -1.0,1.0));
    body = BlendColor(body, diskColor(rotateY(p,PI/2.), vec3(-0.9,0.,0.), 0.85, vec3(1.,0.8,0.),1.,1.));
    body = UnionColor(body, diskColor(p, vec3(0.0,0.,0.), 0.85, vec3(0.25,0.25,0.25),1.,1.));
    body = BlendColor(body, segmentColor(p, vec3(0.0,-1.0,-0.8), vec3(0.,1.0,-0.8), vec3(0.25,0.25,0.25),1.0,0.8));
    body = BlendColor(body, segmentColor(p, vec3(0.0,0.0,1.25), vec3(0.,0.0,1.75), vec3(0.25,0.25,0.25),0.6,0.8));

    // Core
    vec3 color = vec3 ( max(abs(Hash(iTime*0.15)),0.6), max(abs(Hash(iTime*0.75)),0.6), max(abs(Hash(iTime*0.5)),0.6));
    body = UnionColor(body, pointColor(p, vec3(0.75,0.0,0.4), color, 1., 0.75));

    torso = BlendColor(neck,body);

    return torso;
}

vec4 wheelLeg (vec3 p)
{
    vec4 wheelLeg;

    wheelLeg = boxColor(p, vec3(-0.1,-0.3,-0.2), vec3(0.1,0.3,0.7), vec3(1.0,0.8,0.0),1.0,1.0);
    wheelLeg = BlendColor(wheelLeg, diskColor(rotateZ(rotateY(p,PI/2.),PI/2.), vec3(0.,-0.5,-0.),0.5,vec3(1.0,0.8,0.0),-1.,1.));
    wheelLeg = UnionColor(wheelLeg, circleColor(rotateZ(rotateY(scaleY(p,0.75),PI/2.),PI/2.), vec3(0.,-0.7,0.),0.6,vec3(0.2,0.2,0.2),0.75,0.75));
    wheelLeg = UnionColor(wheelLeg, diskColor(rotateZ(rotateY(p,PI/2.),PI/2.), vec3(0.,-0.7,-0.),0.3,vec3(0.5,0.5,0.5),0.75,0.75));
    wheelLeg = BlendColor(wheelLeg, segmentColor(p, vec3(0.,-0.8,0.75),vec3(0.,0.8,0.75),vec3(0.3,0.3,0.3),0.55,0.35));

    return wheelLeg;
}

vec4 leftArm (vec3 p)
{
    vec4 leftArm;

    leftArm = segmentColor(p, vec3(0.,1.5,0.),vec3(0.75,2.25,0.5),vec3(0.25,0.25,0.25),0.55,0.5);
    leftArm = BlendColor(leftArm,segmentColor(p, vec3(0.75,2.25,0.5),vec3(1.75,1.75,0.5),vec3(0.25,0.25,0.25),0.55,0.5));
    vec3 tr = rotateZ(translateZ(translateY(translateX(p,-1.75),-1.75),-0.5),-PI/6.);
    leftArm = BlendColor(leftArm, segmentColor(tr, vec3(-0.25,0.0,0.0),vec3(0.25,0.,0.),vec3(1.,0.8,0.),0.55,0.55));
    leftArm = UnionColor(leftArm, diskColor(tr, vec3(0.5,0.0,0.0),0.25,vec3(0.25,0.25,0.25),0.55,1.));

    vec4 finger1 = segmentColor(tr, vec3(0.65,0.,0.35),vec3(1.,0.,0.55),vec3(0.45,0.45,0.45),0.55,0.1);
    finger1 = UnionColor(finger1, segmentColor(tr, vec3(1.,0.,0.55),vec3(1.3,0.,0.25),vec3(0.45,0.45,0.45),0.55,0.1));

    vec4 finger2 = segmentColor(rotateX(tr,PI/1.5), vec3(0.65,0.,0.35),vec3(1.,0.,0.55),vec3(0.45,0.45,0.45),0.55,0.1);
    finger2 = UnionColor(finger2, segmentColor(rotateX(tr,PI/1.5), vec3(1.,0.,0.55),vec3(1.3,0.,0.25),vec3(0.45,0.45,0.45),0.55,0.1));

    vec4 finger3 = segmentColor(rotateX(tr,-PI/1.5), vec3(0.65,0.,0.35),vec3(1.,0.,0.55),vec3(0.45,0.45,0.45),0.55,0.1);
    finger3 = UnionColor(finger3, segmentColor(rotateX(tr,-PI/1.5), vec3(1.,0.,0.55),vec3(1.3,0.,0.25),vec3(0.45,0.45,0.45),0.55,0.1));

    vec4 hand = UnionColor(finger1,finger2);
    hand = UnionColor(hand, finger3);

    leftArm = UnionColor(leftArm, hand);

    return leftArm;
}

vec4 rightArm (vec3 p)
{
    vec4 rightArm;

    rightArm = segmentColor(p, vec3(0.,-1.5,0.),vec3(0.25,-2.25,.5),vec3(0.25,0.25,0.25),0.55,0.5);
    rightArm = BlendColor(rightArm,segmentColor(p, vec3(0.25,-2.25,0.5),vec3(0.5,-2.5,1.),vec3(0.25,0.25,0.25),0.55,0.35));
    vec3 tr = rotateZ(rotateY(translateZ(translateY(translateX(p,-0.5),2.5),-1.),PI/3.),-PI/10.);
    rightArm = BlendColor(rightArm, segmentColor(tr, vec3(0.25,0.,0.),vec3(1.,0.0,0.0),vec3(1.,0.8,0.),1.,0.35));
    rightArm = UnionColor(rightArm, diskColor(tr, vec3(1.4,0.0,0.0),0.25,vec3(0.25,0.25,0.25),0.55,1.));

    rightArm = UnionColor(rightArm, segmentColor(tr, vec3(1.5,0.,0.),vec3(2.,0.,0.),vec3(0.25,0.25,0.25),0.55,0.2));
    rightArm = UnionColor(rightArm, segmentColor(rotateX(tr,PI/2.), vec3(2.,-0.25,0.),vec3(2.,0.25,0.),vec3(0.25,0.25,0.25),0.55,0.75));
    rightArm = UnionColor(rightArm, diskColor(rotateY(tr,PI/2.), vec3(-0.5,0.,-2.),0.25,vec3(1.,0.8,0.),0.55,1.));
    rightArm = UnionColor(rightArm, diskColor(rotateY(tr,PI/2.), vec3(0.5,0.,-2.),0.25,vec3(1.,0.8,0.),0.55,1.));

    return rightArm;
}

vec4 head (vec3 p)
{
    vec4 head;

    head = pointColor(p * vec3(1.,0.9,1.), vec3(0.,0.,0.), vec3(0.45,0.45,0.45), 1., 3.);
    vec3 TR1 = rotateZ(p,PI/2.) * vec3(1.5,1.5,1.5) - vec3(1.5,0.,0.);
    vec3 TR2 = rotateZ(p,PI/2.) * vec3(1.5,1.5,1.5) + vec3(1.5,0.,0.);

    head = BlendColor(head, diskColor(TR1, vec3(0.,0.,0.),.7,vec3(0.25,0.25,0.25),1.2,1.1));
    head = BlendColor(head, diskColor(TR2, vec3(0.,0.,0.),.7,vec3(0.25,0.25,0.25),1.2,1.1));
    head = BlendColor(head, diskColor(scaleY(rotateX(translateX(TR1,-0.4), PI/3.), 0.5), vec3(0.,0.75,0.), 0.75, vec3(1.,0.8,0.), 0.75,0.75));
    head = BlendColor(head, diskColor(scaleY(rotateX(translateX(TR2,0.4), PI/3.), 0.5), vec3(0.,0.75,0.), 0.75, vec3(1.,0.8,0.), 0.75,0.75));
    head = BlendColor(head, pointColor(TR1, vec3(1.5,0.,0), vec3(0.,0.,0.), -0.75, 1.5));
    head = BlendColor(head, pointColor(TR2, vec3(-1.5,0.,0), vec3(0.,0.,0.), -0.75, 1.5));

    // Eyes
    head = UnionColor(head, segmentColor(scaleUniform(rotateX(p,-PI/6.),1.2), vec3(1.25,-0.95,-0.25), vec3(1.35,-0.75,-.25), vec3(0.7,0.1,0.1), 0.55,0.75));
    head = UnionColor(head, segmentColor(scaleUniform(rotateX(p,PI/6.),1.2), vec3(1.35,0.75,-0.25), vec3(1.25,0.95,-.25), vec3(0.7,0.1,0.1), 0.55,0.75));

    // Mouth
    head = UnionColor(head, pointColor(scaleY(p,0.5), vec3(1.25,0.,0.65), vec3(1.,0.,0.), 0.55, 1.));

    return head;
}

vec4 robot (vec3 p)
{
  vec4 robot;

  vec4 body = torso(translateZ(p,-2.6));
  vec4 wheelLeg = wheelLeg(translateZ(p,-5.));
  vec4 leftArm = leftArm(translateZ(p,-2.));
  vec4 rightArm = rightArm(translateZ(p,-2.));
  vec4 head = head(p);

  robot = body;
  robot = UnionColor(robot,wheelLeg);
  robot = UnionColor(robot, leftArm);
  robot = UnionColor(robot, rightArm);
  robot = UnionColor(robot, head);

  return robot;
}

// Main object scene (root of CSG tree)
// vec4 : 
// -- x : distance value
// -- yzw : vec3 representing color rgb
vec4 ObjectColored(vec3 p)
{
  p.z = -p.z;

  vec3 scale = vec3(4.,4.,4.);

  vec4 v;

  // ===== Robot ===== //
  v = robot(p - vec3(0.,0.,-3.));


  return vec4(v.x - T, v.y, v.z, v.w);
}

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p)
{
  float eps=.0001;
  vec3 n;
  vec4 v=ObjectColored(p);
  n.x=ObjectColored(vec3(p.x+eps,p.y,p.z)).x-v.x;
  n.y=ObjectColored(vec3(p.x,p.y+eps,p.z)).x-v.x;
  n.z=ObjectColored(vec3(p.x,p.y,p.z+eps)).x-v.x;
  return normalize(n);
}

vec3 ObjectColor(in vec3 p)
{
  vec4 v = ObjectColored(p);
  return vec3(v.y, v.z, v.w);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o,vec3 u,float rB,out bool h,out int s)
{
  h=false;

  // Don't start at the origin, instead move a little bit forward
  float t=rA;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=o+t*u;
    //float v=Object(p);
    vec4 v = ObjectColored(p);
    // Hit object
    if(v.x>0.)
    {
      s=i;
      h=true;
      break;
    }
    // Move along ray
    t+=Epsilon;
    // Escape marched far away
    if(t>rB)
    {
      break;
    }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTrace(vec3 o,vec3 u,float rB,out bool h,out int s)
{
  h=false;

  // Don't start at the origin, instead move a little bit forward
  float t=rA;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=o+t*u;
    //float v=Object(p);
    vec4 v = ObjectColored(p);
    // Hit object
    if(v.x>0.)
    {
      s=i;
      h=true;
      break;
    }
    // Move along ray
    t+=max(Epsilon,abs(v.x)/4.);
    // Escape marched far away
    if(t>rB)
    {
      break;
    }
  }
  return t;
}

// Ambient occlusion
// p : Point
// n : Normal
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n,int a)
{
  if(a==0){return 1.;}

  float ao=0.;

  for(int i=0;i<a;i++)
  {
    vec3 d=Cosine(581.123*float(i),n);

    int s;
    bool h;
    float t=SphereTrace(p,d,10.,h,s);
    if(!h){ao+=1.;}
    else if(t>5.)
    {
      ao+=1.;
    }
  }

  ao/=float(a);
  return ao;
}

// Background color
vec3 background(vec3 rd)
{
  return mix(vec3(.652,.451,.995),vec3(.552,.897,.995),rd.z*.5+.5);
}

float Light(vec3 p,vec3 n)
{
  // point light
  const vec3 lp=vec3(5.,10.,25.);

  vec3 l=normalize(lp-p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff=pow(.5*(1.+dot(n,l)),2.);

  bool h;
  int s;
  float t=SphereTrace(p+.1*n,l,100.,h,s);
  if(!h)
  {
    return diff;
  }
  return 0.;
}

float SmoothLight(vec3 p,vec3 n,int a)
{
  if(a==0)
  return 1.;

  // point light
  const vec3 lp=vec3(5.,10.,25.);

  vec3 l=normalize(lp-p);

  float lo=0.;

  for(int i=0;i<a;i++)
  {
    vec3 d=Cosine(581.123*float(i),n);
    d=normalize(l+d*.15);
    int s;
    bool h;
    float t=SphereTrace(p,d,10.,h,s);
    if(!h){lo+=1.;}
    else if(t>100.)
    {
      lo+=1.;
    }
  }

  lo/=float(a);
  return lo;

}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p,vec3 n)
{
  vec3 c=.25+.25*background(n);
  c+=.15*AmbientOcclusion(p+.1*n,n,0)*vec3(1.,1.,1.);
  c+=.35*Light(p,n);
  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
  float t=float(n)/(float(Steps-1));
  return .5+mix(vec3(.05,.05,.5),vec3(.65,.39,.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel,out bool pip)
{
  // Pixel coordinates
  vec2 p=(-iResolution.xy+2.*pixel)/iResolution.y;
  if(pip==true)
  {
    const float fraction=1./4.;
    // Recompute pixel coordinates in sub-picture
    if((pixel.x<iResolution.x*fraction)&&(pixel.y<iResolution.y*fraction))
    {
      p=(-iResolution.xy*fraction+2.*pixel)/(iResolution.y*fraction);
      pip=true;
    }
    else
    {
      pip=false;
    }
  }
  return p;
}

// Image
void mainImage(out vec4 color,in vec2 pxy)
{
  // Picture in picture on
  bool pip=true;

  // Pixel
  vec2 pixel=Pip(pxy,pip);

  // Mouse
  vec2 m=iMouse.xy/iResolution.xy;

  // Camera
  vec3 ro,rd;
  Ray(m,pixel,ro,rd);

  // Rotate Camera
  float a=iTime*0.25;
  ro = rotateZ(ro, a);
  rd = rotateZ(rd, a);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t=SphereTrace(ro,rd,100.,hit,s);
  //float t = Trace(ro, rd, 100., hit, s);

  // Position
  vec3 pt=ro+t*rd;

  // Shade background
  vec3 rgb=background(rd);

  if(hit)
  {
    // Compute normal
    vec3 n=ObjectNormal(pt);

    vec3 col = ObjectColor(pt);

    // Shade object with light
    rgb=Shade(pt,n) * col;
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if(pip==true)
  {
    rgb=ShadeSteps(s);
  }

  color=vec4(rgb,1.);
}

//3d cellular tiling reference: https://www.shadertoy.com/view/ld3Szs
//cloud reference:              https://www.shadertoy.com/view/4sXGRM

#define _StepSize 120.
#define _VolDepth 80000.0
#define _CloudScale 7.
#define _CloudOffset 0.
#define _DensityThreshold .1
#define _DensityMultiplier .00005


#define _DarknessThreshold .3
#define _LightAbsorbtion 100.
#define _LightAbsorbtionTowardsSun 100.
#define  _NumStepsLight 6
#define _PhaseVal 100.

mat3 m = mat3(0.00, 1.60, 1.20, -1.60, 0.72, -0.96, -1.20, -0.96, 1.28);

vec2 cloudHeight = vec2(-2000,2000);

vec3 lightDir = normalize(vec3(0.1,.1,.5));


float drawSphere(in vec3 p){

    p = fract(p)-.5;    
    return dot(p, p);

    p = abs(fract(p)-.5);
    return dot(p, vec3(.166));

}

float cellTile(in vec3 p){

    float c = .25; // Set the maximum.

    c = min(c, drawSphere(p - vec3(.81, .62, .53)));
    c = min(c, drawSphere(p - vec3(.39, .2, .11)));

    c = min(c, drawSphere(p - vec3(.62, .24, .06)));
    c = min(c, drawSphere(p - vec3(.2, .82, .64)));

    p *= 1.4142;

    c = min(c, drawSphere(p - vec3(.48, .29, .2)));
    c = min(c, drawSphere(p - vec3(.06, .87, .78)));

    c = min(c, drawSphere(p - vec3(.6, .86, .0)));
    c = min(c, drawSphere(p - vec3(.18, .44, .58)));

    return (c*4.);

}


vec3 camera(float time)
{
    return vec3(0,5000.*sin(iTime), time*2000.);
}
float getDensity(vec3 pos){
    float n1 = smoothstep(.7,.1,cellTile(pos*.00005));
    float n2 = smoothstep(0.,.5,cellTile(pos*.0001));
   // float n3 = smoothstep(0.,.4,cellTile(pos*.00001));
   // float n4 = smoothstep(0.,.4,cellTile(pos*.0002));
    float alpha = smoothstep(0.5, 1.0,n1*n2);

    alpha = max(0.,alpha-_DensityThreshold)*_DensityMultiplier;
    return alpha;
}

// Fractional Brownian motion
float fbm(vec3 p)
{
   // p*=.00025;
    float f = 0.5000 * getDensity(p);
    p = m * p;
    f += 0.2500 * getDensity(p);
    p = m * p;
    f += 0.1666 * getDensity(p);
    p = m * p;
    f += 0.0834 * getDensity(p);

    return f;
}

float lightmarch(vec3 pos){
    vec3 dirToLight = lightDir;
    float totalDensity = 0.;
    for(float i = 0.; i < _VolDepth; i += _StepSize*200.){

        pos += dirToLight *_StepSize;

        float alpha = fbm(pos);//getDensity(pos);

        totalDensity += max(0.,alpha*_StepSize);
    }
    float transmittance = exp(-totalDensity * _LightAbsorbtionTowardsSun);
    return _DarknessThreshold + transmittance * (1.-_DarknessThreshold);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = (iTime + 13.5 + 44.) * 1.0;
    vec3 campos = camera(time);
    vec3 camtar = camera(time + .1);

    vec3 front = normalize(camtar - campos);
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));
    vec3 fragAt = normalize(uv.x * right + uv.y * up + front);

    // clouds
    vec4 sum = vec4(0, 0, 0, 0);
    vec3 lightE = vec3(0.);
    float transmittance = 1.;
    for (float depth = 0.0; depth < _VolDepth; depth += _StepSize)
    {
        vec3 ray = campos + fragAt * depth;
        if (cloudHeight.x < ray.y && ray.y < cloudHeight.y)
        {

            float alpha =fbm(ray);//getDensity(pos);

            alpha = (1.0 - sum.a) * alpha;
            if(alpha >0.){
                float lightTransmittance = lightmarch(ray);
                lightE+=alpha*_StepSize*transmittance*lightTransmittance*_PhaseVal;
                transmittance*= exp(-alpha*_StepSize*_LightAbsorbtion);
                if(transmittance < 0.01){
                  break;
                }
            }
            sum += vec4(vec3(1) * alpha, alpha);
        }
    }
    float alpha = smoothstep(0.7, 1.0, sum.a);
    sum.rgb= lightE;
    float sundot = clamp(dot(fragAt, lightDir), 0.0, 1.0);
    vec3 col = 0.8 * (vec3(0,0,1));
    col += 0.47 * vec3(1.6, 1.4, 1.0) * pow(sundot, 350.0);
    col += 0.4 * vec3(0.8, 0.9, 1.0) * pow(sundot, 2.0);
    col = mix(col, sum.rgb, 1.-transmittance);

    //col=vec3(smoothstep(.0,.7,cellTile(vec3(uv.x,uv.y,0.)))*smoothstep(1.,.5,cellTile(vec3(uv.x,uv.y,0.)))*smoothstep(1.,.4,cellTile(vec3(uv.x*2.,uv.y*2.,0.))));
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.1415926535
#define pmod(p,x) (mod(p,x)-0.5*(x))

#define elon(p,h) (p-clamp(p,-h,h))

//Enable exit ray
#define refraction2


//iq octohedron
float oct( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float box(vec3 p, vec3 b){
    vec3 d = abs(p)-b;
    return max(d.x,max(d.y,d.z));
}

float torus (vec3 p, vec2 b){
  vec2 d = vec2(length(p.yz)-b.x,p.x);
  return length(d)-b.y;
}
float torus2 (vec3 p, vec2 b){
  vec2 d = vec2(length(p.yx)-b.x,p.z);
  return length(d)-b.y;
}
float glow = 0.;
float glow2 = 0.;

bool inside = false;
vec2 map(vec3 p){
    float t= iTime;
    vec3 po2 = p;
    vec2 a = vec2(1.);
    vec2 b = vec2(2.);
    //Swing
    p.y-=7.0;
    p.xy*=rot(sin(t*2.0*pi/3.)*0.28);
    p.y+=7.0;
    vec3 po = p;
    //Glass Displacment
    p+=t*0.2;
    float dis = sin(p.x*5.)*sin(p.y*5.)*sin(p.z*5.)*0.02;
    p = po;
    //Glass ball
    a.x = length(p)-1.0+dis;
    glow2+=0.5/(0.5+a.x*a.x);
    a.x*=0.85;
    if(inside){
        //Octohedron
        p.xz*=rot(t*0.8);
        b.x = oct(p,0.3)-0.02;

        //Mini Octohedron
        float s = sign(p.x*p.y*p.z);
        p = abs(p)-0.25+sin(t)*0.05;
        p.xz*=rot(-t*s*2.0);
        b.x = min(b.x, oct(p,0.05)-0.01);

        p = po;
        //Particles
        p.xz*=rot(t);
        p.y-=t;
        float m = 0.5;
        vec3 id = floor(p/m)-0.5;
        p.y+=sin(id.x*id.z)*4.0;

        p = pmod(p,vec3(m));
        b.x*=0.9;
        b.x = min(b.x,length(p));

        b.x = max(a.x,b.x);
        glow+=0.005/(0.001+b.x);

        a.x = max(a.x,-b.x);

        a=(abs(a.x)<abs(b.x))?a:b;
    }
    //chain
    b.y = 3.0;
    float scl = 0.6;
    float m2 = 1.5*scl;
    po.y-=7.5*scl;
    po.xz*=rot(t*0.8);
    p = po;

    p.y = pmod(max(p.y,-m2*4.),m2);
    p.y = elon(p.y,0.3*scl);
    b.x = torus(p,vec2(0.2,0.1)*scl);

    p = po;
    p.y+=0.75*scl;
    p.y = pmod(max(p.y,-m2*4.),m2);
    p.y = elon(p.y,0.3*scl);
    b.x = min(b.x,torus2(p,vec2(0.2,0.1)*scl));

    a=(abs(a.x)<abs(b.x))?a:b;

    if(!inside){
        //Background
        b.y = 4.0;
        p = po2;
        //technique idea taken from yonatan
        for(float s = 1.; s<10.; s/=.55){
            p.xz*=rot(s);
            p.y+=abs(dot(sin(p.xz*s+t*0.8)/s,vec2(0.15)));
            p.y+=(dot(sin(p.xz*s+t*0.4+pi/2.)/s,vec2(0.15)));
        }
        b.x = p.y+1.3;
        b.x*=0.7;

        b.x = max(b.x,(length(p)-9.0));
        a=(a.x<b.x)?a:b;
    }
    return a;
}

vec3 norm(vec3 p){
    vec2 e = vec2(0.001,0);
    return normalize(map(p).x-vec3(
    map(p-e.xyy).x,
    map(p-e.yxy).x,
    map(p-e.yyx).x));
}
#define MDIST 50.0
vec2 march(vec3 ro, vec3 rd, int steps){
    float dO = 0.;
    for(int i = 0; i<steps; i++){
        vec3 p = ro+rd*dO;
        vec2 d = map(p);
        dO+=abs(d.x);
        if(dO>MDIST){
            return vec2(dO,-1.);
        }
        if(abs(d.x)<0.0001||i==steps-1){
            return vec2(dO,d.y);
        }
    }
}

vec3 colorI(vec3 ro, vec3 rd, float dO, float mat){
    vec3 col = vec3(0);
    vec3 p = ro+rd*dO;
    vec3 n = norm(p);


    vec3 ld = normalize(vec3(1));
    vec3 h = normalize(ld-rd);
    float ndotl = abs(dot( -rd, n ));
    float rim = pow(1.0-ndotl, 5.0);
    float spec = pow(max(dot(n,h),0.0),5.0);


    vec3 bg = vec3(0.3);
    if(mat==1.0) col = bg;

    col+=rim;

    float dens = 0.1;
    vec3 gemCol = vec3(0.310,0.004,0.396);
    vec3 coloredDens = (1.-log(gemCol))*dens;
    vec3 optDist = exp(-dO*coloredDens);

    //float optDist = exp(-dO*dens);
    col = mix(gemCol,col,optDist);

    col = col*optDist*gemCol;

    if(mat==2.0)glow*=rim;
    col+=clamp(glow*0.5,0.,2.0)*vec3(0.886,0.376,0.129);


    return col;
}
bool refracted = false;
vec3 colorO(vec3 ro, vec3 rd, float dO, float mat){
    vec3 col = vec3(0);
    vec3 p = ro+rd*dO;
    vec3 n = norm(p);
    vec3 ld = normalize(vec3(1));
    vec3 h = normalize(ld-rd);
    vec3 r = reflect(rd,n);
    float ndotl = abs(dot( -rd, n ));
    float rim = pow(1.0-ndotl, 3.0);
    float spec = length(sin(r*2.)*.5+.5)/sqrt(3.);

    vec3 bg = vec3(0.);
    col = mix(col,bg,1.0);
    //col+=clamp(glow*0.3,0.,0.8)*vec3(0.886,0.376,0.129);
    if(dO<MDIST&&mat!=4.0){
        col+=pow(spec,4.0)*3.0*vec3(1,0,0);
        col+=rim*5.0;
    }

    col+=clamp(glow2*0.03,0.,2.0)*vec3(0.886,0.376,0.129);
    if(refracted) col=glow2*rim*vec3(0.886,0.376,0.129)*0.1;
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);
    m.y+=0.1;
    vec3 ro = vec3(0,0.5+m.y*5.0,-4.0-m.y*10.0);

    //ro.yz *= rot(-m.y*3.14+1.);
    ro.xz *= rot(-m.x*6.2831);

    vec3 lk = vec3(0,0.3,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*0.99+uv.x*r+uv.y*cross(f,r));
    vec2 dO = vec2(0);
    vec3 rdo = rd;
    //Index of Refraction
    float ior = 1.4;

    dO = march(ro,rd,170);
    vec3 p = ro+rd*dO.x;
    //Yeah this lighting setup is janky but it looks better than how I intended it
    //so I'm keeping it 
    col = colorO(ro,rd,dO.x,dO.y); //Color the outside

    //If hit the glass then march into it and color the inside
    if(dO.y==1.0||dO.y==3.0){
        inside = true; //optimize which geometry to check
        glow = 0.;
        ro = p;
        vec3 n = norm(p);
        ro-=n*0.05;
        rd = refract(rd,n,1./ior);
        if(dO.y==1.0)dO = march(ro,rd,90);
        col = mix(col,colorI(ro,rd,dO.x,dO.y),0.9);

        //attempted reflection on the surface, might re-visit
        /* 
        inside = false; //optimize which geometry to check
        glow = 0.;
        glow2 = 0.;
        vec2 dO2 = vec2(0);
        dO2 = march(p+n*0.05,reflect(rd,n),90);
        refracted = true;
        vec3 col2 = colorO(ro,rd,dO2.x,dO2.y);
        float fres = pow(1.0-abs(dot(-rdo, n)),1.0);
        col+=col2*fres;
        */

    #ifdef refraction2
        if(dO.y==1.0){
            inside = false; //optimize which geometry to check
            refracted = true; //janky fix to make the background more defined
            p =ro+rd*dO.x;
            glow = 0.;
            glow2 = 0.;
            ro = p;
            n = norm(p);
            ro += n*0.05;
            vec3 ref = refract(rd,-n,ior);
            if(ref == vec3(0)) rd = reflect(rd,-n);
            else rd = ref;

            //not accurate but maybe more visually apealing ray direction
            //rd = rdo; 

            dO = march(ro,rd,40);

            col = mix(col,colorO(ro,rd,dO.x,dO.y),0.1);    
        }
    #endif     

    }

    fragColor = vec4(col,1.0);
}

float tri(float x) { return min(fract(x), 1. - fract(x)) * 4. - 1.; }

bool grid(vec2 p, float l, float h)
{
    return (fract(p.x * 10.) < .1 || fract(p.y * 10.) < .1) && abs(p.x - .025) < 1.25 && p.y > l && p.y < h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (2. * fragCoord.xy - iResolution.xy ) / min(iResolution.x, iResolution.y) + vec2(.0, .4);
    vec4 col = vec4(.5);
    float x = tri(iTime * .1) * 2.6;
    vec2 s = 2. * pos - vec2(x, 1.5 * abs(sin(iTime * 1.7)));

    if (length(s) < .85)
    {
        col = vec4(1.0, .0, 0., .0);
        s = s * mat2(.96, -.28, .28, .96) / sin(1.85 - length(s.xy)*1.4) + vec2(1. * x, 0.);
        if (fract(s.x * 1.2) < .5 ^^ fract(s.y * 1.2) < 0.5) col += 1.1;
    }
    else
    {
        if (grid(pos, -.3, 1.205)) col.g = 0.;
        pos = vec2(pos.x, 1.) / pos.y * -.3;
        if (grid(pos, .7, 1.)) col.g = 0.;
        if (length(s - vec2(.4, .1)) < .85) col /= 2.;
    }

	fragColor = vec4(col.rgb,1.0);
}

//CBS
//Parallax scrolling fractal galaxy.
//Inspired by JoshP's Simplicity shader: https://www.shadertoy.com/view/lslGWr

// http://www.fractalforums.com/new-theories-and-research/very-simple-formula-for-fractal-patterns/
float field(in vec3 p,float s) {
	float strength = 7. + .03 * log(1.e-6 + fract(sin(iTime) * 4373.11));
	float accum = s/4.;
	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 26; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

// Less iterations for second layer
float field2(in vec3 p, float s) {
	float strength = 7. + .03 * log(1.e-6 + fract(sin(iTime) * 4373.11));
	float accum = s/4.;
	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 18; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

vec3 nrand3( vec2 co )
{
	vec3 a = fract( cos( co.x*8.3e-3 + co.y )*vec3(1.3e5, 4.7e5, 2.9e5) );
	vec3 b = fract( sin( co.x*0.3e-3 + co.y )*vec3(8.1e5, 1.0e5, 0.1e5) );
	vec3 c = mix(a, b, 0.5);
	return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
	vec2 uvs = uv * iResolution.xy / max(iResolution.x, iResolution.y);
	vec3 p = vec3(uvs / 4., 0) + vec3(1., -1.3, 0.);
	p += .2 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));

	float freqs[4];
	//Sound
	freqs[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	freqs[2] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	freqs[3] = texture( iChannel0, vec2( 0.30, 0.25 ) ).x;

	float t = field(p,freqs[2]);
	float v = (1. - exp((abs(uv.x) - 1.) * 6.)) * (1. - exp((abs(uv.y) - 1.) * 6.));

    //Second Layer
	vec3 p2 = vec3(uvs / (4.+sin(iTime*0.11)*0.2+0.2+sin(iTime*0.15)*0.3+0.4), 1.5) + vec3(2., -1.3, -1.);
	p2 += 0.25 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));
	float t2 = field2(p2,freqs[3]);
	vec4 c2 = mix(.4, 1., v) * vec4(1.3 * t2 * t2 * t2 ,1.8  * t2 * t2 , t2* freqs[0], t2);


	//Let's add some stars
	//Thanks to http://glsl.heroku.com/e#6904.0
	vec2 seed = p.xy * 2.0;	
	seed = floor(seed * iResolution.x);
	vec3 rnd = nrand3( seed );
	vec4 starcolor = vec4(pow(rnd.y,40.0));

	//Second Layer
	vec2 seed2 = p2.xy * 2.0;
	seed2 = floor(seed2 * iResolution.x);
	vec3 rnd2 = nrand3( seed2 );
	starcolor += vec4(pow(rnd2.y,40.0));

	fragColor = mix(freqs[3]-.3, 1., v) * vec4(1.5*freqs[2] * t * t* t , 1.2*freqs[1] * t * t, freqs[3]*t, 1.0)+c2+starcolor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec4 cam = texture(iChannel0, uv);

    float noise = sin(fragCoord.x * cos(fragCoord.y * iTime)) / 5.0;

    float color = max(clamp(sin(cam.x + cam.y + cam.z / cos(1.4)), 0.0, 0.8) * 1.25, sin(cam.y * fragCoord.y / 5.0 + iTime * 10.0));

    // Output to screen
    fragColor = vec4(color / fragCoord.x * 400.0 + noise, color / 2.0 + noise, color * color, 1.0);
}

#define NUM_OCTAVES 5

vec2 random2(vec2 st) {
	st = vec2(dot(st, vec2(127.1, 311.7)),
		dot(st, vec2(269.5, 183.3)));
	return -1.0 + 2.0 * fract(sin(st) * 43758.5453123 * 0.7897);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st) {
	vec2 i = floor(st);
	vec2 f = fract(st);

	vec2 u = f * f * (3.0 - 2.0 * f);

	return mix(mix(dot(random2(i + vec2(0.0, 0.0)), f - vec2(0.0, 0.0)),
		dot(random2(i + vec2(1.0, 0.0)), f - vec2(1.0, 0.0)), u.x),
		mix(dot(random2(i + vec2(0.0, 1.0)), f - vec2(0.0, 1.0)),
			dot(random2(i + vec2(1.0, 1.0)), f - vec2(1.0, 1.0)), u.x), u.y);
}

float fbm(vec2 x) {
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
	// Rotate to reduce axial bias
	mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

mat2 rotate(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x * 4.0;
    vec3 sph = vec3(uv.x, uv.y, cos(length(uv)));

    float r = length(uv);
	float f = (1.0 - sqrt(1.0 - r)) / r;
    vec3 sunPos = vec3(cos(iTime / 4.0), 0.0, sin(iTime / 4.0));
    float light = dot(sph, sunPos);
    float reflectivity = 1.0;

    float noiseVal = fbm((uv * f * 5.0) + 500.0) + 0.5;
    float cloudNoise = fbm((uv * f * 2.0 + vec2(-iTime / 8.0, 0.0)) + 1000.0) + 0.5;
    //cloudNoise *= rotate(iTime);
    vec3 col = vec3(0.0);

    if (r < 1.0) {
        if (noiseVal < 0.5) {
            col = vec3(0.0, 0.0, 1.0);
        }
        else if (noiseVal > 0.65) {
            col = vec3(1.0, 1.0, 1.0) * noiseVal;
        }
        else {
            col = vec3(0.5, noiseVal, 0.0);
        }
        reflectivity = noiseVal * 2.0;
        col += smoothstep(0.55, 0.6, cloudNoise);
    }
    else {
        col = vec3(0.5, 0.8, 1.0) * smoothstep(1.1, 1.0, r);
        reflectivity = 0.5;
    }

    col *= light;
    col += smoothstep(1.1, 1.0, r) * light * reflectivity;

    vec2 suv = uv - sunPos.xy * 3.0;
    col += 0.1 / length(suv) * smoothstep(0.99, 1.0, r) * smoothstep(0.5, 0.8, -sunPos.z);

    float flareCol = 0.0;
    vec2 fuv = suv;
    float angle = atan(fuv.y, fuv.x) + 3.14159 / 8.0;
    flareCol += 0.1/abs(sin(angle*4.0)*cos(angle*4.0)) * smoothstep(0.6, 0.0, length(fuv));
    flareCol *= 0.1/abs(sin(angle*2.0)*cos(angle*2.0)) * smoothstep(0.6, 0.0, length(fuv));
    //flareCol += 0.01 / abs(fuv.x*fuv.y) * smoothstep(1.0, 0.5, length(fuv)) / 4.0;
    //fuv *= rotate(3.14159 / 4.0);
    //flareCol += 0.01 / abs(fuv.x*fuv.y) * smoothstep(1.0, 0.5, length(fuv)) / 16.0;

    flareCol *= smoothstep(0.7, 0.8, -sunPos.z);
    flareCol *= smoothstep(0.99, 1.0, length(uv-suv));
    //flareCol *= smoothstep(0.99, 1.0, r);
    if (light < 0.01 && r < 1.0) col = vec3(0.0);
    col += flareCol;

    // Output to screen
    fragColor = vec4(col,1.0);
}

