	float a=  distance(vec2(e),vec2(0.510,0.360));
    float b=  distance(vec2(e),vec2(a));
    float r=  distance(st,vec2(0.460,0.400));
	float f=  distance(st,vec2(0.990,0.220));
    float j=  distance(vec2(r),vec2(f));    
    float q=  distance(st,vec2(0.440,-0.130));
    float z=  distance(vec2(q),vec2(j));


    vec2 toCenter = st-vec2(noise(st-iTime) ,noise(st+iTime));
    float pct = length(toCenter);
  	toCenter = st-vec2(noise(st-iTime*0.25) ,noise(st+iTime*0.25));
