    vec3 color = .7212-vec3(j*pct*b*z)*50.000;


    fragColor = vec4(color,1.0);
}

// Common
const float INFINITY = 1e32;

const float DEG_TO_RAD = 0.0174533;

// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed) {
  return fract(sin(seed) * 43758.5453);
}

// Cosine direction
vec3 Cosine(in float seed, in vec3 nor) {
  float u = Hash(78.233 + seed);
  float v = Hash(10.873 + seed);

  // method 3 by fizzer: http://www.amietia.com/lambertnotangent.html
  float a = 6.2831853 * v;
  u = 2. * u - 1.;
  return normalize(nor + vec3(sqrt(1. - u * u) * vec2(cos(a), sin(a)), u));
}

// Rotation matrix around z axis
// a : Angle
mat3 rotate_z(float a) {
  float sa = sin(a);
  float ca = cos(a);
  return mat3(ca, sa, 0., -sa, ca, 0., 0., 0., 1.);
}

// Compute the ray
// m : Mouse position
// p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m, in vec2 p, out vec3 ro, out vec3 rd) {
  float a = 3. * 3.14 * m.x;
  float le = 3.8;

  ro = vec3(20., 0., 5.);
  ro *= rotate_z(3. * 3.14 * m.x);

  vec3 ta = vec3(0., 0., 1.);
  vec3 ww = normalize(ta - ro);
  vec3 uu = normalize(cross(ww, vec3(0., 0., 1.)));
  vec3 vv = normalize(cross(uu, ww));
  rd = normalize(p.x * uu + p.y * vv + le * ww);
}

// Main

const int Steps = 1000;
const float Epsilon = .05;// Marching epsilon
const float T = .5;

const float rA = 10.;// Maximum and minimum ray marching or sphere tracing distance from origin
const float rB = 40.;

// Transforms
vec3 rotateX(vec3 p, float a) {
  float sa = sin(a);
  float ca = cos(a);
  return vec3(p.x, ca * p.y - sa * p.z, sa * p.y + ca * p.z);
}

vec3 rotateY(vec3 p, float a) {
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x + sa * p.z, p.y, -sa * p.x + ca * p.z);
}

vec3 rotateZ(vec3 p, float a) {
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x + sa * p.y, -sa * p.x + ca * p.y, p.z);
}

vec3 translateX(vec3 p, float tx) {
  return vec3(p.x - tx, p.yz);
}

vec3 translateY(vec3 p, float ty) {
  return vec3(p.x, p.y - ty, p.z);
}

vec3 translateZ(vec3 p, float tz) {
  return vec3(p.xy, p.z - tz);
}

vec3 translate(vec3 p, vec3 t) {
  return p - t;
}

vec3 scaleX(vec3 p, float sx) {
  return vec3(p.x / sx, p.yz);
}

vec3 scaleY(vec3 p, float sy) {
  return vec3(p.x, p.y / sy, p.z);
}

vec3 scaleZ(vec3 p, float sz) {
  return vec3(p.xy, p.z / sz);
}

vec3 scaleXYZ(vec3 p, vec3 s) {
  return p / s;
}

vec3 scale(vec3 p, float s) {
  return p / s;
}

// Smooth cubic falloff function
// x : distance
// R : radius
float falloff(float x, float R) {
  float u = clamp(x / R, 0., 1.);
  float v = (1. - u * u);
  return v * v * v;
}

// Primitives

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : radius
vec4 point(vec3 p, vec3 c, float e, float R, vec3 color) {
  return vec4(color, e * falloff(length(p - c), R));
}

// Disk
// p : point
// c : center of disk
// r : radius of disk
// e : energy associated to skeleton
// R : radius
// color : color vector
vec4 disk(vec3 p, vec3 c, float r, float e, float R, vec3 color) {
  vec3 cp = p - c;
  vec3 u = vec3(1., 0., 0.);
  float h = dot(cp, u);
  h = h * h;
  float d = dot(cp, cp);
  float y = d - h * h;
  if(y < r * r) {
    return vec4(color, e * falloff(abs(h), R));
  }
  y = r - sqrt(y);
  return vec4(color, e * falloff(sqrt(y * y + h * h), R));
}

// Circle
// p : point
// c : center of circle
// r : radius of the circle
// e : energy associated to skeleton
// R : radius
// color : color vector
vec4 circle(vec3 p, vec3 c, float r, float e, float R, vec3 color) {
  vec3 n = p - c;
  vec3 u = vec3(1., 0., 0.);
  float h = dot(n, u);
  float d = dot(n, n);
  float y = d - h * h;
  y = r - sqrt(y);
  return vec4(color, e * falloff(sqrt(y * y + h * h), R));
}

// Segment
// p : point
// a : starting coordinates
// b : ending coordinates
// e : energy associated to skeleton
// R : radius
// color : color vector
vec4 segment(vec3 p, vec3 a, vec3 b, float e, float R, vec3 color) {
  vec3 u = (b - a) / length(b - a);
  float l = dot(p - a, u);

  if(l < 0.0) {
    float dist = length(p - a);
    return vec4(color, e * falloff(dist, R));
  }

  if(l < length(b - a)) {
    float left = dot(p - a, p - a);
    float right = dot(p - a, u) * dot(p - a, u);

    return vec4(color, e * falloff(sqrt(left - right), R));
  }

  return vec4(color, e * falloff(length(p - b), R));
}

float box_bis(float px, float ax, float bx) {
  if(px < ax) {
    return ax - px;
  }

  if(px > bx) {
    return px - bx;
  }

  return 0.0;
}

// Box
// p : point
// a : coordinates of the cube
// b : coordinate of the cube
// e : energy associated to skeleton
// R : radius
// color : color vector
vec4 box(vec3 p, vec3 a, vec3 b, float e, float R, vec3 color) {
  float d1 = box_bis(p.x, a.x, b.x);
  float d2 = box_bis(p.y, a.y, b.y);
  float d3 = box_bis(p.z, a.z, b.z);

  return vec4(color, e * falloff(sqrt(d1 * d1 + d2 * d2 + d3 * d3), R));
}

// Sphere
// p : point
// c : center of sphere
// r : radius of the sphere
// e : energy associated to skeleton
// R : radius
// color : color vector
vec4 sphere(vec3 p, vec3 c, float r, float e, float R, vec3 color) {
  return vec4(color, e * falloff(length(p - c) - r, R));
}

// Hemisphere
// p : point
// c : center of hemisphere
// r : radius of the hemisphere
// e : energy associated to skeleton
// R : radius
// color : color vector
vec4 hemisphere(vec3 p, vec3 c, float r, float e, float R, vec3 color) {
  vec3 n = p - c;
  vec3 u = vec3(1.0, 0.0, 0.0);
  if(dot(n, u) < 0.0) {
    return disk(p, c, r, e, R, color);
  }
  return sphere(p, c, r, e, R, color);
}

// Operators

// Blending
// a,b : field function of the sub-trees
vec4 Blend(vec4 a, vec4 b) {
  float denom = a.w + b.w;
  vec3 color;
  if(denom == 0.0){
    color = (a.rgb +  b.rgb) / 2.0;
  }
  else {
    color = ( a.w * a.rgb + b.w * b.rgb) / (a.w + b.w); 
  }

  return vec4(color, denom);
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
vec4 Union(vec4 a, vec4 b) {
  float v = max(a.w, b.w);
  if(v == b.w){
    return b;
  }
  return a;
}

// Intersection
// a : filed function of left sub-tree
// b : field function of right sub-tree
vec4 Intersection(vec4 a, vec4 b) {
  float v = min(a.w, b.w);
  if(v == b.w){
    return b;
  }
  return a;
}

// Difference
// a : filed function of left sub-tree
// b : field function of right sub-tree
vec4 Difference(vec4 a, vec4 b) {
  float comp = 2.0 * T - b.w;
  float v = min(a.w, comp);

  if(v == a.w){
    return a;
  }

  return vec4(a.rgb, comp);
}

