// Rabbit ear
vec4 lapin_oreille(vec3 p){
    vec4 v;

    v = Union(
      disk(scaleXYZ(p, vec3(0.6, 0.7, 1.8)), vec3(0, 0, 0), 1.0, 1.0, 0.6, vec3(1, 1, 1)),
      disk(translateX(scaleXYZ(p, vec3(0.5, 0.5, 1.6)), 0.2), vec3(0, 0, 0), 1.0, 1.0, 0.6, vec3(0.94, 0.80, 0.73))
    );

    return v;
}

// Rabbit eye
vec4 lapin_oeil(vec3 p){
    vec4 v;

    v = Union(
      disk(translateX(scale(p, 0.4), -0.3), vec3(0, 0, 0), 1.3, 1.0, 1.0, vec3(0.94, 0.80, 0.73)),
      sphere(translateX(p, 0.3), vec3(0, 0, 0), 0.1, 1.0, 1.0, vec3(1, 1, 1))
    );

    v = Union(
      v,
      hemisphere(translateX(scale(p, 0.6), 1.0), vec3(0, 0, 0), 0.1, 1.0, 1.0, vec3(0, 0, 0))
    );

    return v;
}

// Rabbit mouth
vec4 lapin_bouche(vec3 p){
  vec4 v;
  vec3 p_levre = scaleXYZ(translate(p, vec3(-0.1, 0, -1.0)), vec3(0.6, 0.45, 0.45));

  v = Difference(
    circle(p_levre, vec3(0, 0, 0), 3.0, 1.0, 0.5, vec3(0, 0, 0)),
    box(scaleXYZ(translateZ(p_levre, -0.8), vec3(1.0, 3.0, 2.38)), vec3(-1, -1, -1), vec3(1, 1, 1), 1.0, 1.0, vec3(1.0, 1.0, 1.0))
  );

  v = Union(
    v,
    hemisphere(translate(p, vec3(-1.6, 0, 0)), vec3(0, 0, 0), 1.2, 1.0, 1.0, vec3(0.94, 0.80, 0.73))
  );

  return v;
}

// Rabbit head
vec4 lapin_tete(vec3 p){
    vec4 v;

    v = sphere(scaleZ(p, 1.3), vec3(0, 0, 0), 2.0, 1.0, 1.0, vec3(1, 1, 1));

    v = Blend(
