// Potential field of the object
// p : point
vec4 Object(vec3 p) {
  vec4 v;
    vec3 p_tete;
    vec3 p_main;

  p.z = -p.z;

  p = scale(p, 0.6);

    p_tete = translateZ(p, -3.0);

    v = lapin_tete(rotateY(p_tete, sin(iTime * 7.0) * 10.0 * DEG_TO_RAD));

    v = Blend(
        v,
        lapin_ventre(translateZ(p, 1.5))
    );

    // JAMBES
    v = Blend(
        v,
        lapin_jambe(translate(rotateY(p, sin(iTime * 7.0) * 0.4), vec3(0, -1.1, 3.5)))
    );

    v = Blend(
        v,
        lapin_jambe(translate(rotateY(p, sin(iTime * 7.0) * -0.4), vec3(0, 1.1, 3.5)))
    );

    p_main = scaleXYZ(p, vec3(0.7, 1.0, 0.8));
    v = Blend(
        v,
        lapin_main(rotateX(translateY(rotateY(p_main, sin(iTime * 7.0)), 1.0), -40.0 * DEG_TO_RAD), 1.0)
    );

    v = Blend(
        v,
        lapin_main(rotateX(translateY(rotateY(p_main, sin(iTime * -7.0)), -1.0), 40.0 * DEG_TO_RAD), -1.0)
    );


  return vec4(v.rgb, v.w - T);
}

float F(vec3 p){
  return -Object(p).a;
}

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p, out vec3 obj_colors) {
  float eps = .0001;
  vec3 n;
  vec4 v = Object(p);
  n.x = Object(vec3(p.x + eps, p.y, p.z)).w - v.w;
  n.y = Object(vec3(p.x, p.y + eps, p.z)).w - v.w;
  n.z = Object(vec3(p.x, p.y, p.z + eps)).w - v.w;
  obj_colors = v.rgb;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o, vec3 u, float rB, out bool h, out int s) {
  h = false;
  const int nb_iter = 500;

  // Don't start at the origin, instead move a little bit forward
  float t = rA;

  for(int i = 0; i < nb_iter; i++) {
    s = i;
    vec3 p = o + t * u;
    vec4 v = Object(p);
    // Hit object
    if(v.w > 0.) {
      s = i;
      h = true;
      break;
    }
    // Move along ray
    t += Epsilon;
    // Escape marched far away
    if(t > rB) {
      break;
    }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTraceOriginal(vec3 o, vec3 u, float rB, out bool h, out int s) {
  h = false;
  int nb_iter = 180;
  // Don't start at the origin, instead move a little bit forward
  float t = rA;

  for(int i = 0; i < nb_iter; i++) {
    s = i;
    vec3 p = o + t * u; // <=> d * t + o    (Keinert and al.)
    vec4 v = Object(p); // <=> f(d * t + o) (Keinert and al.)
    // Hit object
    if(v.a > 0.) {
      s = i;
      h = true;
      break;
    }
    // Move along ray
    t += max(Epsilon, abs(v.a) / 6.);
    // Escape marched far away
    if(t > rB) {
      break;
    }
  }
  return t;
}

// B. Keinert et al. Enhanced sphere tracing. In Proceedings of Smart Tools & Apps for Graphics, Eurographics Association, 2014.
float SphereTraceOverRelaxing(vec3 o, vec3 u, float rB, out bool h, out int s) {
  h = false;
  int i;
  float omega = 1.25;
  float lmbd = 4.0;
  float coeff = omega / lmbd;
  float delta_i = 0.0;
  float f_pi_minus = 0.0;
  // Don't start at the origin, instead move a little bit forward
  float t = rA;
  const int nb_iter = 100;


  for( i = 0; i < nb_iter; i++) {
    s = i;
    vec3 p = o + t * u; // <=> d * t + o    (Keinert and al.)
    float f_pi = F(p); // <=> f(d * t + o) (Keinert and al.)

    // Hit object
    if(f_pi < 0.0) {
      h = true;
      return t;
    }

    f_pi = abs(f_pi);

    if((f_pi + f_pi_minus)/lmbd < delta_i){
      delta_i = -omega * delta_i;
      omega = 1.0;
      coeff = omega / lmbd;

      t += delta_i;
      while(i < nb_iter) {
        s = i;
        p = o + t * u; // <=> d * t + o    (Keinert and al.)
        f_pi = F(p); // <=> f(d * t + o) (Keinert and al.)

        // Hit object
        if(f_pi < 0.0) {
          h = true;
          return t;
        }

        delta_i = max(Epsilon, coeff * f_pi);

        // Move along ray
        t += delta_i;

        i++;
      }
      return INFINITY;
    }

    delta_i = max(Epsilon, coeff * f_pi);


    f_pi_minus = f_pi;


    // Move along ray
    t += delta_i;
  }

  return INFINITY;
}

// B. Keinert et al. Enhanced sphere tracing. In Proceedings of Smart Tools & Apps for Graphics, Eurographics Association, 2014.
float SphereTraceScreenSpace(vec3 o, vec3 u, float rB, out bool h, out int s) {
  int i;
  float pixelRadius = 0.001;
  float omega = 1.4;
  float lmbd = 4.0;
  float coeff = omega / lmbd;
  float delta_i = 0.0;
  float f_pi_minus = 0.0;
  float candidate_error = INFINITY;
  float candidate_t = rB;
  float functionSign = F(o) < 0.0 ? -1.0 : 1.0;
  // Don't start at the origin, instead move a little bit forward
  float t = rA;
  int nb_iter = 100;
  float f;
  float error;
  h = false;


  for( i = 0; i < nb_iter; i++) {
    s = i;
    f = F(u * t + o);
    float signedRadius = functionSign * f;
    float f_pi = abs(signedRadius);

    bool sorFail = omega > 1.0 && (f_pi + f_pi_minus < delta_i);
    if(sorFail){
      delta_i -= coeff * delta_i;
      omega = 1.0;
      lmbd = 40.0;
      coeff = omega / lmbd;
    }
    else {
      // delta_i = max(Epsilon * signedRadius, signedRadius * omega);
      delta_i = max(Epsilon * signedRadius, signedRadius * coeff);
      // delta_i = signedRadius * omega;
    }

    f_pi_minus = f_pi;

    error = f_pi / t;

    if(!sorFail && error < candidate_error){
      candidate_t = t;
      candidate_error = error;
    }

    if(!sorFail && error < pixelRadius || t > rB){
      break;
    }

    // Move along ray
    t += delta_i;
  }

  if(t > rB && candidate_error > pixelRadius || i == nb_iter){
    return INFINITY;
  }

  h = true;
  return candidate_t;
}



float SphereTrace(vec3 o, vec3 u, float rB, out bool h, out int s){
  // return Trace(o, u, rB, h, s);
  // return SphereTraceOriginal(o, u, rB, h, s);
  return SphereTraceOverRelaxing(o, u, rB, h, s);
  // return SphereTraceScreenSpace(o, u, rB, h, s);
}


// Ambient occlusion
// p : Point
// n : Normal
// a : Number of smaples
float AmbientOcclusion(vec3 p, vec3 n, int a) {
  if(a == 0) {
    return 1.;
  }

  float ao = 0.;

  for(int i = 0; i < a; i++) {
    vec3 d = Cosine(581.123 * float(i), n);

    int s;
    bool h;
    float t = SphereTrace(p, d, 10., h, s);
    if(!h) {
      ao += 1.;
    } else if(t > 5.) {
      ao += 1.;
    }
  }

  ao /= float(a);
  return ao;
}

// Background color
vec3 background(vec3 rd) {
  return mix(vec3(.652, .451, .995), vec3(.552, .897, .995), rd.z * .5 + .5);
}

float Light(vec3 p, vec3 n) {
  // point light
  const vec3 lp = vec3(5., 10., 25.);

  vec3 l = normalize(lp - p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff = pow(.5 * (1. + dot(n, l)), 2.);

  bool h;
  int s;
  // float t = SphereTrace(p + .1 * n, l, 100., h, s);
  if(!h) {
    return diff;
  }
  return 0.;
}

float SmoothLight(vec3 p, vec3 n, int a) {
  if(a == 0)
    return 1.;

  // point light
  const vec3 lp = vec3(5., 10., 25.);

  vec3 l = normalize(lp - p);

  float lo = 0.;

  for(int i = 0; i < a; i++) {
    vec3 d = Cosine(581.123 * float(i), n);
    d = normalize(l + d * .15);
    int s;
    bool h;
    float t = SphereTrace(p, d, 10., h, s);
    if(!h) {
      lo += 1.;
    } else if(t > 100.) {
      lo += 1.;
    }
  }

  lo /= float(a);
  return lo;

}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p, vec3 n) {
  vec3 c = .25 + .25 * background(n);
  c += .15 * AmbientOcclusion(p + .1 * n, n, 0) * vec3(1., 1., 1.);
  c += .35 * Light(p, n);
  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n) {
  float t = float(n) / (float(Steps - 1));
  return .5 + mix(vec3(.05, .05, .5), vec3(.65, .39, .65), t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip) {
  // Pixel coordinates
  vec2 p = (-iResolution.xy + 2. * pixel) / iResolution.y;
  if(pip == true) {
    const float fraction = 1. / 4.;
    // Recompute pixel coordinates in sub-picture
    if((pixel.x < iResolution.x * fraction) && (pixel.y < iResolution.y * fraction)) {
      p = (-iResolution.xy * fraction + 2. * pixel) / (iResolution.y * fraction);
      pip = true;
    } else {
      pip = false;
    }
  }
  return p;
}

// Image
void mainImage(out vec4 color, in vec2 pxy) {
  // Picture in picture on
  bool pip = true;

  // Pixel
  vec2 pixel = Pip(pxy, pip);

  // Mouse
  vec2 m = iMouse.xy / iResolution.xy;

  // Camera
  vec3 ro, rd;
  Ray(m, pixel, ro, rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100., hit, s);

  // Position
  vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if(hit) {
    // Compute normal
    vec3 obj_colors;
    vec3 n = ObjectNormal(pt, obj_colors);

    // Shade object with light
    rgb = Shade(pt, n) * obj_colors;
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if(pip == true) {
    rgb = ShadeSteps(s);
  }

  color = vec4(rgb, 1.);
}

#define AA 1
#define EPS .001
#define MAX 50.

vec2 hash2(vec2 p)
{
    return fract(sin(p*mat2(98,-64,-73,69))*397.)*.8;
}
float height(vec2 p)
{
    return p.x+sin(p.y*.3)*3.-iTime;
}
float cell(vec3 p)
{
    vec2 f = floor(p.xz);
    float d = 4.;

    for(int X = -1; X<=1; X++)
    for(int Y = -1; Y<=1; Y++)
    {
        vec2 h = f+vec2(X,Y);
        h += hash2(h)-p.xz;

        vec3 c = vec3(h,p.y+1.);
        vec2 R = normalize(sin(c.xy+p.xz));
        mat2 r = mat2(R,-R.y,R);

        float off = height(p.xz+c.xy);
        c.z -= sqrt(abs(off))-1.;
        c.z = max(c.z,0.);

        float s = .13*smoothstep(-.2,.2,off);
        c.xy *= r;
        float w = .15;
        d = min(d, length(max(abs(c)-s,0.))+s-w);
    }

    return d;
}
float dist(vec3 p)
{
    return min(p.y+1.,cell(p));
}
vec3 normal(vec3 p)
{
    vec2 e = vec2(2,-2)*EPS;
    return normalize(dist(p+e.xxy)*e.xxy+dist(p+e.xyx)*e.xyx+
    dist(p+e.yxx)*e.yxx+dist(p+e.y)*e.y);
}
vec3 color(vec3 p,vec3 r)
{
    float off = height(p.xz);
    float s = smoothstep(-.2,.2,off);

    float l = cell(vec3(p.x,-2,p.z));
    float e = smoothstep(.02,0.,l);


    vec3 n = normal(p);
    float ao = clamp(dist(p+n*.2)/.2,.1,1.);
    vec3 sd = normalize(vec3(3,2,-1));
    float dl = max(.3+.7*dot(n,sd),0.);
    float sl = max(dot(reflect(r,n),sd)*1.2-1.,0.);

    for(float i = .02;i<.5; i*=1.3)
    {
        dl *= clamp(1.5-i/(i+dist(p+sd*i*2.)),.0,1.);
    }
    vec3 sh = mix(vec3(.1,.15,.2),vec3(1),dl);

    vec3 col = mix(vec3(0.7,1,.2),vec3(1,0.4,0.1),s);
    return mix(vec3(.5,.7,.8),col*min((p.y+1.1)/.4,1.),e)*sh*sqrt(ao)+sl;
}
vec4 march(vec3 p,vec3 r)
{
    vec4 m = vec4(p+r,1);
    for(int i = 0;i<200;i++)
    {
        float s = dist(m.xyz);
        m += vec4(r,1)*s;

        if (s<EPS || m.w>MAX) return m;
    }
    return m;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 p = vec3(iTime-2.,.5+.5*cos(iTime*.2),1);
    vec3 col = vec3(0);
    for(int X = 0;X<AA;X++)
    for(int Y = 0;Y<AA;Y++)
    {
        vec2 c = fragCoord+vec2(X,Y)/float(AA)-.5;
        vec3 r = normalize(vec3(c-vec2(.5,.6)*iResolution.xy,iResolution.y));

        vec4 m = march(p,r);
        float fog = smoothstep(MAX*.4,MAX,m.w);
        col += mix(color(m.xyz,r),exp(-vec3(13,7,4)*r.y*r.y-.2),fog);
    }
    col /= float(AA*AA);
    fragColor = vec4(pow(col,vec3(.45)),1);
}

