    return blob3(s1, s2, s3);
}

float rayMaching(vec3 ro, vec3 rd){
    float d0 = 0.0;
    for(int i; i <= Max_Steps; i++){
        vec3 p = ro + rd * d0;
        float ds = collision(p);
        d0 += ds;
        if(d0 > Max_Dist || ds < Surf_Dist){
            break;
        }
    }
    return d0;   
}

vec3 getNormal(vec3 p){
    float d = collision(p);
    vec2 e = vec2(0.01,0);

    vec3 n = d-vec3(
        collision(p-e.xyy),
        collision(p-e.yxy),
        collision(p-e.yyx));

    return normalize(n);
}

float getLight(vec3 p){
    vec3 lightPos = vec3(0,5,6);
    lightPos.xz += vec2(sin(iTime),cos(iTime))*2.0;
    vec3 l = normalize(lightPos-p);// 点光源
    vec3 n = getNormal(p);
    float dif = clamp(dot(n,l),0.,1.);//漫反射颜色

    float d = rayMaching(p + n*Surf_Dist*2.0,l);
    if(d<length(lightPos-p))dif*=0.1;

    return dif;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0.5,0);
    vec3 rd = normalize(vec3(uv,1));
    float d = rayMaching(ro, rd);
    vec3 p = ro + d * rd;

    vec3 col = vec3(getLight(p));

    fragColor = vec4(col, 1.0);
}

// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Slice(p,a) dot(p,vec2(cos(a),sin(a)))
#define S(p,d,b) smoothstep(fwidth(p.y),0.0,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define MATERIAL_OUTSOLE 0
#define MATERIAL_MIDSOLE1 1
#define MATERIAL_MIDSOLE2 2
#define MATERIAL_UPPER 3

#define ZERO (min(iFrame,0))

// IQ's 3D noise function. It's faster than the FBM and enough for me to design.
// The compile is also faster.
float noise3d( vec3 p )
{
	const vec3 s = vec3(27, 111, 57);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); 
    //p *= p*p*(p*(p*6. - 15.) + 10.); // option
    h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float baseOutsole(vec3 p, float h){
    vec3 prevP = p;

    p.xy*=Rot(radians(90.0));
    p.yz*=Rot(radians(15.0));
    p.y*=1.5;
    float d = sdCappedCylinder(p-vec3(0.0,0.0,0.25),h,0.3);

    p = prevP;
    p.xy*=Rot(radians(90.0));
    p.y*=1.5;
    p.z*=1.2;
    float d2 = sdCappedCylinder(p-vec3(0.0,0.0,-0.25),h,0.23);
    d = min(d, d2);

    p = prevP;
    d2 = sdBox(p-vec3(0,0.0,0.0),vec3(0.145,h,0.18));
    d = min(d, d2);

    p = prevP;
    p.xy*=Rot(radians(90.0));
    p.yz*=Rot(radians(15.0));
    p.y*=1.3;
    d2 = sdCappedCylinder(p-vec3(-0.03,0.3,-0.01),0.2,0.125);
    d = max(-d2,d);

    return (d)*0.6;
}

float outsole(vec3 p){
    vec3 prevP = p;
    float d = baseOutsole(p,0.02);

    p.z = mod(p.z,0.04)-0.02;
    p.y*=0.5;
    p.xz*=Rot(radians(90.0));
    p.xy*=Rot(radians(30.0));
    float d2 = sdTriPrism(p-vec3(0.0,0.01,0.0),vec2(0.03,0.3));

    d = max(d,d2);
    p = prevP;
    d2 = baseOutsole(p-vec3(0.0,0.02,0.0),0.01);
    d = min(d,d2);

    return d;
}

vec2 midsole(vec3 p){
    float d = baseOutsole(p-vec3(0.0,0.03,0.0),0.01);
    float d2 = baseOutsole(p-vec3(0.0,0.06,0.0),0.02);

    p.yz*=Rot(radians(-3.0));
    float mask = p.y-0.07;
    d2 = max(mask,d2);

    vec2 model = vec2(d,MATERIAL_MIDSOLE1);
    vec2 model2 = vec2(d2,MATERIAL_MIDSOLE2);

    return combine(model,model2);
}

float upperBase(vec3 p){
    vec3 prevP = p;
    p.xz*=1.03;

    float d2 = baseOutsole(p-vec3(0,0.19,0),0.15);

    p.z-=0.06;
    p.yz*=Rot(radians(40.0));

    float mask = p.y-0.2;
    d2 = max(mask,d2);

    p = prevP;
    p.yz*=Rot(radians(-10.0));
    p.xz*=Rot(radians(15.0));
    float d = sdEllipsoid(p-vec3(0.0,0.095,0.0),vec3(0.23,0.24,0.53));

    d = max(d,d2);
    return d;
}

float upper(vec3 p){
    vec3 prevP = p;
    float d = upperBase(p);
    p.x*=1.3;

    float d2 = upperBase(p-vec3(0.0,-0.02,-0.03));
    d = max(-d2,d);

    float prevD = d;

    p = prevP;
    p.yz*=Rot(radians(-10.0));
    p.xz*=Rot(radians(15.0));
    p.z*=0.6;
    p = mod(p,0.11)-0.055;
    d2 = length(p)-0.05;

    d = max(-d2,d);

    p = prevP;
    p.z-=0.1;
    p.yz*=Rot(radians(40.0));

    float mask = p.y-0.2;
    d2 = max(-mask,prevD);    

    return min(d,d2);
}

vec2 GetDist(vec3 p) {
    p+=noise3d(p*300.0)*0.001;
    vec3 prevP = p;

    p.y+=0.1;
    p.z+=0.1;
    float d = outsole(p);

    vec2 model = vec2(d,MATERIAL_OUTSOLE);
    vec2 model2 = midsole(p);

    d = upper(p);

    vec2 model3 =vec2(d,MATERIAL_UPPER);

    return combine(model,combine(model2,model3));
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,2,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    float nn = noise3d(p*80.0)*0.12;
    if(mat == MATERIAL_OUTSOLE){
        col = diffuseMaterial(n,rd,p,vec3(1.0,1.0,0.9)+nn);
    } else if(mat == MATERIAL_MIDSOLE1){
        col = diffuseMaterial(n,rd,p,vec3(1.0,0.6,0.0)+nn);
    } else if(mat == MATERIAL_MIDSOLE2){
        col = diffuseMaterial(n,rd,p,vec3(0.6,0.6,0.6))+nn;
    } else if(mat == MATERIAL_UPPER){

        col = diffuseMaterial(n,rd,p,vec3(0.7,0.2, 0.3)+nn+nn);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m =  iMouse.xy/iResolution.xy -.3;

    vec3 ro = vec3(0, 0, 1.0);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-30.0));
    if(iMouse.z>0.){
        ro.yz *= Rot(-m.y*3.14+1.);
        ro.xz *= Rot(-m.x*6.2831);
    } else {
        ro.xz *= Rot(radians(-15.0+iTime*20.0));

        //ro.xz *= Rot(radians(45.0));
    }
    #endif

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        col *= exp( -0.038*d.x*d.x*d.x );//fog
    } else {
        //col = vec3(1.0,1.0,0.2);
        //col = vec3(0.95);
    }


    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(col,1.0);
}

/*

    Abstract Geometric Pattern
    --------------------------

    Applying a very simple procedure involving multiple colored square overlays 
    and basic lighting to produce a greeble-like surface in the style of abstract 
    art... or something to that effect. :)

    I'd been experimenting with procedural greebled surfaces a while ago, and this 
    was just a byproduct of that. I wouldn't call it a greebled surface per se, but 
    if you mapped it onto a 3D surface, it'd have that feel. Thanks to the basic 
    lighting, it also has a painted feel. On a side note, a lot of wrapped 
    procedural textures you see have usually been highlighted in a similar wrappable 
    unidirectional way.

    The technique is about as simple as it gets: Combine some offset squares to 
    form a polygon with squared sides. Construct a square grid and render these
    in each cell. After that, combine a few resultant layers at various frequencies.
    The method is different, but similar to stacking Voronoi layers.

    I didn't put a lot of effort into rendering speed, as this is the kind of thing
    that you should probably prerender into one of the buffers prior to use. By the
    way, I have a couple of 3D greeble examples that I'll post later.


    Other examples:
    // Similar principals, and a nice result. By the way Piyushslayer has
    // some other really nice examples.
    Abstract Squircles - piyushslayer 
    https://www.shadertoy.com/view/Wtc3D8


*/


// Just the two palattes - Vibrant: 0, Earth: 1.
#define PALETTE 0

// Base shape -  Square: 0, Diamond: 1., Hexagon: 2, Octagon: 3, Circle: 4.
#define SHAPE 0


// IQ's vec2 to float hash.
float hash21(vec2 p){ return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453); }


// Compact, self-contained version of IQ's 2D value noise function.
float n2D(vec2 p){

    // Setup.
    // Any random integers will work, but this particular
    // combination works well.
    const vec2 s = vec2(1, 113);
    // Unique cell ID and local coordinates.
    vec2 ip = floor(p); p -= ip;
    // Vertex IDs.
    vec4 h = vec4(0., s.x, s.y, s.x + s.y) + dot(ip, s);

    // Smoothing.
    p = p*p*(3. - 2.*p);
    //p *= p*p*(p*(p*6. - 15.) + 10.); // Smoother.

    // Random values for the square vertices.
    h = fract(sin(h)*43758.5453);

    // Interpolation.
    h.xy = mix(h.xy, h.zw, p.y);
    return mix(h.x, h.y, p.x); // Output: Range: [0, 1].
}

// FBM -- Accumulated noise layers of modulated amplitudes and frequencies.
float fBm(vec2 p){ 

    // Layer the noise.
    float ns = 0., sum = 0., a = 1.;
    for(int i = 0; i<5; i++){

        ns += n2D(p)*a;
        p *= 2.5;
        sum += a;
        a /= 1.5;
    }

    return ns/sum; // Range: [0, 1].
}

/*
float sBoxS(in vec2 p, in vec2 b, in float rf){

  vec2 d = abs(p) - b + rf;
  return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - rf;

}
*/

// Shape distance metrics. You could put whatever you feel like here.
float dist(vec2 p, vec2 b){


      #if SHAPE == 0
      // Square.
      p = abs(p) - b;
      return max(p.x, p.y);
      #elif SHAPE == 1
      // Diamond.
      p = abs(p);
      return (p.x + p.y)*.7071 - mix(b.x, b.y, .5);
      #elif SHAPE == 2
      // Hexagon.
      p = abs(p);
      return max(p.x*.8660254 + p.y*.5, p.y) - mix(b.x, b.y, .5);
      #elif SHAPE == 3
      // Octagon.
      p = abs(p);
      return max((p.x + p.y)*.7071, max(p.x, p.y)) - mix(b.x, b.y, .5);
      #else
      // Circle.
      return length(p) - (b.x + b.y)/2.;
      #endif

      //p = abs(p) - b;
      //p = p*.8660254 + p.yx*.5;
      //return max(p.x, p.y);

      //p = abs(p)-b;
      //p = (p + p.yx)*.7071;
      //return max(p.x, p.y);
}


// The cell shape: This is just a union of four offset shapes, but it could
// be anything you can dream up. This particular random shape exceeds the
// boundaries of the cell, so surrounding cells need to be accounted for.

float cellShape(vec2 p, vec2 ip){

    float d = 1e5;

    for(int i = 0; i<4; i++){

        // Four random values.
        float fi = 1./(1. + float(i));
        vec4 rnd = vec4(hash21(ip + .15*fi), hash21(ip + .23*fi), 
                        hash21(ip + .32*fi), hash21(ip + .41*fi));

        // Offset position.
        vec2 q = p - (rnd.xy - .5)*1.5;

        // Render a square, diamond, or whatever.
        //float shape = sBoxS(q, .1 + rnd.zw*.3, min(rnd.z, rnd.w)*.07);
        float shape = dist(q, .1 + rnd.zw*.3); // Rectangular dimensions.
        //float shape = dist(q, vec2(.1 + length(rnd.zw)*.2)); // Square.

        // Take the minimum (union) of all combined shapes.
        d = min(d, shape);

    }

    // Holes... Didn't work. :)
    //d = abs(d  + .135) - .135;

    return d; // Return the random distance, or bound.

}



// Constructing the grid pattern: Render a random shape in each cell.
vec4 gridPattern(vec2 p, float sf){

    const vec2 sc = vec2(1);

    vec3 col = vec3(1);

    float alpha = .9;
    float d = 1e5;

    // The cell shapes exceed the cell boundaries, which means covering all 
    // surrounding cells that the shape covers. In this case, there are 9.
    for(int i=-1; i<=1; i++){
        for(int j=-1; j<=1; j++){

            // Local cell coordinates and cell ID.
            vec2 cntr = vec2(i, j) - .5;
            vec2 q = p;
            vec2 ip = floor(q/sc - cntr) + .5; 
            q -= ip*sc;

            // Cell shape.
            float c = cellShape(q, ip);

            // Using the cell ID to produce some random number.
            vec2 rnd = vec2(hash21(ip/23.), hash21(ip/113.)) - .5;
            rnd.y = smoothstep(.985, .997, sin(rnd.y*6.2831 + iTime/2.)*.5 + .5);

            // Feeding the random cell-ID-based value into IQ's cool palette formula
            // to produce the shape color.
            #if PALETTE == 1
            // Earth tones.
            vec3 cellCol = (.5 + .46*cos(rnd.x*6.2831/2. + vec3(0, 1, 2)))/3.5;
            // Blinking highlights.
            cellCol = mix(cellCol, mix(cellCol, cellCol*vec3(1.8, .9, .3), 1.), rnd.y); 
            #else
            // Vibrant palette.
            vec3 cellCol = (.5 + .46*cos(rnd.x*6.2831/2.75 + vec3(0, 1, 2) + 2.2));
            // Blinking highlights.
            cellCol = mix(cellCol, mix(cellCol, cellCol.xzy, .6), rnd.y); 
            #endif

            // Greyscale.
            //cellCol = vec3(dot(cellCol, vec3(.299, .587, .114)))/1.5;

            // Using the shape distance to produce a bit of shading.
            float sh = max(.5 - c/.1, 0.)*max(1. - dot(q, q)*.5, 0.)*2.5;

            // Rendering to the canvas. This is the way I like to do it, but you can
            // use whatever system you're comfortable with. The following are just some
            // Photoshop type laters: Shadow, outer edge, shaded color and an inner
            // edge for decoration.
            col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., abs(c) - .01))*.5*alpha);
            col = mix(col, vec3(0), (1. - smoothstep(0., sf, abs(c) - .01)));
            col = mix(col, cellCol*sh, (1. - smoothstep(0., sf, c))*alpha);//
            col = mix(col, vec3(0), (1. - smoothstep(0., sf, abs(c + .06) - .005)));

            // Keeping a copy of the minimum overall distance for the layering routine.
            d = min(d, c);

        }
    } 

    // Return the shaded shape color and associated distance.
    return vec4(col, d);

}

// Layering the grid pattern above.
vec3 layeredPattern(vec2 p, vec3 col, float sf){


    // Frequency (associated with scale) and alpha (transparency) values.
    float freq = 1., alpha = 1.;

    for(int i=0; i<4; i++){

        // Random values based on layer count.
        float fi = 1./(1. + float(i));
        vec2 rnd = vec2(hash21(vec2(0) + .1*fi), hash21(vec2(0) + .2*fi))*2.;

        // Render this particular grid layer.

        // Color and distance value.
        vec4 gCol = gridPattern((p - rnd)*freq, sf*freq);
        // Mix a drop shadow, edge and layer color onto the previous layer.
        col = mix(col, vec3(0), (1. - smoothstep(0., sf*8.*freq, abs(gCol.w) - .01))*.5*alpha);
        col = mix(col, vec3(0), (1. - smoothstep(0., sf*freq, abs(gCol.w) - .01))*alpha);
        col = mix(col, gCol.xyz, (1. - smoothstep(0., sf*freq, gCol.w))/freq*alpha);

        // Increace the frequency
        freq *= 1.4;
        alpha *= .92;
    }

    /*
    // Overlayed diagonal stripe pattern.
    float pat = (abs(fract((p.x - p.y)*64.) - .5)*2. - .125)/64./2.;
    pat = smoothstep(0., sf, pat)*.5 + .65;
    col *= pat;
    */

    return col; // Overall texture value.

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){


    // Screen coordinates: They're already in aspect correct form,
    // so tranlation and scalar resizing is all that is necessary.
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;


    // Scale and translation.
    const float sc = 1.5;
    vec2 p = uv*sc + vec2(1, .25)*iTime/5.;

    // Coordinate perturbation.
    p += vec2(n2D(p*24.), n2D(p*24. + 5.))*.004;

    // Smoothing factor.
    float sf = sc/iResolution.y;

    // Background color -- Most of this will be covered with colored shapes.
    vec3 bg = vec3(.05);

    // Two layered pattern samples. The second will be used for 
    // highlighting.
    float sDist = 3./450.;
    vec3 col = layeredPattern(p, bg, sf);
    vec3 col2 = layeredPattern(p - normalize(vec2(1, 1))*sDist, bg, sf);

    // Two bump values. One from each direction.
    float b = max(dot(col2 - col, vec3(.299, .587, .114)), 0.)/sDist;
    float b2 = max(dot(col - col2, vec3(.299, .587, .114)), 0.)/sDist;


    // Applying some subtle fBm noise.
    float ns = fBm(p*40.)*.8 + .5;
    col *= ns;


    // Adding the bump highlights.
    col = col*(vec3(.2, .4, 1)*b*b*.005 + vec3(1, .15, .05)*b2*.04 + .6);



    /*
    // Subtle diagonal pattern overlay. Not used.
    float pat = (abs(fract((p.x - p.y)*64.) - .5)*2. - .125)/64./2.;
    pat = .25 + smoothstep(0., sf, pat);
    col *= pat;
    */

    /*
    // Cell borders.
    vec2 q = abs(fract(p) - .5);
    float bord = max(q.x, q.y) - .5;
    col = mix(col, vec3(1), (1. - smoothstep(0., sf*4., abs(bord) - .0025))*.75);
    col = mix(col, vec3(0), (1. - smoothstep(0., sf, abs(bord) - .0025)));
    */


    // Subtle vignette.
    uv = fragCoord/iResolution.xy;
    col *= pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y) , .0625);
    // Colored variation.
    //col = mix(col*vec3(1, .05, .15), col, pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y) , .125));

    // Rought gamma correction.
    fragColor = vec4(sqrt(max(col, 0.)), 1);
}

float trn(float x){
  float ret=0.;
  ret = abs(x - floor(x+.5))*2.;
  return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 xy = fragCoord.xy;
  vec2 uv = xy/iResolution.xy;  
  vec3 col = vec3(1., 1., 1.);
  float band = 50.;
  float bandrp = 30.;
  float pro = (1.+uv.y);
  vec4 w = vec4(-.006*pow((1.+uv.y),5.), .2, band*2., 0.);
  bool flr =
  mod(xy.x +
      w.z*trn(w.y*iTime + w.x*xy.y + w.a),
      band*2.)
   >= band;
  vec4 v = vec4(0.01*(.5-uv.x), 1., bandrp*sin(iTime)*(1.-uv.y), 0.);
  bool drp =
  mod(xy.x +
    v.z*sin(v.y*iTime + v.x*xy.y + v.a),
    bandrp*2.)
  >= bandrp;
  if(uv.y<.5){
    if(flr){
      col*=0.2;
    }else{
      col=vec3(1.);
    }
    col*=(.5-uv.y)*2.;
    col.gb*=1.-uv.y;
  }else{
    if(drp){
      col*=0.7;
    }else{
      col=vec3(1.);
    }
    col.gb*=pow(1.-uv.y, 2.);
   col*=uv.y-.45;
  }
  fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 40
#define MAX_DIST 40.
#define SURF_DIST .005
#define samples 32
#define LOD 2

float hash(vec2 n) {
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 184.5453);
}

float noise(vec2 n) {
    const vec2 d = vec2(0.0, 1.0);
    vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
    return mix(mix(hash(b), hash(b + d.yx), f.x), mix(hash(b + d.xy), hash(b + d.yy), f.x), f.y);
}

float sdSphere( vec3 p, float s ) { return length(p)-s; }


float smin( float a, float b, float k ) {
    float h = max(k-abs(a-b), 0.0);
    return min(a, b) - h*h*0.25/k; 
}


float getDist(vec3 p) {
    float matId;
    float final = MAX_DIST;
    float iTime = iTime; 
    p = p - vec3(0.,0.5, 5.);
    p.x = abs(p.x);
    vec3 tempP = p;
    for (int i = 0; i < 10; i++) {
        float fi = float(i + 1)  + floor(float(i) / 5.);
        vec3 pos = p;
        float xmov = -dot(p.xy, tempP.xy + tempP.xy * fi * 0.8) * 3.;
        float ymov = sign(mod(fi, 2.)) - dot(tempP.xy, tempP.xy) * 0.2 - xmov * 0.2;

        vec2 xy = vec2(xmov, ymov);

        pos.xy += xy * 0.2;
        pos.xy -= noise(pos.xy * 15. / fi) * 0.3;
        pos.xy += (vec2(sin(iTime + fi) * 2., cos(iTime / 2. - fi) * 0.5) * 0.1 * fi);  

        pos.z += sin(iTime * cos(float(i * 4))) * 0.5;
        float r = sin(fi) * 0.2;
        float n = min(sin(pos.z * float(i) * 5.), cos(pos.x * pos.y * float(i) * 10.)) * 0.1;
        float bubble = sdSphere(pos + vec3(n) * 0.1 - vec3(0.05), r);
        final = smin(final, bubble, 0.3 + final * 0.04);

        tempP = pos;
    }


    return final;
}

float rayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float matId = -1.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float res = getDist(p);
        float dS = res;
        dO += dS;

        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 normals(vec3 p, float of ) {
	float d = getDist(p);
    vec2 e = vec2(of, 0);

    vec3 n = d - vec3(
        getDist(p-e.xyy),
        getDist(p-e.yxy),
        getDist(p-e.yyx));

    return normalize(n);
}

float diffuse(vec3 p, vec3 n, vec3 lp) {
    vec3 l = normalize(lp-p);
    float dif = clamp(dot(n, l), 0., 1.);

    return dif;
}

float specular(vec3 rd, vec3 ld, vec3 n) {    
    vec3 reflection = reflect(-ld, n);
    float spec = max(dot(reflection, -normalize(rd)), 0.); 
    return spec;
}

float gaussian(vec2 i) {
const float sigma = float(samples) * .25;
    return exp( -.5* dot(i/=sigma,i) ) / ( 6.28 * sigma*sigma );
}

vec4 blur(sampler2D sp, vec2 U, vec2 scale) {
    const int  sLOD = 1 << LOD;
    vec4 O = vec4(0);
    int s = samples/sLOD;

    for ( int i = 0; i < s*s; i++ ) {
        vec2 d = vec2(i%s, i/s)*float(sLOD) - float(samples)/2.;
        O += gaussian(d) * texture(sp, U + scale * d);
    }

    return O / O.a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    float iTime = iTime * 2.;

	vec3 ro = vec3(.0, 0., 1.);
    vec3 rd = normalize(vec3(uv.x, uv.y + 0.2 , 2.));
    vec3 ld =  vec3(0., 0., 1.);
    float d = rayMarch(ro, rd);
    vec3 p = ro + rd * d;
    vec3 n = normals(p, 0.003);
    float dif = diffuse(p, n, ld); 
    float spec = specular(rd, ld, n) * 0.1;
    float fresnel = smoothstep(0.5, 0.2, dot(-rd, n));
    vec3 dispersion = vec3(noise(n.xy * 2.7), noise(n.xy * 3.), noise(n.xy * 3.3)) * 0.4; 

    vec2 camUV = fragCoord / iResolution.xy;
    vec3 cam1 =  texture(iChannel0, camUV).xyz * 0.9;
    camUV += n.xy * 0.05 * dif;
    vec3 cam2 = blur(iChannel0, camUV, vec2(0.002)).xyz * 0.9;

    col = dif * cam2;
    col += spec;
    col += cam2 * 0.15;         
    col += dispersion;
    col += fresnel * 0.2;

    if (d > MAX_DIST) { col = vec3(cam1);  }

    fragColor = vec4(col, 1.0);
}

// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define MAX_STEPS 80
#define MAX_DIST 32.
#define SURF_DIST .001
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define MATERIAL0 0
#define MATERIAL1 1
#define MATERIAL2 2
#define SPEED 2.0
#define ZERO (min(iFrame,0))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

vec3 tex(vec2 p, vec3 col, vec3 col2){
    p*=3.0;
    vec2 uv = fract(p)-0.5;
    vec2 id = floor(p);

    vec2 randP = fract(sin(id*123.456)*567.89);
    randP += dot(randP,randP*34.56);
    float rand = fract(randP.x*randP.y);

    float speed = 0.8;
    if(rand<0.5){
        uv*=Rot(radians(90.0));
        speed = 0.6;
        uv.x+=0.4;
    } else {
        uv.x-=0.4;
    }

    float dd = S(B(uv,vec2(0.04,0.5)),-0.05);
    speed*=1.8;
    uv.y*=0.5;
    uv.y+=sin(iTime*speed)*0.4;
    col=mix(col,col2,dd+S(length(uv)-0.18,-0.2));
    return col;
}

// IQ's 3D noise function. It's faster than the FBM and enough for me to design.
// The compile is also faster.
float noise3d( vec3 p )
{
	const vec3 s = vec3(27, 111, 57);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); 
    //p *= p*p*(p*(p*6. - 15.) + 10.); // option
    h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

float B3D(vec3 p, vec3 s) {
    p = abs(p)-s;
    return max(max(p.x,p.y),p.z);
}

float sdOctahedron( vec3 p, float s)
{
    p = abs(p);
    return (p.x+p.y+p.z-s)*0.57735027;
}

float ship(vec3 p){
    p*=0.6;
    vec3 prevP = p;
    p.z*=0.8;
    float d = sdOctahedron(p,0.3);
    float d2 = sdOctahedron(p-vec3(0.0,0.0,-0.25),0.2);
    d = min(d,d2);
    d2 = sdOctahedron(p-vec3(0.0,0.0,-0.5),0.1);
    d = min(d,d2);

    p.xy*=Rot(radians(iTime*-100.0));
    p.z*=0.3;
    p.x = abs(p.x);
    p.x-=0.45;
    d2 = sdOctahedron(p-vec3(0.0,0.0,-0.1),0.1);
    d = min(d,d2);
    return d;
}

float sdBase( in vec3 p)
{
    p = mod(p,4.2)-2.1;
    float d = B3D(p, vec3(0.3));
    for(float i = 1.; i<=2.; i++){
        p = abs(p);
        p -= 1.5*i;
        p.xz *= Rot(radians(i*45.0));
        p.yz *= Rot(radians(i*-30.0));
        d = min(d,B3D(p+vec3(cos(i)*0.3,sin(i)*0.5,0.0), vec3(sin(i)*0.8)));
    }
    return d;
}

vec3 path(float z)
{
    vec3 p = vec3(sin(z) * 0.7, cos(z * .8), z);
    return p;
}

vec2 GetDist(vec3 p) {
    p+=noise3d(p*1000.0)*0.1;

    vec3 q = p+1.5;
    q.z -= iTime*SPEED;
    if(mod(iTime,20.0)<15.0){
        q.z +=2.0;
    } else {
        q.z -=6.0;
    }

    q.x-=1.5;
    q.y-=1.5;
    float sp = ship(q);

    vec3 prevP = p;
    p.xy -= path(p.z).xy;

    p.z = 0.;
    float d = B3D(p,vec3(3.0,2.5,2.5));

    p = prevP;
    p.z += iTime*SPEED;
    float d2 = sdBase(p);

    p.z += iTime*SPEED*1.05;
    p.z = mod(p.z,2.0)-1.0;
    p.x = abs(p.x);
    p.x-=2.3;
    p.xy*=Rot(radians(45.0));
    float d3 = sdOctahedron(p,0.4);

    vec2 res = combine(vec2(max(-d,d2),MATERIAL0),vec2(d3,MATERIAL1));
    res = combine(res,vec2(sp,MATERIAL2));
    return res;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(p.x,p.y,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(0.8,0.7,-1.5)*diff*occ;
    diffCol += col*vec3(0.8,0.5,1.9)*skyDiff*occ;
    diffCol += col*vec3(1.3,1.3,1.7)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    if(mat == MATERIAL0){
        vec3 np = p;
        np.z+=iTime*SPEED;
        float nn = noise3d(np*5.0)*0.15;
        col = diffuseMaterial(n,rd,p,tex(p.xy,vec3(0.2,0.3,0.9)*nn+0.3,vec3(0.9,0.3,0.3)));
    } else if(mat == MATERIAL1){
        col = diffuseMaterial(n,rd,p,vec3(0.6,0.3,0.7));
    } else if(mat == MATERIAL2){
        col = diffuseMaterial(n,rd,p,vec3(0.2,0.1,1.5)+tex(p.xy,vec3(0.2,0.3,1.5),vec3(0.9,0.9,1.2)));
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float t = iTime*SPEED;
    vec3 ro = path(t+1.5);

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);

    if(mod(iTime,20.0)<15.0){
        rd*=matRotateZ(radians(iTime*15.0));
    } else {
        rd*=matRotateZ(radians(iTime*15.0))*matRotateX(radians(180.0));
    }

    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        col *= exp( -0.0001*d.x*d.x*d.x*d.x );//fog
    } else {
        col = vec3(0.0);   
    }

    // POST EFFECTS    
    rd.z+=iTime*2.0;

    // dust effect
    float c = noise3d(rd) * 0.5 + noise3d(rd * 5.0) * 0.25 + noise3d(rd * 10.0) * 0.1;    
    col+=vec3(0.7,0.1,0.9)*c*0.3;    

    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359

// Noise by IQ <3 https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) { // replace this by something better
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
float noise( in vec2 p ) {
    const float K1 = (sqrt(3.)-1.)/2.;
    const float K2 = (3.-sqrt(3.))/6.;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    uv *= 10.;
    vec2 gv = fract(uv) + .5;
    vec2 id = floor(uv);

    float circleX = sin(id.y * .1 + id.x * .32 + iTime * 2.) * .3 + 1.;
    float circleY = sin(id.y * .1 + id.x * .2 + iTime * 3.) * .2 + 1.;
    vec2 circlePos = vec2(circleX, circleY);

    float circle = length(gv - circlePos);
    float circleRadius = noise(vec2(length(uv), iTime * .2)) * sin(id.y * .1 + id.x * .2 + iTime) * .25 + .3;
    circle = smoothstep(circleRadius, circleRadius + .0001, circle) * .3;

    vec3 color = vec3(2., sin(length(gv) * 1.2), gv.x);

    color = mix(color, vec3(0.), circle);
    fragColor = vec4(color,1.0);
}

