    float dO=0.0;

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(EPSILON, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(3, 5, 4);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);
   // if(p.y<.01 && d<length(lightPos-p)) dif *= .5;

    return dif;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float tw = tw();

    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.x;
    //vec2 uv = (gl_FragCoord.xy - 0.5 * resolution.xy)  / resolution.y;
    float aspectRatio = iResolution.x / iResolution.y;
    uv.y += 0.5;

    vec2 m = vec2(0.001, 0.005) * mouse.xy * vec2(- aspectRatio, -1.0);
    //m += resolution.y / resolution.x;

    vec3 col = vec3(0);

    vec3 ro = vec3(0.0, 0.0, -3.0);   // i changed this ... this is flight through   
    // ro vec3 (l..r, o..u, distance)
    //vec3 ro = vec3(0.0);

    //ro.yz *= Rot(time);

    //ro *= Rotation3d(vec3(1.0, 0.0, 1.0), time);

    ro.xz *= Rot(0.1*time);

    ro.yz *= Rot(-m.y*3.14+1.55);
    ro.xz *= Rot(-m.x*8.);
    //ro.z += 12.;

    vec3 rd = R(uv, ro, vec3(0.0, 0.0, 0.0), +1.0);  // i changed this from vec3(0,1,0)

    float d = RayMarch(ro, rd);

    if(d < MAX_DIST)
    {
        vec3 p = ro + rd * d;

        float dif = GetLight(p);
        //col = vec3(dif);   
        col = GetNormal(p) * 0.5 + 0.5 - 0.5 * dif;
        //col = 0.2 * col;
        //float tpos = floor((sin(time*1.5)*0.5+0.5)*18.0);
        /* if (length(ti) <= abs(tpos))                          // tile position
        {
            col *= 10.; 
        } */ 
        //float fog0 = smoothstep(6.0, MAX_DIST, d);   // fog
        //col = mix(vec3(0.0), col, 90.1*fog0);
    }

    col = pow(col, vec3(.4545));    // gamma correction
    // fog #2
    float fog = 0.005;
    col *= 1.25 / (1.0  + pow(d, 3.0) * fog); // pow 2.0
    fragColor = vec4(col,1.0);
}

/*
=============================================

Limit set of rank 4 hyperbolic Coxeter groups

                                by Zhao Liang
=============================================

This program shows the limit set of rank 4 hyperbolic Coxeter groups.

Some math stuff:

Let G be a hyperbolic Coxeter group and x a point inside the hyperbolic
unit ball, the orbit S_x = { gx, g \in G } has accumulation points
(under Euclidean metric) only on the boundary of the space. We call the
accumulation points of S_x the limit set of the group, it can be proved that
this set is independent of the way x is chosen, and it's the smallest
closed subset of the boundary that is invariant under the action of the group.

The Coxeter-Dynkin diagram of a rank 4 Coxeter group of string type has the form

   A --- B --- C --- D
      p     q     r

Here A, B, D can be chosen as ususal Euclidean planes, C is a sphere orthongonal
to the unit ball. This is taken from mla's notation, and as far as I know this
has long been used by users on fractalforums. (fragmentarium)

In this animation these points are colored in "brass metal".

==========
!important
==========

The limit set is a closed set with no interior points, to show them we have
to use an approximate procedure: we simply try to reflect a point p on the
boundary to the fundamental domain up to a maximum steps, once failed then we
think p belongs to the limit set.

**So the number MAX_REFLECTIONS is an important param**, if' its set to a high
threshold then little limit set will be shown, or if it's not high enough then
the boundary of the set will look too coarse, so beware of this.

As always, you can do whatever you want to this work.

Update: thanks @mla for helping fix some bugs!
*/
// ------------------------------------------

// --------------------------
// You can try more patterns like
// (3, 7, 3), (4, 6, 3), (4, 4, 5), (5, 4, 4), (7, 3, 4), ..., etc. (5, 4, 4) is now
// my favorite! set PQR below to see the result.
// For large PQRs the limit set will become too small to be visible, you need to adjust
// MAX_REFLECTIONS and tweak with the function chooseColor to get appealling results.

#define inf        -1.

const vec3 PQR = vec3(3, 3, 7);

// --------------------------
// some global settings

#define MAX_TRACE_STEPS  100
#define MIN_TRACE_DIST   0.1
#define MAX_TRACE_DIST   100.0
#define PRECISION        0.0001
#define AA               3
#define MAX_REFLECTIONS  500
#define PI               3.141592653

// another pattern
//#define CHECKER1  vec3(0.196078, 0.33, 0.82)
//#define CHECKER2  vec3(0.75, 0.35, 0.196078)

/*
#define CHECKER1  vec3(0.82, 0.196078, 0.33)
#define CHECKER2  vec3(0.196078, 0.35, 0.92)
#define MATERIAL  vec3(0.71, 0.65, 0.26)
#define FUNDCOL   vec3(0., 0.82, .33)
*/

// Shane's color scheme
#define CHECKER1  vec3(0., 0., 0.05)
#define CHECKER2  vec3(0.2)
#define MATERIAL  vec3(10, 0.3, 0.2)
#define FUNDCOL   vec3(.3, 1, 8)


// used to highlight the limit set
#define LighteningFactor 8.
// --------------------------

vec3 A, B, D;
vec4 C;
float orb;

float dihedral(float x) { return x == inf ? 1. : cos(PI / x); }

// minimal distance to the four mirrors
float distABCD(vec3 p)
{
    float dA = abs(dot(p, A));
    float dB = abs(dot(p, B));
    float dD = abs(dot(p, D));
    float dC = abs(length(p - C.xyz) - C.w);
    return min(dA, min(dB, min(dC, dD)));
}

// try to reflect across a plane with normal n and update the counter
bool try_reflect(inout vec3 p, vec3 n, inout int count)
{
    float k = dot(p, n);
    // if we are already inside, do nothing and return true
    if (k >= 0.0)
    	return true;

    p -= 2.0 * k * n;
    count += 1;
    return false;
}

// similar with above, instead this is a sphere inversion
bool try_reflect(inout vec3 p, vec4 sphere, inout int count)
{
    vec3 cen = sphere.xyz;
    float r = sphere.w;
    vec3 q = p - cen;
    float d2 = dot(q, q);
    if (d2 == 0.0)
    	return true;
    float k = (r * r) / d2;
    if (k < 1.0)
    	return true;
    p = k * q + cen;
    count += 1;
    orb *= k;
    return false;
}

// sdf of the unit sphere at origin
float sdSphere(vec3 p, float radius) { return length(p) - 1.0; }

// sdf of the plane y=-1
float sdPlane(vec3 p, float offset) { return p.y + 1.0; }

// inverse stereo-graphic projection, from a point on plane y=-1 to
// the unit ball centered at the origin
vec3 planeToSphere(vec2 p)
{
    float pp = dot(p, p);
    return vec3(2.0 * p, pp - 1.0).xzy / (1.0 + pp);
}

// iteratively reflect a point on the unit sphere into the fundamental cell
// and update the counter along the way
bool iterateSpherePoint(inout vec3 p, inout int count)
{
    bool inA, inB, inC, inD;
    for(int iter=0; iter<MAX_REFLECTIONS; iter++)
    {
        inA = try_reflect(p, A, count);
        inB = try_reflect(p, B, count);
        inC = try_reflect(p, C, count);
        inD = try_reflect(p, D, count);
        p =  normalize(p);  // avoid floating error accumulation
        if (inA && inB && inC && inD)
            return true;
    }
    return false;
}

// colors for fundamental domain, checker pattern and limit set.
vec3 chooseColor(bool found, int count)
{
    vec3 col;
    if (found)
    {
        if (count == 0) return FUNDCOL;
        else if (count >= 300) col = MATERIAL;
        else
            col = (count % 2 == 0) ? CHECKER1 : CHECKER2;

    }
    else
        col = MATERIAL;

    float t =  float(count) / float(MAX_REFLECTIONS);
    col = mix(MATERIAL*LighteningFactor, col, 1. - t * smoothstep(0., 1., log(orb) / 32.));
    return col;
}

// 2d rotation
vec2 rot2d(vec2 p, float a) { return p * cos(a) + vec2(-p.y, p.x) * sin(a); }

vec2 map(vec3 p)
{
    float d1 = sdSphere(p, 1.0);
    float d2 = sdPlane(p, -1.0);
    float id = (d1 < d2) ? 0.: 1.;
    return vec2(min(d1, d2), id);
}

// standard scene normal
vec3 getNormal(vec3 p)
{
    const vec2 e = vec2(0.001, 0.);
    return normalize(
        vec3(
            map(p + e.xyy).x - map(p  - e.xyy).x,
            map(p + e.yxy).x - map(p  - e.yxy).x,
            map(p + e.yyx).x - map(p  - e.yyx).x
            )
        );
}

// get the signed distance to an object and object id
vec2 raymarch(in vec3 ro, in vec3 rd)
{
    float t = MIN_TRACE_DIST;
    vec2 h;
    for(int i=0; i<MAX_TRACE_STEPS; i++)
    {
        h = map(ro + t * rd);
        if (h.x < PRECISION * t)
            return vec2(t, h.y);

        if (t > MAX_TRACE_DIST)
            break;

        t += h.x;
    }
    return vec2(-1.0);
}

float calcOcclusion(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n).x;
        occ += (h - d) * sca;
        sca *= 0.75;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}


float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k) {
    float res = 1.0;
    float t = tmin;
    for (int i = 0; i < 12; i++) {
        float h = map(ro + rd * t).x;
        res = min(res, k * h / t);
        t += clamp(h, 0.01, 0.2);
        if (h < 0.0001 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}


vec3 getColor(vec3 ro, vec3 rd, vec3 pos, vec3 nor, vec3 lp, vec3 basecol)
{
    vec3 col = vec3(0.0);
    vec3 ld = lp - pos;
    float lDist = max(length(ld), .001);
    ld /= lDist;
    float ao = calcOcclusion(pos, nor);
    float sh = softShadow(pos+0.001*nor, ld, 0.02, lDist, 32.);
    float diff = clamp(dot(nor, ld), 0., 1.);
    float atten = 2. / (1. + lDist * lDist * .01);

    float spec = pow(max( dot( reflect(-ld, nor), -rd ), 0.0 ), 32.);
    float fres = clamp(1.0 + dot(rd, nor), 0.0, 1.0);

    col += basecol * diff;
    col += basecol * vec3(1., 0.8, 0.3) * spec * 4.;
    col += basecol * vec3(0.8) * fres * fres * 2.;
    col *= ao * atten * sh;
    col += basecol * clamp(0.8 + 0.2 * nor.y, 0., 1.) * 0.5;
    return col;
}

mat3 sphMat(float theta, float phi)
{
    float cx = cos(theta);
    float cy = cos(phi);
    float sx = sin(theta);
    float sy = sin(phi);
    return mat3(cy, -sy * -sx, -sy * cx,
                0,   cx,  sx,
                sy,  cy * -sx, cy * cx);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 finalcol = vec3(0.);
    int count = 0;
    vec2 m = vec2(0.0, 1.0) + iMouse.xy / iResolution.xy;
    float rx = m.y * PI;
    float ry = -m.x * 2. * PI;
    mat3 mouRot = sphMat(rx, ry);

// ---------------------------------
// initialize the mirrors

    float P = PQR.x, Q = PQR.y, R = PQR.z;
    float cp = dihedral(P), sp = sqrt(1. - cp*cp);
    float cq = dihedral(Q);
    float cr = dihedral(R);
    A = vec3(0,  0,   1);
    B = vec3(0, sp, -cp);
    D = vec3(1,  0,   0);

    float r = 1.0 / cr;
    float k = r * cq / sp;
    vec3 cen = vec3(1, k, 0);
    C = vec4(cen, r) / sqrt(dot(cen, cen) - r * r);

// -------------------------------------
// view setttings

    vec3 camera = vec3(3., 3.2, -3.);
    vec3 lp = vec3(0.5, 3.0, -0.8); //light position
    camera.xz = rot2d(camera.xz, iTime*0.3);
    vec3 lookat  = vec3(0., -0.5, 0.);
    vec3 up = vec3(0., 1., 0.);
    vec3 forward = normalize(lookat - camera);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));

// -------------------------------------
// antialiasing loop

    for(int ii=0; ii<AA; ii++)
    {
        for(int jj=0; jj<AA; jj++)
        {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * fragCoord + o - iResolution.xy) / iResolution.y;
            vec3 rd = normalize(uv.x * right + uv.y * up + 3.0 * forward);
            orb = 1.0;
            // ---------------------------------
            // hit the scene and get distance, object id

            vec2 res = raymarch(camera, rd);
            float t = res.x;
            float id = res.y;
            vec3 pos = camera + t * rd;

            bool found;
            float edist;
            vec3 col;
            // the sphere is hit
            if (id == 0.)
            {
                vec3 nor = pos;
                vec3 q = pos * mouRot;
                found = iterateSpherePoint(q, count);
                edist = distABCD(q);
                vec3 basecol = chooseColor(found, count);

                col = getColor(camera, rd, pos, nor, lp, basecol);
            }
            // the plane is hit
            else if (id == 1.)
            {
                vec3 nor = vec3(0., 1., 0.);
                vec3 q = planeToSphere(pos.xz);
                q = q * mouRot;
                found = iterateSpherePoint(q, count);
                edist = distABCD(q);
                vec3 basecol = chooseColor(found, count);
                col = getColor(camera, rd, pos, nor, lp, basecol) * .9;
            }
            // draw the arcs
            col = mix(col, vec3(0.), (1.0 - smoothstep(0., 0.005, edist))*0.85);
            col = mix(col, vec3(0.), 1.0 - exp(-0.01*t*t));
            finalcol += col;
        }
    }
    finalcol /= (float(AA) * float(AA));

// ------------------------------------
// a little post-processing

    finalcol = mix(finalcol, 1. - exp(-finalcol), .35);
    fragColor = vec4(sqrt(max(finalcol, 0.0)), 1.0);
}

//Modified version from this shader https://www.shadertoy.com/view/7d3XD8


#define hash(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)
#define T(c)     texelFetch(c, ivec2(u) % textureSize(c,0).xy, 0).x 


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U, P = u/R;
    u.x -= float(iFrame);
    U = u / R;

    float h =    P.y > 0.66 ? hash(U)
               : P.y > 0.33 ? T(iChannel0) 
               :             T(iChannel1);
    O = vec4 ( iMouse.z > 0.0 ? step(P.x,h)               // raw thresholding
                             : R.x/64.* ( h - P.x) +0.5   // progressive appear
             );             // 10.



}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Divide the canvas into grids
    vec2 gridCount = vec2(5., 3.);
    vec2 uv = fragCoord / iResolution.xy * gridCount; // x: [0, 5[ and y: [0, 3[
    vec2 gridID = floor(uv); // same as uv but integers
    // Map the 2D grid ID from (0, 0) -> 1 to (4, 2) -> 15
    int mappedID = int((gridCount.y - gridID.y - 1.) * gridCount.x + gridID.x) + 1;

    // Base and overlay texture for blending. Sometime we only use base (t1)
    vec4 t1 = texture(iChannel0, uv);
    vec4 t2 = texture(iChannel1, uv);
    // Shadertoy's (non-noise) textures are in sRGB. Turn them into linear space for manipulation. Turn them back at the end
    t1 = pow(t1, vec4(2.2));
    t2 = pow(t2, vec4(2.2));

    // Used by certain effects. from 0 to 1
    float percentSlider = sin(iTime) / 2. + .5;
    // Click & drag horizontally to manually adjust the percentage
    if (iMouse.z > 0.) percentSlider = iMouse.x / iResolution.x;

    // === Ok, start!
    vec4 color;
    switch (mappedID) {
        case 1: // blend: dissolve
            color = mix(t1, t2, percentSlider);
            break;
        case 2: // blend: lighten
            color = max(t1, t2);
            break;
        case 3: // blend: darken
            color = min(t1, t2);
            break;
        case 4: // blend: subtract
            color = t1 - t2;
            break;
        case 5: // blend: multiply. Would require more calculations for non-1 alpha
            color = t2 * t1;
            break;
        case 6: // blend: add. Would require more calculations for non-1 alpha
            color = t1 + t2;
            break;
        case 7: // blend: divide. Would require more calculations for non-1 alpha
            color = t1 / t2;
            break;
        case 8: // luminence
            vec3 weights = vec3(0.2125, 0.7154, 0.0721); // sums to 1
            float luminance = dot(t1.rgb, weights);
            color = vec4(vec3(luminance), 1.);
            break;
        case 9: // brightness
            color = t1 + percentSlider;
            break;
        case 10: // contrast
            color = (t1 - .5) * percentSlider * 2. + .5; // 0 to 2 contrast
            break;
        case 11: // exposure
            color = t1 * pow(2., percentSlider);
            break;
        case 12: // gamma
            color = pow(t1, vec4(percentSlider) * 2.2); // 0 to 2.2, by convention
            break;
        case 13: // saturation
            vec3 weights_ = vec3(0.2125, 0.7154, 0.0721); // sums to 1
            float luminance_ = dot(t1.rgb, weights_);
            color = mix(vec4(luminance_), t1, vec4(percentSlider) * 5.);
            break;
        case 14: // vibrance
            float average = (t1.r + t1.g + t1.b) / 3.;
            float mx = max(t1.r, max(t1.g, t1.b));
            float amount = (mx - average) * percentSlider * 3. * 5.;
            color = t1 - (mx - t1) * amount;
            break;
        case 15: // inversion
            color = 1. - t1;
            break;
    }

    fragColor = pow(color, vec4(1./2.2)); // gamma correct back
}

#define PI 3.14159265
#define MAX_STEPS 100
#define HIT_DISTANCE 0.0001
#define MAX_DIST 100.


float distToMandelbub(vec3 pos, int iterations, float bailout, float power) {
	vec3 z = pos;
	float dr = 1.0;
	float r;

	for (int i = 0; i < iterations ; i++) {
		r = length(z);
		if (r > bailout) break;

		// convert to polar coordinates
		float theta = acos(z.z/r) * power;
		float phi = atan(z.y,z.x) * power;
		dr =  pow( r, power - 1.0) * power * dr + 1.0;

		// scale and rotate the point
		float zr = pow(r, power);

		// convert back to cartesian coordinates
		z = zr * vec3(sin(theta) * cos(phi), sin(phi) * sin(theta), cos(theta));
		z += pos;
	}
	return 0.5 * log(r) * r / dr;
}


float signedDistToPlane(vec3 origin) {
    return origin.y;
}


float signedDistToSphere(vec4 sphere, vec3 origin) {
    return length(sphere.xyz - origin) - sphere.w;
}


vec2 smoothMin(float dstA, float dstB, float k, float power) {
    float h = max(k - abs(dstA - dstB), 0.0) /k;
    float m = pow(h, power) * 0.5;
    float s = m * k / power;

    return (dstA < dstB) ? vec2(dstA - s, m) : vec2(dstB - s, 1.0 - m);
}


vec4 marchOnce(vec3 pos) {

    vec3 mdbColor = vec3(1., 0, .15);
    vec3 sphColor = vec3(0, 0.1, 1);

    float mdbDist = distToMandelbub(pos, 15, 2.0, (cos(iTime  / 25. - PI) * 0.5 + 0.5) * 15. + 1.);
    float sphDist = signedDistToSphere(vec4(0, 0, sin(iTime / 3.5) * 2., 0.2), pos);

    vec2 smin = smoothMin(mdbDist, sphDist, 0.3, 3.);

    return vec4(mix(mdbColor, sphColor, smin.y), smin.x);
}


vec4 rayMarch(vec3 origin, vec3 direction) {
    vec4 result = vec4(0);
    for (int i=0 ; i < MAX_STEPS ; i++ ) {
        vec3 p = origin + direction * result.w;
        vec4 tempResult = marchOnce(p);

        result.w += tempResult.w;
        result.xyz = tempResult.xyz;
        if (result.w > MAX_DIST)
            return vec4(0, 0, 0, 100000);

        if (tempResult.w < HIT_DISTANCE)
            break;
    }

    return result;
}


vec3 applyFog(vec3 rgb, float dist) {
    vec3 fogColor = vec3(0.05, 0.1, 0.2);
    float fogDensity = 1.;
    float fogStartDist = 1.2;

    dist = dist - fogStartDist;

    float fogAmount = 1.0 - exp( -dist * fogDensity);
    return mix(rgb, fogColor, fogAmount);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;
    float time = iTime / 5.0; 
    float rotationRadius = 2.5;


    vec3 origin = vec3(sin(time) * rotationRadius, 0, -cos(time) * rotationRadius);
    vec3 direction = normalize(vec3(uv.x, uv.y, 1));
    mat3 spin = mat3(cos(time), 0,  sin(time),
                    0, 1, 0,
                    -sin(time), 0, cos(time));
    direction = spin * direction;

    vec4 result = rayMarch(origin, direction);

    vec3 col = vec3(result.xyz);
    col = applyFog(col, result.w);

    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float timeRatio = .3;
    float timeFlow = iTime * timeRatio;
    float acidRatio = 5.;

    vec3 position = vec3(timeFlow, timeFlow, timeFlow);
    vec3 color = vec3(acidRatio, acidRatio, acidRatio);

    for (int i = 0; i < 25; i++) 
    {
        position += vec3(-sin(uv), sin(uv) - cos(uv));

    	color += vec3(
            -sin(color.g + sin(position.y)), 
            -sin(color.b + sin(position.z)), 
            -sin(color.r + sin(position.x)) 
        );
    }

    color *= color * .005;

    fragColor = vec4(color, 1.);
}

