mat2 rot(float a) {
	return mat2(cos(a),sin(a),-sin(a),cos(a));	
}


//reef/waves combined field
vec3 field(in vec3 p) {

	float s=2.,e,f,o=1.;
    vec3 q=p;
	vec2 l = vec2(2.);
	for(e=f=p.y;s<4e2;s*=1.6)
            p.xz*=rot(s),
            q=p,
            q.x+=iTime,
            e+=abs(dot(sin(q.xz*s*.1)/s,.8*l)),
            f+=.2+.5*(dot(sin(p.xz*s*.5)/s,l));
	o+=(f>.001 ? (e<0.01 ? -exp(-e*e):f*e):-exp(-f*f));
    return vec3(max(o,0.),e,f);
}


vec3 raycast( in vec3 ro, vec3 rd )
{
    float t = 3.5, dt = .15,c,d,e,f;
    vec3 col= vec3(0.);
    for( int i=0; i<100; i++ )
	{        
        vec3 v = field(ro+t*rd); 
        c=v.x; e=v.y; f=v.z; d=min(min(e,2.*e*f),f);
        t+=dt*d;
        dt *= 1.015;
        e=exp(-e);f=exp(-f);
        col = .95*col+ .5*c*(f*vec3(.5,.3, .2)+e*vec3(.1, .4, .7));        
    }    
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime;
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;

    // camera

    vec3 ro = vec3(2.);

    ro.yz*=rot(-1.5);

    ro.y +=3.;
    ro.xz*=rot(0.1*t);

    vec3 ta = vec3( 0.0 , 0.0, 0.0 );
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 rd = normalize( p.x*uu + p.y*vv + 4.0*ww );
    ro.x -= t*.4;
    rd.xy*=rot(sin(t)*.1);
    rd.xz*=rot(sin(t*1.3)*.1);
	// raymarch 

    vec3 col = raycast(ro,rd);

	// shade

    col =  .5 *(log(1.+col));
    col = clamp(col,0.,1.);
    fragColor = vec4( col, 1.0 );
}

// First attempt to visualize a phyllotaxis arrangement using shader.
// I am a beginner with the shaders, so the code might not be very optimal.

// A tshirt and probably a mug with this design will be available in my store soon. 
// Feel free to check it out here:
// https://threadhub.store/products/phyllotaxis-t-shirt

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv.x = uv.x * ratio + (iResolution.y - iResolution.x)/iResolution.y / 2.;
    float time = iTime + 1.;
    float c = 0.025;
    vec2 center = vec2(0.5, 0.5);
    vec2 _uv = uv - center;
    float minDiff = 1.;
    float index = 0.;
    for(float i = 0.; i < 400.; i++) {
        float angle = 137.5 * (i + (i / 2300.) * iTime / 20.);
        float radius = c * sqrt(i);
        float x = radius * cos(angle);
        float y = radius * sin(angle);

        vec2 pos = vec2(x, y);
        float diff = abs(distance(pos, _uv)) * 60.;

        if(diff < minDiff) {
            minDiff = diff;
            index = i + 1.;
        }
    }

    minDiff = smoothstep(0., .5, minDiff);
    minDiff = minDiff * minDiff;
    minDiff = 1. - minDiff;
    float changeRate = .05;
    float r = minDiff * (sin((time * 1. * index) * changeRate) + 1.);
    float g = minDiff * (sin((time * 2. * index) * changeRate) + 1.);
    float b = minDiff * (sin((time * 3. * index) * changeRate) + 1.);

    fragColor = vec4(r, g, b, 1);
    return;
}

//Arrangemonk 2021, innefficient noise function
//in the unlikely event of someone forking this: yeah, do that

float PI  = 3.14159265359;
float TAU = 6.28318530718;
float E = 2.71828182846;
float iters = 4.;
float octaves = 8.;

float wave(float x,float time)
{
   return sin(x * PI - time) *.5 + .5;
}

vec2 rot(vec2 uv, float phi)
{
    return vec2(sin(phi)*uv.x + cos(phi)*uv.y,cos(phi)*uv.x - sin(phi)*uv.y);
}

float rotfbm(vec3 pos)
{
float result = 0.;
float m = 0.;
for(float i = 1.; i < octaves;i++)
    {
    float tmp = 1./i;
    m += tmp;
    result += wave(rot(pos.xy*i,PI * E * i).x * iters,pos.z)* tmp;
    }
return result /m;
}

vec3 NormalMap(in vec2 uv)
{ 
    float time = iTime * 0.2;
    float p  = rotfbm(vec3(uv,time));
    float h1 = rotfbm(vec3(uv + vec2(.1,0),time));
    float v1 = rotfbm(vec3(uv + vec2(0,.1),time));      
   	vec2 normal = 0.5 + (p - vec2(h1, v1));
    return normalize(vec3(normal, 1.));
}

float curvature(in vec2 uv)
{
    vec3 p  = NormalMap(uv);
    vec3 h1 = NormalMap(uv + vec2(.001,0));
    vec3 v1 = NormalMap(uv + vec2(0,.001));      

    return (h1.x -p.x + v1.y -p.y - p.z)*.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x * 4.;

    float val = rotfbm(vec3(uv,iTime));
    vec2 pos = .25 * (uv + NormalMap(.5 *(uv + NormalMap(1. *(uv + NormalMap(uv + vec2(0,-iTime * 0.1)).xy)).yz)).zx);
    vec3 col = NormalMap(pos);
    float occ = curvature(pos);

    fragColor = texture(iChannel0,uv + col.xy * col.z);

    fragColor = fragColor * fragColor * 4.;

    float amount = uv.y + col.y * col.z;
    //amount = sqrt(amount);

    fragColor = mix(vec4(4,1.5,0,1),vec4(0,0,0.2,1),amount)+occ*.1;

}

// https://www.desmos.com/calculator/rnlcawwmf8

// Error function approximation
float erf(in float x) {
    return sign(x) * sqrt(1.0 - exp2(-1.787776 * x * x));
}

// Gaussian filtered rectangle
float grect(in vec2 p, in vec2 b, in float w) {
    float u = erf((p.x + b.x) / w) - erf((p.x - b.x) / w);
    float v = erf((p.y + b.y) / w) - erf((p.y - b.y) / w);
    return u * v / 4.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float w = mix(0.25, 0.5, 0.5 - 0.5 * cos(0.5 * iTime));
    float h = mix(0.25, 0.4, 0.5 - 0.5 * sin(iTime));
    float s = mix(0.001, 0.125, 0.5 - 0.5 * cos(iTime));

    fragColor = vec4(grect(uv, vec2(w, h), s));
}

