float dotp(vec2 vector) { return dot(vector, vector); }
float dotp(vec3 vector) { return dot(vector, vector); }
float dotp(vec4 vector) { return dot(vector, vector); }

vec2 rotate(vec2 vector, float rotation)
{
    float s = sin(rotation), c = cos(rotation);
    return vec2(vector.x*c-vector.y*s, vector.x*s+vector.y*c);
}

