    return mix(mix(mix(v0, v1, u.x), mix(v2, v3, u.x), u.y), mix(mix(v4, v5, u.x), mix(v6, v7, u.x), u.y), u.z);
}

float fbm(vec3 p)
{
    float value = 0.0;
    float accum = 0.0;
    float atten = 0.5;
    float scale = 1.0;

    for(int i = 0; i < 8; i++)
    {
        value += atten*noise(scale*p, i);
        accum += atten;
        atten *= 0.5;
        scale *= 2.0;
    }

    return value/accum;
}

