    raycastOut.tMax = -1.0;
    raycastOut.hit = false;
    raycastOut.expire = false;
    raycastOut.normal = vec3(0.0);

    vec3 oc = ro-sphPos;
    float b = dot(oc, rd);
    float c = dot(oc, oc)-sphRad*sphRad;
    float h = b*b-c;

    if(h < 0.0)
    {
        return raycastOut;
    }

    h = sqrt(h);

    raycastOut.tMin = -b-h;
    raycastOut.tMax = -b+h;
    raycastOut.hit = true;
    raycastOut.normal = normalize(ro+rd*raycastOut.tMin);

    return raycastOut;
}

rayData process_hit(rayData raycast0, rayData raycast1)
{
    if(raycast0.hit)
    {
        if(raycast1.hit)
        {
            rayData raycastOut;

            raycastOut.tMin = min(raycast0.tMin, raycast1.tMin);
            raycastOut.tMax = max(raycast0.tMax, raycast1.tMax);

            raycastOut.hit  = true;

            raycastOut.expire  = false;

            raycastOut.normal  = raycast0.tMin < raycast1.tMin ? raycast0.normal : raycast1.normal;

            return raycastOut;
        }

        return raycast0;
    }

    return raycast1;
}

rayData trace(vec3 ro, vec3 rd)
{
    return traceSphere(ro, rd, vec3(0.0), 0.5);
}

vec3 skyColor(vec3 rd)
{
    return vec3(0.000, 0.000, 0.000);
}

float shade_point(vec3 ro, vec3 rd, vec3 normal, vec3 hitPos, vec3 lightPos)
{
    vec3 lightDir = lightPos-hitPos;
    float lightIntensity = ( 1.0/max(dot(lightDir, lightDir), 0.001) );
    lightDir = normalize(lightDir);

    return max(dot(normal, lightDir), 0.0)*lightIntensity;
}

float shade_sun(vec3 ro, vec3 rd, vec3 normal, vec3 lightDir)
{
    return max(dot(normal, lightDir), 0.0);
}

vec3 radiance(vec3 ro, vec3 rd)
{
    rayData t = trace(ro, rd);

    if(!t.hit)
    {
        return skyColor(rd);
    }

    vec3 hitPos = ro+rd*t.tMin;
    vec3 normal = t.normal;

    //vec3 lightPos = vec3( 2.0*sin(0.25*pi*iTime), 2.0, 2.0*cos(0.25*pi*iTime) );
    vec3 lightPos = vec3(10.0, 0.0, 10.0);

    //float lambert = 500.0*shade_point( ro, rd, normal, hitPos, lightPos);
    float lambert = 3.0*shade_sun( ro, rd, normal, normalize(lightPos) );

    vec3 surfacePos = normalize(hitPos);

    float noise0 = fbm( 10.0*(vec3(rotate(surfacePos.xz, -0.005*2.0*pi*iTime), surfacePos.y).xzy) )-0.5;
    float noise1 = fbm(  8.0*(vec3(rotate(surfacePos.xz,  0.001*2.0*pi*iTime), surfacePos.y).xzy)+(surfacePos.y*4.0*noise0) );
    //float noise1 = fbm(  8.0*(vec3(rotate(surfacePos.xz,  0.001*2.0*pi*iTime), surfacePos.y).xzy)+distortion_noise(20.0*surfacePos, 0.125*2.0*pi*iTime) );
    float noise2 = noise( (48.0*surfacePos.y)+(6.0*noise1) );

    vec3 baseColor = vec3(noise2);
    //vec3 baseColor = jupiter_palette(noise2)*noise2;

    return baseColor*lambert;
}

