        camPos = vec3( 2.0*sin(2.0*pi*mouseUV.x), 2.0*mouseUV.y, 2.0*cos(2.0*pi*mouseUV.x) );
    }

    mat3 rotmat;

    rotmat[2] = normalize(target-camPos);
