// truncated pyramid - https://www.shadertoy.com/view/NsKSDc
float sdCappedPyramid(vec3 position, float h, float r1, float r2 )
{
    float s1 = abs(position.y) - h;
    // bottom
    vec3 position1 = position;
    position1.y -= -h;
    position1 = abs(position1) - vec3(r1, 0.0, r1);
    vec3 intersection1 = max(position1, 0.0);
    float d1 = dot(intersection1, intersection1);
    // top
    vec3 position2 = position;
    position2.y -= h;
    position2 = abs(position2) - vec3(r2, 0.0, r2);
    vec3 intersection2 = max(position2, 0.0);
    float d2 = dot(intersection2, intersection2);

    position.x = abs(position.x);
    position.z = abs(position.z);

    vec3 p1 = vec3(r1, -h, 0.0);
    vec3 p2 = vec3(r1, -h, r1);
    vec3 p3 = vec3(0.0, -h, r1);
    vec3 p4 = vec3(r2, h, 0.0);
    vec3 p5 = vec3(r2, h, r2);
    vec3 p6 = vec3(0.0, h, r2);
    vec3 end = p5 - p2;
    vec3 position3 = position - p2;

    // side
    vec3 normal1 = cross(p1 - p2, end);
    float s2 = dot(position3, normal1);
    float d3;
    if (dot(cross(normal1, p2 - p1), position - p2) < 0.0 && 
        dot(cross(normal1, p2 - p1), position - p5) > 0.0 &&
        dot(cross(normal1, p5 - p2), position - p2) < 0.0) { 
        d3 = s2 * s2 / dot(normal1, normal1);
    } else {
        vec3 intersection = position3 - end * clamp(dot(position3, end) / dot(end, end), 0.0, 1.0);
        d3 = dot(intersection, intersection);
    }

    // front/back
    vec3 normal2 = cross(end, p3 - p2);
    float s3 = dot(position3, normal2);
    float d4;
    if (dot(cross(normal2, p6 - p5), position - p2) < 0.0 &&
        dot(cross(normal2, p6 - p5), position - p5) > 0.0 && 
        dot(cross(normal2, p2 - p5), position - p5) < 0.0) { 
        d4 = s3 * s3 / dot(normal2, normal2);
    } else {
        vec3 intersection = position3 - end * clamp(dot(position3, end) / dot(end, end), 0.0, 1.0);
        d4 = dot(intersection, intersection);
    }
    return sqrt(min(min(min(d1, d2), d3), d4)) * sign(max(max(s1, s2), s3));
}

mat3 lookAtMatrix(vec3 from, vec3 to) {
    vec3 forward = normalize(to - from);
    vec3 right = normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, forward);
    return mat3(right, up, forward);
}

mat2 rot( float th ){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }
float sdf(in vec3 position)
{ 

    position.xz*=rot(fract(iTime*0.1)*6.28);

    float m = sin(iTime*2.2)*0.3;


    float r1 = 0.7-m;
    float r2 = 0.7+m;
    float halfHeight = 0.75;
    float d1 = sdCappedPyramid(position-vec3(1.5,0.0,0.0), halfHeight, r1, r2);
    float d2 = sdCappedCone(position+vec3(1.5,0.0,0.0), halfHeight, r1, r2);

    return min(d1,d2);
}

vec3 normal(vec3 position) {
    float epsilon = 0.001;
    vec3 gradient = vec3(
        sdf(position + vec3(epsilon, 0, 0)) - sdf(position + vec3(-epsilon, 0, 0)),
        sdf(position + vec3(0, epsilon, 0)) - sdf(position + vec3(0, -epsilon, 0)),
        sdf(position + vec3(0, 0, epsilon)) - sdf(position + vec3(0, 0, -epsilon))
    );
    return normalize(gradient);
}


//https://www.shadertoy.com/view/ttGfz1
float lighting(vec3 normal)
{
    //fake image based lighting ("outdoor lighting")
    return length(sin(normal*2.)*.5+.5)/sqrt(3.)*smoothstep(-1.,1.,normal.z);
}


float raycast(vec3 rayOrigin, vec3 rayDirection) {
    int stepCount = 128;
    float maximumDistance = 15.0;
    float t = 0.0;
    for (int i = 0; i < stepCount; i++) {
        if (t > maximumDistance) {
            break;
        }
        vec3 currentPosition = rayOrigin + rayDirection * t;
        float d = sdf(currentPosition);
        if (d < 0.0001) {
            return t;
        }
        t += d;
    }
    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	//float angle = 0.5 * (iTime - 10.0);
    float angle = 0.0;
	vec3 rayOrigin = 1.2 * vec3(3.0 * cos(angle), 1.5, 3.0 * sin(angle));
    vec3 targetPosition = vec3(0.0);
    mat3 cameraTransform = lookAtMatrix(rayOrigin, targetPosition);
    vec3 result = vec3(0.0);
    ivec2 sampleCount = ivec2(1.0, 1.0);
    for (int y = 0; y < sampleCount.y; y++) {
        for (int x = 0; x < sampleCount.x; x++) {
            vec2 uv = fragCoord + (vec2(float(x), float(y)) / vec2(sampleCount) - 0.5);
            uv = uv / iResolution.xy;
            uv = (uv * 2.0) - 1.0;
            uv.x *= iResolution.x / iResolution.y;
            vec3 rayDirection = normalize(vec3(uv, 1.5));
            rayDirection = cameraTransform * rayDirection;
            float t = raycast(rayOrigin, rayDirection);
            vec3 color = vec3(0.0);
            if (t > 0.0) {
                vec3 position = rayOrigin + rayDirection * t;
                vec3 n = normal(position);
                float ll = lighting(n);
                color = vec3(0.9,0.9,0.5)*ll;


                if (iMouse.z>0.5)
                {
                    fragColor = vec4(n, 1.0);
                    return;
                }

            }
            // gamma        
            color = sqrt(color);
            result += color;
        }
    }
    result /= float(sampleCount.x * sampleCount.y);
	fragColor = vec4(result, 1.0);
}

