// variant on https://www.shadertoy.com/view/3tKGzV

#define SURF_HIT 0.001
#define FAR_PLANE 50.
#define animTime (mod(iTime, 10.))
#define S(x,y,z) smoothstep(x,y,z)
#define A(v1,v2,t1,t2) mix(v1,v2,S(t1,t2,animTime))

const float PI = 3.14159;

mat2 r2d(float a) {float sa=sin(a), ca=cos(a);return mat2(ca, -sa, sa, ca);}

float fold = 0.0;
float map(vec3 p) {
    vec3 q = p;


    q = mod(q, fold) - fold*.5;
    float d = length(q.xy) - 0.4;
    d = min(d, length(q.xz) - 0.4);
    d = min(d, length(q.yz) - 0.4);
    return d;
}

vec3 mapNormal(vec3 p) {
    vec2 e = vec2(SURF_HIT, 0);
    float m = map(p);
    return normalize(vec3(
    	m - map(p - e.xyy),
        m - map(p - e.yxy),
        m - map(p - e.yyx)
    ));
}

float mapTrace(vec3 ro, vec3 rd) {
    float d = 0.;
    for (int i=0;i<128;i++) {
        float h = map(ro + rd * d);
        if (h < SURF_HIT || d >= FAR_PLANE) break;
        d += h;
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y * 1.0;

    vec3 ro = vec3(0, 0, 0);
    vec3 rd = normalize(vec3(uv, -1));

    fold = mix(0.1, 4.0, animTime);
    fold = A(fold, 0.1, 7.0, 10.0);

    ro += vec3(0, 0, -1.* animTime * fold);
    rd.xy *= r2d(PI * mix(animTime * 0.1, animTime * 0.1 - 0.3, sin(animTime * PI * 0.3)));

    float d = mapTrace(ro, rd);
    vec3 p = ro + rd*d;
    vec3 n = mapNormal(p);

    vec3 fogCol = vec3(1);
    vec3 col = fogCol;
    if (d < FAR_PLANE) {
        vec3 outer = vec3(0.9);
        vec3 inner = vec3(0.1, 0.1, 0.1);

        float f = pow(clamp(1.0 - dot(-rd, n), 0.0, 1.0), 1.5);
        float fog = clamp(pow(d/FAR_PLANE, 1.), 0.0, 1.0);

        col = f * outer + (1.0 - f) * inner;        
        col = mix(col, fogCol, vec3(fog));
    }

    fragColor = vec4(col, 1.0f);
}

#define pi 3.14159265359
#define S smoothstep
vec3 BLACK = vec3(0.);
vec3 WHITE = vec3(1);
vec3 RED = vec3(227,10,23)/255.;
vec3 BLUE = vec3(173,216,237)/255.;
float Star(vec2 uv, float size) {
    uv.x  -= 2.30; // Yıldızın yatay konumu
    float tx = uv.x;
    uv.x = uv.y;
    uv.y = tx;
    uv.x = abs(uv.x);
    float a = 2.0*pi/5.;
    float d1 = dot(uv, vec2(sin(a),cos(a)));

    a = 3.*2.*pi/5.;
    float d2 = dot(uv, vec2(sin(a),cos(a)));


    a = 2.*2.*pi/5.;
    float d4 = dot(uv, vec2(sin(a),cos(a)));

    float d = min(max(d1, d2), max(uv.y, d4));
    float w = min(fwidth(d), 0.05);
    return smoothstep(w, -w, d-size); // d*6.
}
float Circle(vec2 uv, vec2 p, float r, float blur) {
 	float d = length(uv-p);
    float c= smoothstep(r, r-blur, d);
    return c;
}
float Hilal(vec2 uv, float size) {
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    uv.x += .20; // Hilalin yatay konumu
    vec3 col = RED;
    float mask = Circle(uv / size, vec2(0.), .4, .007);
    float mouth = Circle(uv / size, vec2(0., 0.), .3, .007);
    mouth -= Circle(uv / size, vec2(0.08, 0.), .24, .007);
    return mouth;
}
vec2 Remap(vec2 p, float b, float l, float t, float r) {
    return vec2((p.x-l) / (r - l), (p.y - b) / (t - b));
}
vec3 Flag (vec2 uv) {
    float y = sin(uv.y * pi * 13.);
    float w = fwidth(y);
	float stripes = smoothstep(-w, w, y);
    w = fwidth(uv.y);
    vec3 col = RED;

    vec2 st = Remap(uv, .445, .75, 1., .4);
    float size = .375;
    vec2 gv = st*vec2(6,5) - .5;
    if (st.x <= -.7 || st.x >= 2.13) {
        col = BLACK;
        return col;
    }

    col = mix(
        mix(col, RED, smoothstep(0., w*3., st.y)),
        WHITE,
        Hilal(uv, .90)
        );

    col = clamp(max(col, Star(gv, size)), RED, WHITE);
    if (st.x >= 2.08) { // Uçkurluk
        col = mix(col, WHITE, .9);

    }
    col *= smoothstep(w, 0., abs(uv.y-.51)-.51+w);

    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
