vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
}

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(0., 1., -1.));

    vec3 camStep = vec3(-1., 0., 0.) * (iTime+12.)/4.;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;


    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    ro.y += fbm(ro.xz,OCTAVES)-1.8;

    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));

    if (length(iMouse.xy) > 40.0) {
        rd.yx *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
        rd.xz *= rot(3.14-iMouse.x/iResolution.x*3.14*2.0);
    }


    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

// Physically based lightness calculation
// for a rectangular area light perpendicular to a
// matte (Lambertian) ground plane, expanding up and down.
// details: https://hyperknot.com/blog/area_lights_in_shaders/

float area_light_antideriv(vec2 uv, float i, float h, float t) {
  float lxh = length(vec2(uv.x, h));
  return -i * uv.x * atan((t-uv.y)/lxh) / lxh;
}

float area_light(vec2 uv, float i, float h_bottom, float h_top, float t_start, float t_end) {
  // i - light's intensity
  // h_top and h_bottom - the light's top and bottom above the ground
  // t_start and t_end - the light's start and end on the y-axis
  float v =
  + area_light_antideriv(uv, i, h_top, t_end)
  + area_light_antideriv(uv, i, h_bottom, t_start)
  - area_light_antideriv(uv, i, h_bottom, t_end)
  - area_light_antideriv(uv, i, h_top, t_start);
  return max(0., v);
}


float lin_to_srgb ( float val ) {
  if( val < 0.0031308 )
    return val * 12.92;
  else
    return 1.055 * pow(val,1.0/2.4) - 0.055;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    uv *= 2.;

    float h_top = pow(sin(iTime/3.), 2.) * 0.1 + 0.001;

    float v = area_light(uv, 0.05, 0.0, h_top, -0.5, 0.5);
    v += area_light(uv, -0.5, 0.0, h_top, -0.5, 0.5);
    v = lin_to_srgb(v);


 	fragColor=vec4(v);
}

#define ss smoothstep
#define saturate(x) clamp(x, 0.0, 1.0)
const float BUBBLE_AMOUNT = 30.0;
const float BUBBLE_SIZE_MULT = 0.9;
const float BUBBLE_SIZE_ADD = 0.3;
const vec2 BUBBLE_OFFS_MULT = vec2(0.35, 0.2);


//From https://stackoverflow.com/a/17897228
vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

//From https://stackoverflow.com/a/17897228
vec3 rgb2hsv(vec3 c){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// From Martijn Steinrucken
// https://www.youtube.com/channel/UCcAlTqd9zID6aNX3TzwxJXg
// https://www.shadertoy.com/user/BigWIngs
float N21(vec2 p) {
    p = fract(p*vec2(123.3,234.3));
    p += dot(p, p+23.2);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p){
    return vec2(N21(p), N21(vec2(fract(p.x*p.y*2.0), dot(p, p+723.27531))));
}

float spot(vec2 uv, vec2 pos, float size){
    uv = (uv - 0.5) * 2.0;
    return ss(1.0-saturate(length(uv * size - pos)), 0.0, 0.5);
}

float bubble(vec2 uv, float seed, float size){
    vec2  sd = vec2(seed, seed);
    vec2  ps = N22(sd)*size;
    vec2  tm = N22(ps);
    tm *= BUBBLE_OFFS_MULT;
    tm = vec2(sin(iTime * (tm.x + 0.05)) * BUBBLE_OFFS_MULT.x, sin(iTime * (tm.y + 0.05)));
    float sz = N21(ps + sd) * BUBBLE_SIZE_MULT + BUBBLE_SIZE_ADD;
    sz *= size;
    return spot(uv, ps + tm, sz);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 buv = fragCoord/iResolution.y;
    buv.y *= 0.5;
    buv.x *= 0.5;
    buv += vec2(0.5, 0.25);

    float bub = 0.0;

    for(float i = 0.0; i < BUBBLE_AMOUNT; i += 1.0){
        bub += bubble(buv, i * 2.0 + 1.0, 15.0);
    }

     for(float i = 0.0; i < BUBBLE_AMOUNT; i += 1.0){
        bub += bubble(buv, i * 22.0 + 5.0, 7.0);
    }

    bub /= BUBBLE_AMOUNT * 2.0;

    float bubShade = ss(bub, 0.0, 0.0015);
    bubShade *= bubShade * bubShade;
    bub = ss(bubShade, 0.0, 0.1);


    vec3 c1 = vec3(1.0, 0.0, 0.0);
    vec3 c2 = vec3(0.5, 0.0, 0.1);
    vec3 col = mix(c2, c1, bubShade) * bub;
    col = mix(c2*0.5, col, col.r);

    vec3 hsv = rgb2hsv(col);
    hsv.x += (iTime + uv.x * 16.0) * 0.01;
    col = hsv2rgb(hsv);

    fragColor = vec4(col,1.0);
}

